(3192, 3193, 'μελίσσιος', '{\"def\":{\"short\":\"relating to honey, i.e., bee (comb)\",\"long\":[\"of bees, made by bees\"]},\"deriv\":\"from G3192\",\"pronun\":{\"ipa\":\"mɛˈlis.si.os\",\"ipa_mod\":\"me̞ˈlis.si.ows\",\"sbl\":\"melissios\",\"dic\":\"meh-LEES-see-ose\",\"dic_mod\":\"may-LEES-see-ose\"},\"see\":[\"G3192\"]}', 'honeycomb'),
(3193, 3194, 'Μελίτη', '{\"def\":{\"lit\":\"honey\",\"short\":\"Melita, an island in the Mediterranean\",\"long\":[\"the name of an island in the Mediterranean, lying between Africa and Sicily, now called Malta\"]},\"deriv\":\"of uncertain origin\",\"pronun\":{\"ipa\":\"mɛˈli.te\",\"ipa_mod\":\"me̞ˈli.te̞\",\"sbl\":\"melitē\",\"dic\":\"meh-LEE-tay\",\"dic_mod\":\"may-LEE-tay\"}}', 'Melita'),
(3194, 3195, 'μέλλω', '{\"def\":{\"short\":\"to intend, i.e., be about to be, do, or suffer something (of persons or things, especially events; in the sense of purpose, duty, necessity, probability, possibility, or hesitation)\",\"long\":[\"to be about\",[\"to be on the point of doing or suffering something\",\"to intend, have in mind, think to\"]]},\"deriv\":\"a strengthened form of G3199 (through the idea of expectation)\",\"pronun\":{\"ipa\":\"ˈmɛl.lo\",\"ipa_mod\":\"ˈme̞l.low\",\"sbl\":\"mellō\",\"dic\":\"MEL-loh\",\"dic_mod\":\"MALE-loh\"},\"see\":[\"G3199\"]}', 'about, after that, be (almost), (that which is, things, + which was for) to come, intend, was to (be), mean, mind, be at the point, (be) ready, + return, shall (begin), (which, that) should (after, afterwards, hereafter) tarry, which was for, will, would, be yet'),
(3195, 3196, 'μέλος', '{\"def\":{\"short\":\"a limb or part of the body\",\"long\":[\"a member, limb: a member of the human body\",[\"of bodies given up to criminal intercourse, because they are as it were members belonging to the harlot\'s body\"]]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ˈmɛ.los\",\"ipa_mod\":\"ˈme̞.lows\",\"sbl\":\"melos\",\"dic\":\"MEH-lose\",\"dic_mod\":\"MAY-lose\"}}', 'member'),
(3196, 3197, 'Μελχί', '{\"def\":{\"lit\":\"my king, my counsel\",\"short\":\"Melchi (i.e., Malki), the name of two Israelites\",\"long\":[\"the son of Janna in the genealogy of Christ\",\"Melchi was the son of Addi in the genealogy of Christ\"]},\"deriv\":\"of Hebrew origin (H4428 with pronominal suffix, my king)\",\"pronun\":{\"ipa\":\"mɛlˈxi\",\"ipa_mod\":\"me̞lˈxi\",\"sbl\":\"melchi\",\"dic\":\"mel-HEE\",\"dic_mod\":\"male-HEE\"},\"see\":[\"H4428\"]}', 'Melchi'),
(3197, 3198, 'Μελχισεδέκ', '{\"def\":{\"lit\":\"king of righteousness\",\"short\":\"Melchisedek (also Melchisedec or Malki-tsedek)\",\"long\":[\"the king of Peace (i.e., Salem) and priest of the most high God, who appeared in the days of Abraham\",\"evidently, a pre-incarnate appearance of Jesus Christ\"]},\"deriv\":\"of Hebrew origin (H4442)\",\"pronun\":{\"ipa\":\"mɛl.xi.sɛˈðɛk\",\"ipa_mod\":\"me̞l.çi.se̞ˈðe̞k\",\"sbl\":\"melchisedek\",\"dic\":\"mel-hee-seh-THEK\",\"dic_mod\":\"male-hee-say-THAKE\"},\"see\":[\"H4442\"]}', 'Melchisedec'),
(3198, 3199, 'μέλω', '{\"def\":{\"short\":\"to be of interest to, i.e., to concern (only third person singular present indicative used impersonally, it matters)\",\"long\":[\"to care about\"]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈmɛ.lo\",\"ipa_mod\":\"ˈme̞.low\",\"sbl\":\"melō\",\"dic\":\"MEH-loh\",\"dic_mod\":\"MAY-loh\"}}', '(take) care'),
