(3204, 3305, 'μέντοι', '{\"def\":{\"short\":\"indeed though, i.e., however\",\"long\":[\"but yet, nevertheless, howbeit\"]},\"deriv\":\"from G3303 and G5104\",\"pronun\":{\"ipa\":\"ˈmɛn.ty\",\"ipa_mod\":\"ˈme̞n.ty\",\"sbl\":\"mentoi\",\"dic\":\"MEN-too\",\"dic_mod\":\"MANE-too\"},\"see\":[\"G3303\",\"G5104\"]}', 'also, but, howbeit, nevertheless, yet'),
(3205, 3306, 'μένω', '{\"def\":{\"short\":\"to stay (in a given place, state, relation or expectancy)\",\"long\":[\"to remain, abide\",[\"in reference to place\",[\"to sojourn, tarry\",\"not to depart\",[\"to continue to be present\",\"to be held, kept, continually\"]],\"in reference to time\",[\"to continue to be, not to perish, to last, endure\",[\"of persons, to survive, live\"]],\"in reference to state or condition\",[\"to remain as one, not to become another or different\"]],\"to wait for, await one\"]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈmɛ.no\",\"ipa_mod\":\"ˈme̞.now\",\"sbl\":\"menō\",\"dic\":\"MEH-noh\",\"dic_mod\":\"MAY-noh\"}}', 'abide, continue, dwell, endure, be present, remain, stand, tarry (for), X thine own'),
(3206, 3307, 'μερίζω', '{\"def\":{\"short\":\"to part, i.e., (literally) to apportion, bestow, share, or (figuratively) to disunite, differ\",\"long\":[\"to divide\",[\"to separate into parts, cut into pieces\",[\"to divide into parties, i.e., be split into factions\"],\"to distribute\",[\"a thing among people\",\"bestow, impart\"]]]},\"deriv\":\"from G3313\",\"pronun\":{\"ipa\":\"mɛˈri.zo\",\"ipa_mod\":\"me̞ˈri.zow\",\"sbl\":\"merizō\",\"dic\":\"meh-REE-zoh\",\"dic_mod\":\"may-REE-zoh\"},\"see\":[\"G3313\"]}', 'deal, be difference between, distribute, divide, give participle'),
