(3213, 3314, 'μεσημβρία', '{\"def\":{\"short\":\"midday; by implication, the south\",\"long\":[\"as respects to time: noon\",\"as respects to locality\",[\"the south\"]]},\"deriv\":\"from G3319 and G2250\",\"pronun\":{\"ipa\":\"mɛ.semˈβri.ɑ\",\"ipa_mod\":\"me̞.se̞ɱˈvri.ɑ\",\"sbl\":\"mesēmbria\",\"dic\":\"meh-same-VREE-ah\",\"dic_mod\":\"may-same-VREE-ah\"},\"see\":[\"G2250\",\"G3319\"]}', 'noon, south'),
(3214, 3315, 'μεσιτεύω', '{\"def\":{\"short\":\"to interpose (as arbiter), i.e (by implication) to ratify (as surety)\",\"long\":[\"to act as a mediator between litigating or covenanting parties\",[\"to accomplish something by interposing between two parties\",\"to mediate\"],\"act as a sponsor or surety\",[\"to pledge one\'s self, give surety\"]]},\"deriv\":\"from G3316\",\"pronun\":{\"ipa\":\"mɛ.siˈtɛβ.o\",\"ipa_mod\":\"me̞.siˈtev.ow\",\"sbl\":\"mesiteuō\",\"dic\":\"meh-see-TEV-oh\",\"dic_mod\":\"may-see-TAVE-oh\"},\"see\":[\"G3316\"]}', 'confirm'),
(3215, 3316, 'μεσίτης', '{\"def\":{\"short\":\"a go-between, i.e., (simply) an internunciator, or (by implication) a reconciler (intercessor)\",\"long\":[\"one who intervenes between two, either in order to make or restore peace and friendship, or form a compact, or for ratifying a covenant\",\"a medium of communication, arbitrator\"]},\"deriv\":\"from G3319\",\"pronun\":{\"ipa\":\"mɛˈsi.tes\",\"ipa_mod\":\"me̞ˈsi.te̞s\",\"sbl\":\"mesitēs\",\"dic\":\"meh-SEE-tase\",\"dic_mod\":\"may-SEE-tase\"},\"see\":[\"G3319\"]}', 'mediator'),
(3216, 3317, 'μεσονύκτιον', '{\"def\":{\"short\":\"midnight (especially as a watch)\",\"long\":[\"midnight\"]},\"deriv\":\"neuter of compound of G3319 and G3571\",\"pronun\":{\"ipa\":\"mɛ.soˈnyk.ti.on\",\"ipa_mod\":\"me̞.sowˈnjuk.ti.own\",\"sbl\":\"mesonyktion\",\"dic\":\"meh-soh-NOOK-tee-one\",\"dic_mod\":\"may-soh-NYOOK-tee-one\"},\"see\":[\"G3319\",\"G3571\"]}', 'midnight'),
(3217, 3318, 'Μεσοποταμία', '{\"def\":{\"lit\":\"between two rivers\",\"short\":\"Mesopotamia (as lying between the Euphrates and the Tigris; compare H0763), a region of Asia\",\"long\":[\"the entire country between the two rivers, the Tigris and the Euphrates\"]},\"deriv\":\"from G3319 and G4215\",\"pronun\":{\"ipa\":\"mɛ.so.po.tɑˈmi.ɑ\",\"ipa_mod\":\"me̞.sow.pow.tɑˈmi.ɑ\",\"sbl\":\"mesopotamia\",\"dic\":\"meh-soh-poh-ta-MEE-ah\",\"dic_mod\":\"may-soh-poh-ta-MEE-ah\"},\"see\":[\"H0763\",\"G3319\",\"G4215\"]}', 'Mesopotamia'),
