(3220, 3321, 'μεσουράνημα', '{\"def\":{\"short\":\"mid-sky\",\"long\":[\"mid-heaven\",\"the highest point in the heavens, which the sun occupies at noon, where what is done can be seen and heard by all\"]},\"deriv\":\"from a presumed compound of G3319 and G3772\",\"pronun\":{\"ipa\":\"mɛ.suˈrɑ.ne.mɑ\",\"ipa_mod\":\"me̞.suˈrɑ.ne̞.mɑ\",\"sbl\":\"mesouranēma\",\"dic\":\"meh-soo-RA-nay-ma\",\"dic_mod\":\"may-soo-RA-nay-ma\"},\"see\":[\"G3319\",\"G3772\"]}', 'midst of heaven'),
