(3466, 3567, 'νυμφών', '{\"def\":{\"short\":\"the bridal room\",\"long\":[\"the chamber containing the bridal bed, the bridal chamber\",[\"of the friends of the bridegroom whose duty it was to provide and care for whatever pertained to the bridal chamber, i.e., whatever was needed for the due celebration of the nuptials\",\"the room in which the marriage ceremonies are held\"]]},\"deriv\":\"from G3565\",\"pronun\":{\"ipa\":\"nymˈfon\",\"ipa_mod\":\"njuɱˈfown\",\"sbl\":\"nymphōn\",\"dic\":\"noom-FONE\",\"dic_mod\":\"nyoom-FONE\"},\"see\":[\"G3565\"]}', 'bridechamber'),
(3467, 3568, 'νῦν', '{\"def\":{\"short\":\"\\\"now\\\" (as adverb of date, a transition or emphasis); also as noun or adjective present or immediate\",\"long\":[\"at this time, the present, now\"]},\"deriv\":\"a primary particle of present time\",\"pronun\":{\"ipa\":\"nyn\",\"ipa_mod\":\"njun\",\"sbl\":\"nyn\",\"dic\":\"noon\",\"dic_mod\":\"nyoon\"},\"see\":[\"G3569\",\"G3570\"]}', 'henceforth, + hereafter, of late, soon, present, this (time)'),
