(3473, 3574, 'νυχθήμερον', '{\"def\":{\"short\":\"a day-and-night, i.e., full day of twenty-four hours\",\"long\":[\"a night and a day, the space of twenty four hours\"]},\"deriv\":\"from G3571 and G2250\",\"pronun\":{\"ipa\":\"nyxˈθe.mɛ.ron\",\"ipa_mod\":\"njuxˈθe̞.me̞.rown\",\"sbl\":\"nychthēmeron\",\"dic\":\"nook-THAY-meh-rone\",\"dic_mod\":\"nyook-THAY-may-rone\"},\"see\":[\"G2250\",\"G3571\"]}', 'night and day'),
(3474, 3575, 'Νῶε', '{\"def\":{\"lit\":\"rest\",\"short\":\"Noë, (i.e., Noäch), a patriarch\",\"long\":[\"the tenth in descent from Adam, second father of the human race\"]},\"deriv\":\"of Hebrew origin (H5146)\",\"pronun\":{\"ipa\":\"ˈno.ɛ\",\"ipa_mod\":\"ˈnow.e̞\",\"sbl\":\"nōe\",\"dic\":\"NOH-eh\",\"dic_mod\":\"NOH-ay\"},\"see\":[\"H5146\"]}', 'Noe'),
(3475, 3576, 'νωθρός', '{\"def\":{\"short\":\"sluggish, i.e., (literally) lazy, or (figuratively) stupid\",\"long\":[\"slow, sluggish, indolent, dull, languid\"]},\"deriv\":\"from a derivative of G3541\",\"pronun\":{\"ipa\":\"noˈθros\",\"ipa_mod\":\"nowˈθrows\",\"sbl\":\"nōthros\",\"dic\":\"noh-THROSE\",\"dic_mod\":\"noh-THROSE\"},\"see\":[\"G3541\"]}', 'dull, slothful'),
