(3669, 3770, 'οὐράνιος', '{\"def\":{\"short\":\"celestial, i.e., belonging to or coming from the sky\",\"long\":[\"heavenly\",[\"dwelling in heaven\",\"coming from heaven\"]]},\"deriv\":\"from G3772\",\"pronun\":{\"ipa\":\"uˈrɑ.ni.os\",\"ipa_mod\":\"uˈrɑ.ni.ows\",\"sbl\":\"ouranios\",\"dic\":\"oo-RA-nee-ose\",\"dic_mod\":\"oo-RA-nee-ose\"},\"see\":[\"G3772\"]}', 'heavenly'),
(3670, 3771, 'οὐρανόθεν', '{\"def\":{\"short\":\"from the sky\",\"long\":[\"from heaven\"]},\"deriv\":\"from G3772 and the enclitic of source\",\"pronun\":{\"ipa\":\"u.rɑˈno.θɛn\",\"ipa_mod\":\"u.rɑˈnow.θe̞n\",\"sbl\":\"ouranothen\",\"dic\":\"oo-ra-NOH-then\",\"dic_mod\":\"oo-ra-NOH-thane\"},\"see\":[\"G3772\"]}', 'from heaven'),
(3671, 3772, 'οὐρανός', '{\"def\":{\"short\":\"the sky; by extension, heaven (as the abode of God); by implication, happiness, power, eternity; specially, the Gospel (Christianity)\",\"long\":[\"the vaulted expanse of the sky with all things visible in it\",[\"the universe, the world\",\"the aerial heavens or sky, the region where the clouds and the tempests gather, and where thunder and lightning are produced\",\"the sidereal or starry heavens\"],\"the region above the sidereal heavens, the seat of order of things eternal and consummately perfect where God dwells and other heavenly beings\"]},\"deriv\":\"perhaps from the same as G3735 (through the idea of elevation)\",\"pronun\":{\"ipa\":\"u.rɑˈnos\",\"ipa_mod\":\"u.rɑˈnows\",\"sbl\":\"ouranos\",\"dic\":\"oo-ra-NOSE\",\"dic_mod\":\"oo-ra-NOSE\"},\"see\":[\"G3735\"]}', 'air, heaven(-ly), sky'),
