(3680, 3781, 'ὀφειλέτης', '{\"def\":{\"short\":\"an ower, i.e., person indebted; figuratively, a delinquent; morally, a transgressor (against God)\",\"long\":[\"one who owes another, a debtor\",[\"one held by some obligation, bound by some duty\",\"one who has not yet made amends to whom he has injured:\",[\"one who owes God penalty or whom God can demand punishment as something due, i.e., a sinner\"]]]},\"deriv\":\"from G3784\",\"pronun\":{\"ipa\":\"o.fiˈlɛ.tes\",\"ipa_mod\":\"ow.fiˈle̞.te̞s\",\"sbl\":\"opheiletēs\",\"dic\":\"oh-fee-LEH-tase\",\"dic_mod\":\"oh-fee-LAY-tase\"},\"see\":[\"G3784\"]}', 'debtor, which owed, sinner'),
(3681, 3782, 'ὀφειλή', '{\"def\":{\"short\":\"indebtedness, i.e., (concretely) a sum owed; figuratively, obligation, i.e., (conjugal) duty\",\"long\":[\"that which is owed\",\"a debt\",\"metaphorically dues: specifically of conjugal duty\"]},\"deriv\":\"from G3784\",\"pronun\":{\"ipa\":\"o.fiˈle\",\"ipa_mod\":\"ow.fiˈle̞\",\"sbl\":\"opheilē\",\"dic\":\"oh-fee-LAY\",\"dic_mod\":\"oh-fee-LAY\"},\"see\":[\"G3784\"]}', 'debt, due'),
(3682, 3783, 'ὀφείλημα', '{\"def\":{\"short\":\"something owed, i.e., (figuratively) a due; morally, a fault\",\"long\":[\"that which is owed\",[\"that which is justly or legally due, a debt\"],\"metaphorically offence, sin\"]},\"deriv\":\"from (the alternate of) G3784\",\"pronun\":{\"ipa\":\"oˈfi.le.mɑ\",\"ipa_mod\":\"owˈfi.le̞.mɑ\",\"sbl\":\"opheilēma\",\"dic\":\"oh-FEE-lay-ma\",\"dic_mod\":\"oh-FEE-lay-ma\"},\"see\":[\"G3784\"]}', 'debt'),
