(3985, 4086, 'πιθανολογία', '{\"def\":{\"short\":\"persuasive language\",\"long\":[\"speech adapted to persuade, discourse in which probable arguments are adduced\",\"in a bad sense, persuasiveness of speech, specious discourse leading others into error\"]},\"deriv\":\"from a compound of a derivative of G3982 and G3056\",\"pronun\":{\"ipa\":\"pi.θɑ.no.loˈɣi.ɑ\",\"ipa_mod\":\"pi.θɑ.now.lowˈɣi.ɑ\",\"sbl\":\"pithanologia\",\"dic\":\"pee-tha-noh-loh-GEE-ah\",\"dic_mod\":\"pee-tha-noh-loh-GEE-ah\"},\"see\":[\"G3056\",\"G3982\"]}', 'enticing words'),
(3986, 4087, 'πικραίνω', '{\"def\":{\"short\":\"to embitter (literally or figuratively)\",\"long\":[\"to make bitter\",[\"to produce a bitter taste in the stomach\"],\"to embitter, exasperate\",[\"render angry, indignant\",\"to be embittered, irritated\",\"to visit with bitterness, to grieve (deal bitterly with)\"]]},\"deriv\":\"from G4089\",\"pronun\":{\"ipa\":\"piˈkrɛ.no\",\"ipa_mod\":\"piˈkre.now\",\"sbl\":\"pikrainō\",\"dic\":\"pee-KREH-noh\",\"dic_mod\":\"pee-KRAY-noh\"},\"see\":[\"G4089\"]}', 'be (make) bitter'),
(3987, 4088, 'πικρία', '{\"def\":{\"short\":\"acridity (especially poison), literally or figuratively\",\"long\":[\"bitter gall\",[\"extreme wickedness\",\"a bitter root, and so producing a bitter fruit\",\"metaphorically bitterness, bitter hatred\"]]},\"deriv\":\"from G4089\",\"pronun\":{\"ipa\":\"piˈkri.ɑ\",\"ipa_mod\":\"piˈkri.ɑ\",\"sbl\":\"pikria\",\"dic\":\"pee-KREE-ah\",\"dic_mod\":\"pee-KREE-ah\"},\"see\":[\"G4089\"]}', 'bitterness'),
