(4010, 4111, 'πλάσσω', '{\"def\":{\"short\":\"to mould, i.e., shape or fabricate\",\"long\":[\"to form, mould (something from clay, wax, etc.)\",[\"used of a potter\"]]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈplɑs.so\",\"ipa_mod\":\"ˈplɑs.sow\",\"sbl\":\"plassō\",\"dic\":\"PLAHS-soh\",\"dic_mod\":\"PLAHS-soh\"}}', 'form'),
(4011, 4112, 'πλαστός', '{\"def\":{\"short\":\"moulded, i.e., (by implication) artificial or (figuratively) fictitious (false)\",\"long\":[\"moulded, formed, as from clay, wax, or stone\",\"feigned\"]},\"deriv\":\"from G4111\",\"pronun\":{\"ipa\":\"plɑˈstos\",\"ipa_mod\":\"plɑˈstows\",\"sbl\":\"plastos\",\"dic\":\"pla-STOSE\",\"dic_mod\":\"pla-STOSE\"},\"see\":[\"G4111\"]}', 'feigned'),
