(4370, 4471, 'Ῥαμᾶ', '{\"def\":{\"lit\":\"a hill\",\"short\":\"Rama (i.e., Ramah), a place in Palestine\",\"long\":[\"a town of the tribe of Benjamin, situated about 5 miles (8 km) north of Jerusalem on the road to Bethel\"]},\"deriv\":\"of Hebrew origin (H7414)\",\"pronun\":{\"ipa\":\"rɑˈmɑ\",\"ipa_mod\":\"rɑˈmɑ\",\"sbl\":\"rhama\",\"dic\":\"ra-MA\",\"dic_mod\":\"ra-MA\"},\"see\":[\"H7414\"]}', 'Rama'),
(4371, 4472, 'ῥαντίζω', '{\"def\":{\"short\":\"to render besprinkled, i.e., asperse (ceremonially or figuratively)\",\"long\":[\"to sprinkle\",\"to cleanse by sprinkling, hence to purify, cleanse\"]},\"deriv\":\"from a derivative of ῥαίνω (to sprinkle)\",\"pronun\":{\"ipa\":\"rɑnˈti.zo\",\"ipa_mod\":\"rɑnˈti.zow\",\"sbl\":\"rhantizō\",\"dic\":\"rahn-TEE-zoh\",\"dic_mod\":\"rahn-TEE-zoh\"}}', 'sprinkle'),
(4372, 4473, 'ῥαντισμός', '{\"def\":{\"short\":\"aspersion (ceremonially or figuratively)\",\"long\":[\"a sprinkling (purification)\",[\"blood of sprinkling\",[\"i.e., appointed for sprinkling (serving to purify)\"]]]},\"deriv\":\"from G4472\",\"pronun\":{\"ipa\":\"rɑn.tiˈsmos\",\"ipa_mod\":\"rɑn.tiˈsmows\",\"sbl\":\"rhantismos\",\"dic\":\"rahn-tee-SMOSE\",\"dic_mod\":\"rahn-tee-SMOSE\"},\"see\":[\"G4472\"]}', 'sprinkling'),
(4373, 4474, 'ῥαπίζω', '{\"def\":{\"short\":\"to slap\",\"long\":[\"to smite with a rod or staff\",\"to smite in the face with the palm of the hand, to box the ear\"]},\"deriv\":\"from a derivative of a primary ῥέπω (to let fall, \\\"rap\\\")\",\"pronun\":{\"ipa\":\"rɑˈpi.zo\",\"ipa_mod\":\"rɑˈpi.zow\",\"sbl\":\"rhapizō\",\"dic\":\"ra-PEE-zoh\",\"dic_mod\":\"ra-PEE-zoh\"},\"see\":[\"G5180\"],\"comment\":\"Compare G5180.\"}', 'smite (with the palm of the hand)'),
(4374, 4475, 'ῥάπισμα', '{\"def\":{\"short\":\"a slap\",\"long\":[\"a blow with a rod or staff or a scourge\",\"a blow with the flat of the hand, a slap in the face, box the ear\"]},\"deriv\":\"from G4474\",\"pronun\":{\"ipa\":\"ˈrɑ.pi.smɑ\",\"ipa_mod\":\"ˈrɑ.pi.smɑ\",\"sbl\":\"rhapisma\",\"dic\":\"RA-pee-sma\",\"dic_mod\":\"RA-pee-sma\"},\"see\":[\"G4474\"]}', '(+ strike with the) palm of the hand, smite with the hand'),
(4375, 4476, 'ῥαφίς', '{\"def\":{\"short\":\"a needle\",\"long\":[\"a needle\"]},\"deriv\":\"from a primary ῥάπτω (to sew; perhaps rather akin to the base of G4474 through the idea of puncturing)\",\"pronun\":{\"ipa\":\"rɑˈfis\",\"ipa_mod\":\"rɑˈfis\",\"sbl\":\"rhaphis\",\"dic\":\"ra-FEES\",\"dic_mod\":\"ra-FEES\"},\"see\":[\"G4474\"]}', 'needle'),
(4376, 4477, 'Ῥαχάβ', '{\"def\":{\"lit\":\"wide\",\"short\":\"Rachab, a Canaanitess\",\"long\":[\"a harlot of Jericho\"]},\"deriv\":\"from the same as G4460\",\"pronun\":{\"ipa\":\"rɑˈxɑβ\",\"ipa_mod\":\"rɑˈxɑv\",\"sbl\":\"rhachab\",\"dic\":\"ra-HAHV\",\"dic_mod\":\"ra-HAHV\"},\"see\":[\"G4460\"]}', 'Rachab'),
(4377, 4478, 'Ῥαχήλ', '{\"def\":{\"lit\":\"ewe, sheep\",\"short\":\"Rachel, the wife of Jacob\",\"long\":[\"the wife of Jacob\"]},\"deriv\":\"of Hebrew origin (H7354)\",\"pronun\":{\"ipa\":\"rɑˈxel\",\"ipa_mod\":\"rɑˈxe̞l\",\"sbl\":\"rhachēl\",\"dic\":\"ra-HALE\",\"dic_mod\":\"ra-HALE\"},\"see\":[\"H7354\"]}', 'Rachel'),
(4378, 4479, 'Ῥεβέκκα', '{\"def\":{\"lit\":\"ensnarer\",\"short\":\"Rebecca (i.e., Ribkah), the wife of Isaac\",\"long\":[\"the wife of Isaac\"]},\"deriv\":\"of Hebrew origin (H7259)\",\"pronun\":{\"ipa\":\"rɛˈβɛk.kɑ\",\"ipa_mod\":\"re̞ˈve̞k.kɑ\",\"sbl\":\"rhebekka\",\"dic\":\"reh-VEK-ka\",\"dic_mod\":\"ray-VAKE-ka\"},\"see\":[\"H7259\"]}', 'Rebecca'),
