(4389, 4490, 'ῥητῶς', '{\"def\":{\"short\":\"out-spokenly, i.e., distinctly\",\"long\":[\"expressly, in express words\"]},\"deriv\":\"adverb from a derivative of G4483\",\"pronun\":{\"ipa\":\"reˈtos\",\"ipa_mod\":\"re̞ˈtows\",\"sbl\":\"rhētōs\",\"dic\":\"ray-TOSE\",\"dic_mod\":\"ray-TOSE\"},\"see\":[\"G4483\"]}', 'expressly'),
(4390, 4491, 'ῥίζα', '{\"def\":{\"short\":\"a \\\"root\\\" (literally or figuratively)\",\"long\":[\"a root\",\"that which like a root springs from a root, a sprout, shoot\",\"metaphorically offspring, progeny\"]},\"deriv\":\"apparently a primary word\",\"pronun\":{\"ipa\":\"ˈri.zɑ\",\"ipa_mod\":\"ˈri.zɑ\",\"sbl\":\"rhiza\",\"dic\":\"REE-za\",\"dic_mod\":\"REE-za\"}}', 'root'),
