(4406, 4507, 'ῥυπαρία', '{\"def\":{\"short\":\"dirtiness (morally)\",\"long\":[\"to make filthy, befoul\",\"to defile, dishonor\",\"to make filthy\"]},\"deriv\":\"from G4508\",\"pronun\":{\"ipa\":\"ry.pɑˈri.ɑ\",\"ipa_mod\":\"rju.pɑˈri.ɑ\",\"sbl\":\"rhyparia\",\"dic\":\"roo-pa-REE-ah\",\"dic_mod\":\"ryoo-pa-REE-ah\"},\"see\":[\"G4508\"]}', 'turpitude'),
(4407, 4508, 'ῥυπαρός', '{\"def\":{\"short\":\"dirty, i.e., (relatively) cheap or shabby; morally, wicked\",\"long\":[\"to be dirty, grow filthy\",\"metaphorically to be defiled with iniquity\"]},\"deriv\":\"from G4509\",\"pronun\":{\"ipa\":\"ry.pɑˈros\",\"ipa_mod\":\"rju.pɑˈrows\",\"sbl\":\"rhyparos\",\"dic\":\"roo-pa-ROSE\",\"dic_mod\":\"ryoo-pa-ROSE\"},\"see\":[\"G4509\"]}', 'vile'),
(4408, 4509, 'ῥύπος', '{\"def\":{\"short\":\"dirt, i.e., (morally) depravity\",\"long\":[\"filthy, dirty\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ˈry.pos\",\"ipa_mod\":\"ˈrju.pows\",\"sbl\":\"rhypos\",\"dic\":\"ROO-pose\",\"dic_mod\":\"RYOO-pose\"}}', 'filth'),
(4409, 4510, 'ῥυπόω', '{\"def\":{\"short\":\"to soil, i.e., (intransitively) to become dirty (morally)\",\"long\":[\"to make filthy, defile, soil\",\"to be filthy (morally)\"]},\"deriv\":\"from G4509\",\"pronun\":{\"ipa\":\"ryˈpo.o\",\"ipa_mod\":\"rjuˈpow.ow\",\"sbl\":\"rhypoō\",\"dic\":\"roo-POH-oh\",\"dic_mod\":\"ryoo-POH-oh\"},\"see\":[\"G4509\"]}', 'be filthy'),
(4410, 4511, 'ῥύσις', '{\"def\":{\"short\":\"a flux (of blood)\",\"long\":[\"a flowing issue\"]},\"deriv\":\"from G4506 in the sense of its congener G4482\",\"pronun\":{\"ipa\":\"ˈry.sis\",\"ipa_mod\":\"ˈrju.sis\",\"sbl\":\"rhysis\",\"dic\":\"ROO-sees\",\"dic_mod\":\"RYOO-sees\"},\"see\":[\"G4482\",\"G4506\"]}', 'issue'),
(4411, 4512, 'ῥυτίς', '{\"def\":{\"short\":\"a fold (as drawing together), i.e., a wrinkle (especially on the face)\",\"long\":[\"a wrinkle\"]},\"deriv\":\"from G4506\",\"pronun\":{\"ipa\":\"ryˈtis\",\"ipa_mod\":\"rjuˈtis\",\"sbl\":\"rhytis\",\"dic\":\"roo-TEES\",\"dic_mod\":\"ryoo-TEES\"},\"see\":[\"G4506\"]}', 'wrinkle'),
(4412, 4513, 'Ῥωμαϊκός', '{\"def\":{\"lit\":\"of Rome\'s strength\",\"short\":\"Romaic, i.e., Latin\",\"long\":[\"the language spoken by the Romans\"]},\"deriv\":\"from G4514\",\"pronun\":{\"ipa\":\"ro.mɑ.iˈkos\",\"ipa_mod\":\"row.mɑ.iˈkows\",\"sbl\":\"rhōmaikos\",\"dic\":\"roh-ma-ee-KOSE\",\"dic_mod\":\"roh-ma-ee-KOSE\"},\"see\":[\"G4514\"]}', 'Latin'),
(4413, 4514, 'Ῥωμαῖος', '{\"def\":{\"short\":\"Romaean, i.e., Roman (as noun)\",\"long\":[\"a resident of the city of Rome, a Roman citizen\"]},\"deriv\":\"from G4516\",\"pronun\":{\"ipa\":\"roˈmɛ.os\",\"ipa_mod\":\"rowˈme.ows\",\"sbl\":\"rhōmaios\",\"dic\":\"roh-MEH-ose\",\"dic_mod\":\"roh-MAY-ose\"},\"see\":[\"G4516\"]}', 'Roman, of Rome'),
(4414, 4515, 'Ῥωμαϊστί', '{\"def\":{\"lit\":\"of Rome\'s strength\",\"short\":\"Romaistically, i.e., in the Latin language\",\"long\":[\"the language spoken by the Romans\"]},\"deriv\":\"adverb from a presumed derivative of G4516\",\"pronun\":{\"ipa\":\"ro.mɑ.iˈsti\",\"ipa_mod\":\"row.mɑ.iˈsti\",\"sbl\":\"rhōmaisti\",\"dic\":\"roh-ma-ee-STEE\",\"dic_mod\":\"roh-ma-ee-STEE\"},\"see\":[\"G4516\"]}', 'Latin'),
(4415, 4516, 'Ῥώμη', '{\"def\":{\"lit\":\"strength\",\"short\":\"strength; Roma, the capital of Italy\",\"long\":[\"the famous capital of the ancient world\"]},\"deriv\":\"from the base of G4517\",\"pronun\":{\"ipa\":\"ˈro.me\",\"ipa_mod\":\"ˈrow.me̞\",\"sbl\":\"rhōmē\",\"dic\":\"ROH-may\",\"dic_mod\":\"ROH-may\"},\"see\":[\"G4517\"]}', 'Rome'),
(4416, 4517, 'ῥώννυμι', '{\"def\":{\"short\":\"probably akin to G4506); to strengthen, i.e., (impersonal passive) have health (as a parting exclamation, good-bye)\",\"long\":[\"to make strong, strengthen\",\"to be strong, to thrive, prosper\",\"in the usual formula in closing of a letter, farewell\"]},\"deriv\":\"prolongation from ῥώομαι (to dart\",\"pronun\":{\"ipa\":\"ˈron.ny.mi\",\"ipa_mod\":\"ˈrown.nju.mi\",\"sbl\":\"rhōnnymi\",\"dic\":\"RONE-noo-mee\",\"dic_mod\":\"RONE-nyoo-mee\"},\"see\":[\"G4506\"]}', 'farewell'),
(4417, 4518, 'σαβαχθάνι', '{\"def\":{\"short\":\"thou hast left me; sabachthani (i.e., shebakthani), a cry of distress\",\"long\":[\"thou hast forsaken me\"]},\"deriv\":\"of Chaldee or (H7662 with pronominal suffix)\",\"pronun\":{\"ipa\":\"sɑ.βɑxˈθɑ.ni\",\"ipa_mod\":\"sɑ.vɑxˈθɑ.ni\",\"sbl\":\"sabachthani\",\"dic\":\"sa-vahk-THA-nee\",\"dic_mod\":\"sa-vahk-THA-nee\"},\"see\":[\"H7662\"]}', 'sabachthani'),
(4418, 4519, 'σαβαώθ', '{\"def\":{\"short\":\"armies; sabaoth (i.e., tsebaoth), a military epithet of God\",\"long\":[\"\\\"Lord of Sabaoth\\\"\",[\"Lord of the armies of Israel, as those who are under the leadership and protection of Jehovah maintain his cause in war\"]]},\"deriv\":\"of Hebrew origin (H6635 in feminine plural)\",\"pronun\":{\"ipa\":\"sɑ.βɑˈoθ\",\"ipa_mod\":\"sɑ.vɑˈowθ\",\"sbl\":\"sabaōth\",\"dic\":\"sa-va-OHTH\",\"dic_mod\":\"sa-va-OHTH\"},\"see\":[\"H6635\"]}', 'sabaoth'),
(4419, 4520, 'σαββατισμός', '{\"def\":{\"short\":\"a \\\"sabbatism\\\", i.e., (figuratively) the repose of Christianity (as a type of heaven)\",\"long\":[\"a keeping sabbath\",\"the blessed rest from toils and troubles looked for in the age to come by the true worshippers of God and true Christians\"]},\"deriv\":\"from a derivative of G4521\",\"pronun\":{\"ipa\":\"sɑβ.βɑ.tiˈsmos\",\"ipa_mod\":\"sɑv.vɑ.tiˈsmows\",\"sbl\":\"sabbatismos\",\"dic\":\"sahv-va-tee-SMOSE\",\"dic_mod\":\"sahv-va-tee-SMOSE\"},\"see\":[\"G4521\"]}', 'rest'),
(4420, 4521, 'σάββατον', '{\"def\":{\"short\":\"the Sabbath (i.e., Shabbath), or day of weekly repose from secular avocations (also the observance or institution itself); by extension, a se\'nnight, i.e., the interval between two Sabbaths; likewise the plural in all the above applications\",\"long\":[\"the seventh day of each week which was a sacred festival on which the Israelites were required to abstain from all work\",[\"the institution of the sabbath, the law for keeping holy every seventh day of the week\",\"a single sabbath, sabbath day\"],\"seven days, a week\"]},\"deriv\":\"of Hebrew origin (H7676)\",\"pronun\":{\"ipa\":\"ˈsɑβ.βɑ.ton\",\"ipa_mod\":\"ˈsɑv.vɑ.town\",\"sbl\":\"sabbaton\",\"dic\":\"SAHV-va-tone\",\"dic_mod\":\"SAHV-va-tone\"},\"see\":[\"H7676\"]}', 'sabbath (day), week'),
(4421, 4522, 'σαγήνη', '{\"def\":{\"short\":\"a \\\"seine\\\" for fishing\",\"long\":[\"a large fishing net, a drag net\"]},\"deriv\":\"from a derivative of σάττω (to equip) meaning furniture, especially a pack-saddle (which in the East is merely a bag of netted rope)\",\"pronun\":{\"ipa\":\"sɑˈɣe.ne\",\"ipa_mod\":\"sɑˈɣe̞.ne̞\",\"sbl\":\"sagēnē\",\"dic\":\"sa-GAY-nay\",\"dic_mod\":\"sa-GAY-nay\"}}', 'net'),
(4422, 4523, 'Σαδδουκαῖος', '{\"def\":{\"lit\":\"the righteous\",\"short\":\"a Sadducaean (i.e., Tsadokian), or follower of a certain heretical Israelite\",\"long\":[\"a religious party at the time of Christ among the Jews, who denied that the oral law was a revelation of God to the Israelites, and who deemed the written law alone to be obligatory on the nation, as the divine authority\"]},\"deriv\":\"probably from G4524\",\"pronun\":{\"ipa\":\"sɑð.ðuˈkɛ.os\",\"ipa_mod\":\"sɑð.ðuˈke.ows\",\"sbl\":\"saddoukaios\",\"dic\":\"sahth-thoo-KEH-ose\",\"dic_mod\":\"sahth-thoo-KAY-ose\"},\"see\":[\"G4524\"],\"comment\":\"They denied the following doctrines: (1) the resurrection of the body, (2) immortality of the soul, (3) existence of spirits and angels, (4) divine predestination.\"}', 'Sadducee'),
(4423, 4524, 'Σαδώκ', '{\"def\":{\"lit\":\"just\",\"short\":\"Sadoc (i.e., Tsadok), an Israelite\",\"long\":[\"a descendant of Zerubbabel in the genealogy of Christ\"]},\"deriv\":\"of Hebrew origin (H6659)\",\"pronun\":{\"ipa\":\"sɑˈðok\",\"ipa_mod\":\"sɑˈðowk\",\"sbl\":\"sadōk\",\"dic\":\"sa-THOKE\",\"dic_mod\":\"sa-THOKE\"},\"see\":[\"H6659\"]}', 'Sadoc'),
(4424, 4525, 'σαίνω', '{\"def\":{\"short\":\"to wag (as a dog its tail fawningly), i.e., (generally) to shake (figuratively, disturb)\",\"long\":[\"to wag the tail\",[\"of dogs\"],\"metaphorically\",[\"to flatter, fawn upon\",\"to move (the mind of one)\",[\"agreeably\",\"to agitate, disturb, trouble\"]]]},\"deriv\":\"akin to G4579\",\"pronun\":{\"ipa\":\"ˈsɛ.no\",\"ipa_mod\":\"ˈse.now\",\"sbl\":\"sainō\",\"dic\":\"SEH-noh\",\"dic_mod\":\"SAY-noh\"},\"see\":[\"G4579\"]}', 'move'),
(4425, 4526, 'σάκκος', '{\"def\":{\"short\":\"\\\"sack\\\"-cloth, i.e., mohair (the material or garments made of it, worn as a sign of grief)\",\"long\":[\"a sack\",[\"a receptacle for holding or carrying various things, as money, food, etc.\",\"a course cloth, a dark course stuff made especially from the hair of animals\",\"a garment of the like material, and clinging to the person like a sack, which was wont to be worn (or drawn over the tunic instead of the cloak or mantle) by mourners, penitents, suppliants and also by those who like the Hebrew prophets, lead an austere life\"]]},\"deriv\":\"of Hebrew origin (H8242)\",\"pronun\":{\"ipa\":\"ˈsɑk.kos\",\"ipa_mod\":\"ˈsɑk.kows\",\"sbl\":\"sakkos\",\"dic\":\"SAHK-kose\",\"dic_mod\":\"SAHK-kose\"},\"see\":[\"H8242\"]}', 'sackcloth'),
(4426, 4527, 'Σαλά', '{\"def\":{\"lit\":\"sprout\",\"short\":\"Sala (i.e., Shelach), a patriarch\",\"long\":[\"the son of Arphaxad, and father of Eber\"]},\"deriv\":\"of Hebrew origin (H7974)\",\"pronun\":{\"ipa\":\"sɑˈlɑ\",\"ipa_mod\":\"sɑˈlɑ\",\"sbl\":\"sala\",\"dic\":\"sa-LA\",\"dic_mod\":\"sa-LA\"},\"see\":[\"H7974\"]}', 'Sala'),
(4427, 4528, 'Σαλαθιήλ', '{\"def\":{\"lit\":\"I have asked of God\",\"short\":\"Salathiel (i.e., Shealtiel), an Israelite\",\"long\":[\"the son of Jeconiah in the genealogy of Christ\"]},\"deriv\":\"of Hebrew origin (H7597)\",\"pronun\":{\"ipa\":\"sɑ.lɑ.θiˈel\",\"ipa_mod\":\"sɑ.lɑ.θiˈe̞l\",\"sbl\":\"salathiēl\",\"dic\":\"sa-la-thee-ALE\",\"dic_mod\":\"sa-la-thee-ALE\"},\"see\":[\"H7597\"]}', 'Salathiel'),
(4428, 4529, 'Σαλαμίς', '{\"def\":{\"lit\":\"salt\",\"short\":\"Salamis, a place in Cyprus\",\"long\":[\"a principal city at the east end of the island of Cyprus\"]},\"deriv\":\"probably from G4535 (from the surge on the shore)\",\"pronun\":{\"ipa\":\"sɑ.lɑˈmis\",\"ipa_mod\":\"sɑ.lɑˈmis\",\"sbl\":\"salamis\",\"dic\":\"sa-la-MEES\",\"dic_mod\":\"sa-la-MEES\"},\"see\":[\"G4535\"]}', 'Salamis'),
(4429, 4530, 'Σαλείμ', '{\"def\":{\"lit\":\"peace\",\"short\":\"Salim, a place in Palestine\",\"long\":[\"a well known town 8 miles (11 km) south of Scythopolis\"]},\"deriv\":\"probably from the same as G4531\",\"pronun\":{\"ipa\":\"sɑˈlim\",\"ipa_mod\":\"sɑˈlim\",\"sbl\":\"saleim\",\"dic\":\"sa-LEEM\",\"dic_mod\":\"sa-LEEM\"},\"see\":[\"G4531\"]}', 'Salim'),
(4430, 4531, 'σαλεύω', '{\"def\":{\"short\":\"to waver, i.e., agitate, rock, topple or (by implication) destroy; figuratively, to disturb, incite\",\"long\":[\"a motion produced by winds, storms, waves, etc\",[\"to agitate or shake\",\"to cause to totter\",\"to shake thoroughly, of a measure filled by shaking its contents together\"],\"to shake down, overthrow\",[\"to cast down from one\'s (secure and happy) state\",\"to move, agitate the mind, to disturb one\"]]},\"deriv\":\"from G4535\",\"pronun\":{\"ipa\":\"sɑˈlɛβ.o\",\"ipa_mod\":\"sɑˈlev.ow\",\"sbl\":\"saleuō\",\"dic\":\"sa-LEV-oh\",\"dic_mod\":\"sa-LAVE-oh\"},\"see\":[\"G4535\"]}', 'move, shake (together), which can(-not) be shaken, stir up'),
(4431, 4532, 'Σαλήμ', '{\"def\":{\"lit\":\"peace\",\"short\":\"Salem (i.e., Shalem), a place in Palestine\",\"long\":[\"the place where Melchizedek was king and thought to be the ancient name of Jerusalem\"]},\"deriv\":\"of Hebrew origin (H8004)\",\"pronun\":{\"ipa\":\"sɑˈlem\",\"ipa_mod\":\"sɑˈle̞m\",\"sbl\":\"salēm\",\"dic\":\"sa-LAME\",\"dic_mod\":\"sa-LAME\"},\"see\":[\"H8004\"]}', 'Salem'),
(4432, 4533, 'Σαλμών', '{\"def\":{\"lit\":\"raiment, a garment\",\"short\":\"Salmon, an Israelite\",\"long\":[\"the father of Boaz in the genealogy of Christ\"]},\"deriv\":\"of Hebrew origin (H8012)\",\"pronun\":{\"ipa\":\"sɑlˈmon\",\"ipa_mod\":\"sɑlˈmown\",\"sbl\":\"salmōn\",\"dic\":\"sahl-MONE\",\"dic_mod\":\"sahl-MONE\"},\"see\":[\"H8012\"]}', 'Salmon'),
(4433, 4534, 'Σαλμώνη', '{\"def\":{\"lit\":\"clothed\",\"short\":\"Salmone, a place in Crete\",\"long\":[\"a bold promontory on the east point of the island of Crete\"]},\"deriv\":\"perhaps of similar origin to G4529\",\"pronun\":{\"ipa\":\"sɑlˈmo.ne\",\"ipa_mod\":\"sɑlˈmow.ne̞\",\"sbl\":\"salmōnē\",\"dic\":\"sahl-MOH-nay\",\"dic_mod\":\"sahl-MOH-nay\"},\"see\":[\"G4529\"]}', 'Salmone'),
(4434, 4535, 'σάλος', '{\"def\":{\"short\":\"a vibration, i.e., (specially), billow\",\"long\":[\"the tossing or swell of the sea\"]},\"deriv\":\"probably from the base of G4525\",\"pronun\":{\"ipa\":\"ˈsɑ.los\",\"ipa_mod\":\"ˈsɑ.lows\",\"sbl\":\"salos\",\"dic\":\"SA-lose\",\"dic_mod\":\"SA-lose\"},\"see\":[\"G4525\"]}', 'wave'),
(4435, 4536, 'σάλπιγξ', '{\"def\":{\"short\":\"a trumpet\",\"long\":[\"a trumpet\"]},\"deriv\":\"perhaps from G4535 (through the idea of quavering or reverberation)\",\"pronun\":{\"ipa\":\"ˈsɑl.piŋ.k͡s\",\"ipa_mod\":\"ˈsɑl.piŋ.k͡s\",\"sbl\":\"salpinx\",\"dic\":\"SAHL-peeng-ks\",\"dic_mod\":\"SAHL-peeng-ks\"},\"see\":[\"G4535\"]}', 'trump(-et)'),
(4436, 4537, 'σαλπίζω', '{\"def\":{\"short\":\"to trumpet, i.e., sound a blast (literally or figuratively)\",\"long\":[\"to sound a trumpet\"]},\"deriv\":\"from G4536\",\"pronun\":{\"ipa\":\"sɑlˈpi.zo\",\"ipa_mod\":\"sɑlˈpi.zow\",\"sbl\":\"salpizō\",\"dic\":\"sahl-PEE-zoh\",\"dic_mod\":\"sahl-PEE-zoh\"},\"see\":[\"G4536\"]}', '(which are yet to) sound (a trumpet)'),
(4437, 4538, 'σαλπιστής', '{\"def\":{\"short\":\"a trumpeter\",\"long\":[\"a trumpeter\"]},\"deriv\":\"from G4537\",\"pronun\":{\"ipa\":\"sɑl.piˈstes\",\"ipa_mod\":\"sɑl.piˈste̞s\",\"sbl\":\"salpistēs\",\"dic\":\"sahl-pee-STASE\",\"dic_mod\":\"sahl-pee-STASE\"},\"see\":[\"G4537\"]}', 'trumpeter'),
(4438, 4539, 'Σαλώμη', '{\"def\":{\"lit\":\"peaceful\",\"short\":\"Salome (i.e., Shelomah), an Israelitess\",\"long\":[\"the wife of Zebedee and the mother of the apostle James the elder and John\"]},\"deriv\":\"probably of Hebrew origin (feminine from H7965)\",\"pronun\":{\"ipa\":\"sɑˈlo.me\",\"ipa_mod\":\"sɑˈlow.me̞\",\"sbl\":\"salōmē\",\"dic\":\"sa-LOH-may\",\"dic_mod\":\"sa-LOH-may\"},\"see\":[\"H7965\"]}', 'Salome'),
(4439, 4540, 'Σαμάρεια', '{\"def\":{\"lit\":\"guardianship\",\"short\":\"Samaria (i.e., Shomeron), a city and region of Palestine\",\"long\":[\"a territory in Palestine, which had Samaria as its capital\"]},\"deriv\":\"of Hebrew origin (H8111)\",\"pronun\":{\"ipa\":\"sɑˈmɑ.ri.ɑ\",\"ipa_mod\":\"sɑˈmɑ.ri.ɑ\",\"sbl\":\"samareia\",\"dic\":\"sa-MA-ree-ah\",\"dic_mod\":\"sa-MA-ree-ah\"},\"see\":[\"H8111\"]}', 'Samaria'),
(4440, 4541, 'Σαμαρείτης', '{\"def\":{\"short\":\"a Samarite, i.e., inhabitant of Samaria\",\"long\":[\"a Samaritan,\",[\"an inhabitant of the city of Samaria\",\"an inhabitant of the region of Samaria\"]]},\"deriv\":\"from G4540\",\"pronun\":{\"ipa\":\"sɑ.mɑˈri.tes\",\"ipa_mod\":\"sɑ.mɑˈri.te̞s\",\"sbl\":\"samareitēs\",\"dic\":\"sa-ma-REE-tase\",\"dic_mod\":\"sa-ma-REE-tase\"},\"see\":[\"G4540\"]}', 'Samaritan'),
(4441, 4542, 'Σαμαρεῖτις', '{\"def\":{\"short\":\"a Samaritess, i.e., woman of Samaria\",\"long\":[\"a Samaritan woman\"]},\"deriv\":\"feminine of G4541\",\"pronun\":{\"ipa\":\"sɑ.mɑˈri.tis\",\"ipa_mod\":\"sɑ.mɑˈri.tis\",\"sbl\":\"samareitis\",\"dic\":\"sa-ma-REE-tees\",\"dic_mod\":\"sa-ma-REE-tees\"},\"see\":[\"G4541\"]}', 'of Samaria'),
(4442, 4543, 'Σαμοθρᾴκη', '{\"def\":{\"lit\":\"a sign of rags\",\"short\":\"Samo-thrace (Samos of Thrace), an island in the Mediterranean\",\"long\":[\"an island on the Aegean Sea, about 38 Miles (60 km) from the coast of Thrace at the mouth of the river Hebrus\"]},\"deriv\":\"from G4544 and Θτρᾴκη (Thrace)\",\"pronun\":{\"ipa\":\"sɑ.moˈθrɑ.ke\",\"ipa_mod\":\"sɑ.mowˈθrɑ.ke̞\",\"sbl\":\"samothrakē\",\"dic\":\"sa-moh-THRA-kay\",\"dic_mod\":\"sa-moh-THRA-kay\"},\"see\":[\"G4544\"]}', 'Samothracia'),
(4443, 4544, 'Σάμος', '{\"def\":{\"lit\":\"a sandy bluff\",\"short\":\"Samus, an island of the Mediterranean\",\"long\":[\"an island off that part of Asia Minor where Ionia touches Caria\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ˈsɑ.mos\",\"ipa_mod\":\"ˈsɑ.mows\",\"sbl\":\"samos\",\"dic\":\"SA-mose\",\"dic_mod\":\"SA-mose\"}}', 'Samos'),
(4444, 4545, 'Σαμουήλ', '{\"def\":{\"lit\":\"his name is of God\",\"short\":\"Samuel (i.e., Shemuel), an Israelite\",\"long\":[\"the son of Elkanah and Hannah, the last of the judges, a distinguished prophet, and a founder of the prophetic order, he gave the Jews there first kings, Saul, David\"]},\"deriv\":\"of Hebrew origin (H8050)\",\"pronun\":{\"ipa\":\"sɑ.muˈel\",\"ipa_mod\":\"sɑ.muˈe̞l\",\"sbl\":\"samouēl\",\"dic\":\"sa-moo-ALE\",\"dic_mod\":\"sa-moo-ALE\"},\"see\":[\"H8050\"]}', 'Samuel'),
(4445, 4546, 'Σαμψών', '{\"def\":{\"lit\":\"like the sun\",\"short\":\"Sampson (i.e., Shimshon), an Israelite\",\"long\":[\"a famous judge of Israel renowned for his physical strength\"]},\"deriv\":\"of Hebrew origin (H8123)\",\"pronun\":{\"ipa\":\"sɑmˈp͡son\",\"ipa_mod\":\"sɑmˈp͡sown\",\"sbl\":\"sampsōn\",\"dic\":\"sahm-PSONE\",\"dic_mod\":\"sahm-PSONE\"},\"see\":[\"H8123\"]}', 'Samson'),
(4446, 4547, 'σανδάλιον', '{\"def\":{\"short\":\"a slipper or sole-pad\",\"long\":[\"a sandal, a sole made of wood or leather, covering the bottom of the foot and bound on with thongs\"]},\"deriv\":\"neuter of a derivative of σάνδαλον (a \\\"sandal\\\"; of uncertain origin)\",\"pronun\":{\"ipa\":\"sɑnˈðɑ.li.on\",\"ipa_mod\":\"sɑn̪ˈðɑ.li.own\",\"sbl\":\"sandalion\",\"dic\":\"sahn-THA-lee-one\",\"dic_mod\":\"sahn-THA-lee-one\"}}', 'sandal'),
(4447, 4548, 'σανίς', '{\"def\":{\"short\":\"a plank\",\"long\":[\"a board, a plank\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"sɑˈnis\",\"ipa_mod\":\"sɑˈnis\",\"sbl\":\"sanis\",\"dic\":\"sa-NEES\",\"dic_mod\":\"sa-NEES\"}}', 'board'),
(4448, 4549, 'Σαούλ', '{\"def\":{\"lit\":\"desired\",\"short\":\"Saul (i.e., Shaul), the Jewish name of Paul\",\"long\":[\"the Jewish name of the apostle Paul\",\"the son of Kish and the first king of Israel\"]},\"deriv\":\"of Hebrew origin (H7586)\",\"pronun\":{\"ipa\":\"sɑˈul\",\"ipa_mod\":\"sɑˈul\",\"sbl\":\"saoul\",\"dic\":\"sa-OOL\",\"dic_mod\":\"sa-OOL\"},\"see\":[\"H7586\",\"G4569\"],\"comment\":\"Compare G4569.\"}', 'Saul'),
(4449, 4550, 'σαπρός', '{\"def\":{\"short\":\"rotten, i.e., worthless (literally or morally)\",\"long\":[\"rotten, putrefied\",\"corrupted by one and no longer fit for use, worn out\",\"of poor quality, bad, unfit for use, worthless\"]},\"deriv\":\"from G4595\",\"pronun\":{\"ipa\":\"sɑˈpros\",\"ipa_mod\":\"sɑˈprows\",\"sbl\":\"sapros\",\"dic\":\"sa-PROSE\",\"dic_mod\":\"sa-PROSE\"},\"see\":[\"G4190\",\"G4595\"],\"comment\":\"Compare G4190.\"}', 'bad, corrupt'),
(4450, 4551, 'Σαπφείρη', '{\"def\":{\"lit\":\"a sapphire\",\"short\":\"Sapphire, an Israelitess\",\"long\":[\"the wife of Ananias\"]},\"deriv\":\"feminine of G4552\",\"pronun\":{\"ipa\":\"sɑpˈfi.re\",\"ipa_mod\":\"sɑp̪ˈfi.re̞\",\"sbl\":\"sappheirē\",\"dic\":\"sahp-FEE-ray\",\"dic_mod\":\"sahp-FEE-ray\"},\"see\":[\"G4552\"]}', 'Sapphira'),
(4451, 4552, 'σάπφειρος', '{\"def\":{\"short\":\"a \\\"sapphire\\\" or lapis-lazuli gem\",\"long\":[\"sapphire, a precious stone\"]},\"deriv\":\"of Hebrew origin (H5601)\",\"pronun\":{\"ipa\":\"ˈsɑp.fi.ros\",\"ipa_mod\":\"ˈsɑp̪.fi.rows\",\"sbl\":\"sappheiros\",\"dic\":\"SAHP-fee-rose\",\"dic_mod\":\"SAHP-fee-rose\"},\"see\":[\"H5601\"]}', 'sapphire'),
(4452, 4553, 'σαργάνη', '{\"def\":{\"short\":\"a basket (as interwoven or wicker-work\",\"long\":[\"a braided rope, a band\",\"a basket, a basket made of ropes, a hamper\"]},\"deriv\":\"apparently of Hebrew origin (H8276)\",\"pronun\":{\"ipa\":\"sɑrˈɣɑ.ne\",\"ipa_mod\":\"sɑrˈɣɑ.ne̞\",\"sbl\":\"sarganē\",\"dic\":\"sahr-GA-nay\",\"dic_mod\":\"sahr-GA-nay\"},\"see\":[\"H8276\"]}', 'basket'),
(4453, 4554, 'Σάρδεις', '{\"def\":{\"lit\":\"red ones\",\"short\":\"Sardis, a place in Asia Minor\",\"long\":[\"a luxurious city in Asia Minor, the capital of Lydia\"]},\"deriv\":\"plural of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈsɑr.ðis\",\"ipa_mod\":\"ˈsɑr.ðis\",\"sbl\":\"sardeis\",\"dic\":\"SAHR-thees\",\"dic_mod\":\"SAHR-thees\"}}', 'Sardis'),
(4454, 4555, 'σάρδινος', '{\"def\":{\"short\":\"sardine (G3037 being implied), i.e., a gem, so called\",\"long\":[\"a sardius, a precious stone of which there are two types, the former is called a carnelian (because flesh colored) and the latter a sard\"]},\"deriv\":\"from the same as G4556\",\"pronun\":{\"ipa\":\"ˈsɑr.ði.nos\",\"ipa_mod\":\"ˈsɑr.ði.nows\",\"sbl\":\"sardinos\",\"dic\":\"SAHR-thee-nose\",\"dic_mod\":\"SAHR-thee-nose\"},\"see\":[\"G3037\",\"G4556\"]}', 'sardine'),
(4455, 4556, 'σάρδιος', '{\"def\":{\"short\":\"sardian (3037 being implied), i.e., (as noun) the gem so called\",\"long\":[\"a sardius, a precious stone of which there are two types, the former is called a carnelian (because flesh colored) and the latter a sard\"]},\"deriv\":\"properly, an adjective from an uncertain base\",\"pronun\":{\"ipa\":\"ˈsɑr.ði.os\",\"ipa_mod\":\"ˈsɑr.ði.ows\",\"sbl\":\"sardios\",\"dic\":\"SAHR-thee-ose\",\"dic_mod\":\"SAHR-thee-ose\"}}', 'sardius');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(4456, 4557, 'σαρδόνυξ', '{\"def\":{\"short\":\"a \\\"sardonyx\\\", i.e., the gem so called\",\"long\":[\"a sardonyx, a precious stone marked by the red colors of the carnelian (sard) and the white of the onyx\"]},\"deriv\":\"from the base of G4556 and ὄνυξ (the nail of a finger; hence the \\\"onyx\\\" stone)\",\"pronun\":{\"ipa\":\"sɑrˈðo.nyk͡s\",\"ipa_mod\":\"sɑrˈðow.njuk͡s\",\"sbl\":\"sardonyx\",\"dic\":\"sahr-THOH-nooks\",\"dic_mod\":\"sahr-THOH-nyooks\"},\"see\":[\"G4556\"]}', 'sardonyx'),
(4457, 4558, 'Σάρεπτα', '{\"def\":{\"lit\":\"smelting\",\"short\":\"Sarepta (i.e., Tsarephath), a place in Palestine\",\"long\":[\"the Greek name for Zarephath, a Phoenician town between Tyre and Sidon, but nearer Sidon\"]},\"deriv\":\"of Hebrew origin (H6886)\",\"pronun\":{\"ipa\":\"ˈsɑ.rɛ.ptɑ\",\"ipa_mod\":\"ˈsɑ.re̞.ptɑ\",\"sbl\":\"sarepta\",\"dic\":\"SA-reh-pta\",\"dic_mod\":\"SA-ray-pta\"},\"see\":[\"H6886\"]}', 'Sarepta'),
(4458, 4559, 'σαρκικός', '{\"def\":{\"short\":\"pertaining to flesh, i.e., (by extension) bodily, temporal, or (by implication) animal, unregenerate\",\"long\":[\"fleshly, carnal\",[\"having the nature of flesh, i.e., under the control of the animal appetites\",[\"governed by mere human nature not by the Spirit of God\",\"having its seat in the animal nature or aroused by the animal nature\",\"human: with the included idea of depravity\"],\"pertaining to the flesh\",[\"to the body: related to birth, linage, etc\"]]]},\"deriv\":\"from G4561\",\"pronun\":{\"ipa\":\"sɑr.kiˈkos\",\"ipa_mod\":\"sɑr.ciˈkows\",\"sbl\":\"sarkikos\",\"dic\":\"sahr-kee-KOSE\",\"dic_mod\":\"sahr-kee-KOSE\"},\"see\":[\"G4561\"]}', 'carnal, fleshly'),
(4459, 4560, 'σάρκινος', '{\"def\":{\"short\":\"similar to flesh, i.e., (by analogy) soft\",\"long\":[\"fleshly, consisting of flesh, composed of flesh\",\"pertaining to the body (as earthly and perishable material)\",\"wholly given up to the flesh, rooted in the flesh as it were\"]},\"deriv\":\"from G4561\",\"pronun\":{\"ipa\":\"ˈsɑr.ki.nos\",\"ipa_mod\":\"ˈsɑr.ci.nows\",\"sbl\":\"sarkinos\",\"dic\":\"SAHR-kee-nose\",\"dic_mod\":\"SAHR-kee-nose\"},\"see\":[\"G4561\"]}', 'fleshly'),
(4460, 4561, 'σάρξ', '{\"def\":{\"short\":\"flesh (as stripped of the skin), i.e., (strictly) the meat of an animal (as food), or (by extension) the body (as opposed to the soul (or spirit), or as the symbol of what is external, or as the means of kindred), or (by implication) human nature (with its frailties (physically or morally) and passions), or (specially), a human being (as such)\",\"long\":[\"flesh (the soft substance of the living body, which covers the bones and is permeated with blood) of both man and beasts\",\"the body\",[\"the body of a man\",\"used of natural or physical origin, generation or relationship\",[\"born of natural generation\"],\"the sensuous nature of man, \\\"the animal nature\\\"\",[\"without any suggestion of depravity\",\"the animal nature with cravings which incite to sin\",\"the physical nature of man as subject to suffering\"]],\"a living creature (because possessed of a body of flesh) whether man or beast\",\"the flesh, denotes mere human nature, the earthly nature of man apart from divine influence, and therefore prone to sin and opposed to God\"]},\"deriv\":\"probably from the base of G4563\",\"pronun\":{\"ipa\":\"ˈsɑr.k͡s\",\"ipa_mod\":\"ˈsɑr.k͡s\",\"sbl\":\"sarx\",\"dic\":\"SAHR-ks\",\"dic_mod\":\"SAHR-ks\"},\"see\":[\"G4563\"]}', 'carnal(-ly), + carnally minded, flesh(-ly)'),
(4461, 4562, 'Σαρούχ', '{\"def\":{\"lit\":\"intertwined\",\"short\":\"Saruch (i.e., Serug), a patriarch\",\"long\":[\"the son of Reu in the genealogy of Christ\"]},\"deriv\":\"of Hebrew origin (H8286)\",\"pronun\":{\"ipa\":\"sɑˈrux\",\"ipa_mod\":\"sɑˈrux\",\"sbl\":\"sarouch\",\"dic\":\"sa-ROOK\",\"dic_mod\":\"sa-ROOK\"},\"see\":[\"H8286\"]}', 'Saruch'),
(4462, 4563, 'σαρόω', '{\"def\":{\"short\":\"meaning a broom; to sweep\",\"long\":[\"to sweep, clean by sweeping\"]},\"deriv\":\"from a derivative of σαίρω (to brush off; akin to G4951)\",\"pronun\":{\"ipa\":\"sɑˈro.o\",\"ipa_mod\":\"sɑˈrow.ow\",\"sbl\":\"saroō\",\"dic\":\"sa-ROH-oh\",\"dic_mod\":\"sa-ROH-oh\"},\"see\":[\"G4951\"]}', 'sweep'),
(4463, 4564, 'Σάῤῥα', '{\"def\":{\"lit\":\"princess\",\"short\":\"Sarra (i.e., Sarah), the wife of Abraham\",\"long\":[\"the wife of Abraham\"]},\"deriv\":\"of Hebrew origin (H8283)\",\"pronun\":{\"ipa\":\"ˈsɑr.rɑ\",\"ipa_mod\":\"ˈsɑr.rɑ\",\"sbl\":\"sarrha\",\"dic\":\"SAHR-ra\",\"dic_mod\":\"SAHR-ra\"},\"see\":[\"H8283\"]}', 'Sara, Sarah'),
(4464, 4565, 'Σάρων', '{\"def\":{\"lit\":\"plain, level\",\"short\":\"Saron (i.e., Sharon), a district of Palestine\",\"long\":[\"a level region extending from Caesarea of Palestine as far as Joppa, abounding in good pasture and famous for its fertility\"]},\"deriv\":\"of Hebrew origin (H8289)\",\"pronun\":{\"ipa\":\"ˈsɑ.ron\",\"ipa_mod\":\"ˈsɑ.rown\",\"sbl\":\"sarōn\",\"dic\":\"SA-rone\",\"dic_mod\":\"SA-rone\"},\"see\":[\"H8289\"]}', 'Saron'),
(4465, 4566, 'Σατᾶν', '{\"def\":{\"short\":\"Satan, i.e., the devil\",\"long\":[\"adversary (one who opposes another in purpose or act), the name given to\",[\"the prince of evil spirits, the inveterate adversary of God and Christ\",[\"he incites apostasy from God and to sin\",\"circumventing men by his wiles\",\"the worshippers of idols are said to be under his control\",\"by his demons he is able to take possession of men and inflict them with diseases\",\"by God\'s assistance he is overcome\",\"on Christ\'s return from heaven he will be bound with chains for a thousand years, but when the thousand years are finished he will walk the earth in yet greater power, but shortly after will be given over to eternal punishment\"],\"a Satan-like man\"]]},\"deriv\":\"of Hebrew origin (H7854)\",\"pronun\":{\"ipa\":\"sɑˈtɑn\",\"ipa_mod\":\"sɑˈtɑn\",\"sbl\":\"satan\",\"dic\":\"sa-TAHN\",\"dic_mod\":\"sa-TAHN\"},\"see\":[\"H7854\",\"G4567\"],\"comment\":\"Compare G4567.\"}', 'Satan'),
(4466, 4567, 'Σατανᾶς', '{\"def\":{\"short\":\"the accuser, i.e., the devil\",\"long\":[\"adversary (one who opposes another in purpose or act), the name given to\",[\"the prince of evil spirits, the inveterate adversary of God and Christ\",[\"he incites apostasy from God and to sin\",\"circumventing men by his wiles\",\"the worshippers of idols are said to be under his control\",\"by his demons he is able to take possession of men and inflict them with diseases\",\"by God\'s assistance he is overcome\",\"on Christ\'s return from heaven he will be bound with chains for a thousand years, but when the thousand years are finished he will walk the earth in yet greater power, but shortly after will be given over to eternal punishment\"],\"a Satan-like man\"]]},\"deriv\":\"of Chaldee origin corresponding to H4566 (with the definite affix)\",\"pronun\":{\"ipa\":\"sɑ.tɑˈnɑs\",\"ipa_mod\":\"sɑ.tɑˈnɑs\",\"sbl\":\"satanas\",\"dic\":\"sa-ta-NAHS\",\"dic_mod\":\"sa-ta-NAHS\"},\"see\":[\"H4566\"]}', 'Satan'),
(4467, 4568, 'σάτον', '{\"def\":{\"short\":\"a certain measure for things dry\",\"long\":[\"a kind of dry measure, 3 gallons (14 liters)\"]},\"deriv\":\"of Hebrew origin (H5429)\",\"pronun\":{\"ipa\":\"ˈsɑ.ton\",\"ipa_mod\":\"ˈsɑ.town\",\"sbl\":\"saton\",\"dic\":\"SA-tone\",\"dic_mod\":\"SA-tone\"},\"see\":[\"H5429\"]}', 'measure'),
(4468, 4569, 'Σαῦλος', '{\"def\":{\"lit\":\"desired\",\"short\":\"Saulus (i.e., Shaul), the Jewish name of Paul\",\"long\":[\"the Jewish name of the apostle Paul\"]},\"deriv\":\"of Hebrew origin, the same as G4549\",\"pronun\":{\"ipa\":\"ˈsɛβ.los\",\"ipa_mod\":\"ˈsa.lows\",\"sbl\":\"saulos\",\"dic\":\"SEV-lose\",\"dic_mod\":\"SA-lose\"},\"see\":[\"G4549\"]}', 'Saul'),
(4469, 4570, 'σβέννυμι', '{\"def\":{\"short\":\"to extinguish (literally or figuratively)\",\"long\":[\"to extinguish, quench\",[\"of fire or things on fire\",[\"to be quenched, to go out\"],\"metaphorically to quench, to suppress, stifle\",[\"of divine influence\"]]]},\"deriv\":\"a prolonged form of an apparently primary verb\",\"pronun\":{\"ipa\":\"sˈβɛn.ny.mi\",\"ipa_mod\":\"sˈve̞n.nju.mi\",\"sbl\":\"sbennymi\",\"dic\":\"s-VEN-noo-mee\",\"dic_mod\":\"s-VANE-nyoo-mee\"}}', 'go out, quench'),
(4470, 4571, 'σέ', '{\"def\":{\"short\":\"thee\",\"long\":[\"thou, thee\"]},\"deriv\":\"accusative case singular of G4771\",\"pronun\":{\"ipa\":\"sɛ\",\"ipa_mod\":\"se̞\",\"sbl\":\"se\",\"dic\":\"seh\",\"dic_mod\":\"say\"},\"see\":[\"G4771\"]}', 'thee, thou, X thy house'),
(4471, 4572, 'σεαυτοῦ', '{\"def\":{\"short\":\"of (with, to) thyself\",\"long\":[\"thyself, thee\"]},\"deriv\":\"genitive case from G4571 and G0846, also dative case of the same, σεαυτῷ <pronunciation strongs=\\\"seh-ow-to\'\\\"/>, and accusative case σεαυτόν <pronunciation strongs=\\\"seh-ow-ton\'\\\"/>, likewise contracted σαυτοῦ <pronunciation strongs=\\\"sow-too\'\\\"/>, σαυτῷ <pronunciation strongs=\\\"sow-to\'\\\"/>, and σαυτόν <pronunciation strongs=\\\"sow-ton\'\\\"/>, respectively\",\"pronun\":{\"ipa\":\"sɛ.ɑβˈtu\",\"ipa_mod\":\"se̞.ɑfˈtu\",\"sbl\":\"seautou\",\"dic\":\"seh-av-TOO\",\"dic_mod\":\"say-af-TOO\"},\"see\":[\"G0846\",\"G4571\"]}', 'thee, thine own self, (thou) thy(-self)'),
(4472, 4573, 'σεβάζομαι', '{\"def\":{\"short\":\"to venerate, i.e., adore\",\"long\":[\"to fear, be afraid\",\"to honor religiously, to worship\"]},\"deriv\":\"middle voice from a derivative of G4576\",\"pronun\":{\"ipa\":\"sɛˈβɑ.zo.mɛ\",\"ipa_mod\":\"se̞ˈvɑ.zow.me\",\"sbl\":\"sebazomai\",\"dic\":\"seh-VA-zoh-meh\",\"dic_mod\":\"say-VA-zoh-may\"},\"see\":[\"G4576\"]}', 'worship'),
(4473, 4574, 'σέβασμα', '{\"def\":{\"short\":\"something adored, i.e., an object of worship (god, altar, etc)\",\"long\":[\"whatever is religiously honored, an object of worship\",[\"of temples, altars, statues, idolatrous images\"]]},\"deriv\":\"from G4573\",\"pronun\":{\"ipa\":\"ˈsɛ.βɑ.smɑ\",\"ipa_mod\":\"ˈse̞.vɑ.smɑ\",\"sbl\":\"sebasma\",\"dic\":\"SEH-va-sma\",\"dic_mod\":\"SAY-va-sma\"},\"see\":[\"G4573\"]}', 'devotion, that is worshipped'),
(4474, 4575, 'σεβαστός', '{\"def\":{\"short\":\"venerable (august), i.e., (as noun) a title of the Roman Emperor, or (as adjective) imperial\",\"long\":[\"reverend, venerable\",\"the title of the Roman emperors\",[\"Augustan, i.e., taking its name from the emperor\",\"a title of honor which used to be given certain legions, or cohorts, or battalions, \\\"for valour\\\"\"]]},\"deriv\":\"from G4573\",\"pronun\":{\"ipa\":\"sɛ.βɑˈstos\",\"ipa_mod\":\"se̞.vɑˈstows\",\"sbl\":\"sebastos\",\"dic\":\"seh-va-STOSE\",\"dic_mod\":\"say-va-STOSE\"},\"see\":[\"G4573\"]}', 'Augustus(-\')'),
(4475, 4576, 'σέβομαι', '{\"def\":{\"short\":\"to revere, i.e., adore\",\"long\":[\"to revere, to worship\"]},\"deriv\":\"middle voice of an apparently primary verb\",\"pronun\":{\"ipa\":\"ˈsɛ.βo.mɛ\",\"ipa_mod\":\"ˈse̞.vow.me\",\"sbl\":\"sebomai\",\"dic\":\"SEH-voh-meh\",\"dic_mod\":\"SAY-voh-may\"}}', 'devout, religious, worship'),
(4476, 4577, 'σειρά', '{\"def\":{\"short\":\"akin to G0138); a chain (as binding or drawing)\",\"long\":[\"a line, a rope\",\"a chain\"]},\"deriv\":\"probably from G4951 through its congener εἴρω (to fasten\",\"pronun\":{\"ipa\":\"siˈrɑ\",\"ipa_mod\":\"siˈrɑ\",\"sbl\":\"seira\",\"dic\":\"see-RA\",\"dic_mod\":\"see-RA\"},\"see\":[\"G0138\",\"G4951\"]}', 'chain'),
(4477, 4578, 'σεισμός', '{\"def\":{\"short\":\"a commotion, i.e., (of the air) a gale, (of the ground) an earthquake\",\"long\":[\"a shaking, a commotion\",\"a tempest\",\"an earthquake\"]},\"deriv\":\"from G4579\",\"pronun\":{\"ipa\":\"siˈsmos\",\"ipa_mod\":\"siˈsmows\",\"sbl\":\"seismos\",\"dic\":\"see-SMOSE\",\"dic_mod\":\"see-SMOSE\"},\"see\":[\"G4579\"]}', 'earthquake, tempest'),
(4478, 4579, 'σείω', '{\"def\":{\"short\":\"to rock (vibrate, properly, sideways or to and fro), i.e., (generally) to agitate (in any direction; cause to tremble); figuratively, to throw into a tremor (of fear or concern)\",\"long\":[\"to shake, agitate, cause to tremble\",[\"of men, to be thrown into a tremor, to quake for fear\",\"metaphorically to agitate the mind\"]]},\"deriv\":\"apparently a primary verb\",\"pronun\":{\"ipa\":\"ˈsi.o\",\"ipa_mod\":\"ˈsi.ow\",\"sbl\":\"seiō\",\"dic\":\"SEE-oh\",\"dic_mod\":\"SEE-oh\"}}', 'move, quake, shake'),
(4479, 4580, 'Σεκοῦνδος', '{\"def\":{\"lit\":\"fortunate\",\"short\":\"\\\"second\\\"; Secundus, a Christian\",\"long\":[\"a Thessalonian Christian\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"sɛˈkun.ðos\",\"ipa_mod\":\"se̞ˈkun̪.ðows\",\"sbl\":\"sekoundos\",\"dic\":\"seh-KOON-those\",\"dic_mod\":\"say-KOON-those\"}}', 'Secundus'),
(4480, 4581, 'Σελεύκεια', '{\"def\":{\"lit\":\"white light\",\"short\":\"Seleuceia, a place in Syria\",\"long\":[\"a city of Syria near the mouth of the Orontes, about 16 miles (25 km) from Antioch\"]},\"deriv\":\"from Σέλευκος (Seleucus, a Syrian king)\",\"pronun\":{\"ipa\":\"sɛˈlɛβ.ki.ɑ\",\"ipa_mod\":\"se̞ˈlef.ci.ɑ\",\"sbl\":\"seleukeia\",\"dic\":\"seh-LEV-kee-ah\",\"dic_mod\":\"say-LAYF-kee-ah\"}}', 'Seleucia'),
(4481, 4582, 'σελήνη', '{\"def\":{\"short\":\"the moon\",\"long\":[\"the moon\"]},\"deriv\":\"from σέλας (brilliancy; probably akin to the alternate of G0138, through the idea of attractiveness)\",\"pronun\":{\"ipa\":\"sɛˈle.ne\",\"ipa_mod\":\"se̞ˈle̞.ne̞\",\"sbl\":\"selēnē\",\"dic\":\"seh-LAY-nay\",\"dic_mod\":\"say-LAY-nay\"},\"see\":[\"G0138\"]}', 'moon'),
(4482, 4583, 'σεληνιάζομαι', '{\"def\":{\"short\":\"to be moon-struck, i.e., crazy\",\"long\":[\"to be moon-struck, to be a lunatic\",\"to be epileptic\",[\"epilepsy being supposed to return and increase with the increase of the moon\"]]},\"deriv\":\"middle voice or passive from a presumed derivative of G4582\",\"pronun\":{\"ipa\":\"sɛ.le.niˈɑ.zo.mɛ\",\"ipa_mod\":\"se̞.le̞.niˈɑ.zow.me\",\"sbl\":\"selēniazomai\",\"dic\":\"seh-lay-nee-AH-zoh-meh\",\"dic_mod\":\"say-lay-nee-AH-zoh-may\"},\"see\":[\"G4582\"]}', 'be a lunatic'),
(4483, 4584, 'Σεμεΐ', '{\"def\":{\"lit\":\"harkeners, my report\",\"short\":\"Semei (i.e., Shimi), an Israelite\",\"long\":[\"the father of Mattathias in the genealogy of Christ\"]},\"deriv\":\"of Hebrew origin (H8096)\",\"pronun\":{\"ipa\":\"sɛ.mɛˈi\",\"ipa_mod\":\"se̞.me̞ˈi\",\"sbl\":\"semei\",\"dic\":\"seh-meh-EE\",\"dic_mod\":\"say-may-EE\"},\"see\":[\"H8096\"]}', 'Semei'),
(4484, 4585, 'σεμίδαλις', '{\"def\":{\"short\":\"fine wheaten flour\",\"long\":[\"the finest wheat flour\"]},\"deriv\":\"probably of foreign origin\",\"pronun\":{\"ipa\":\"sɛˈmi.ðɑ.lis\",\"ipa_mod\":\"se̞ˈmi.ðɑ.lis\",\"sbl\":\"semidalis\",\"dic\":\"seh-MEE-tha-lees\",\"dic_mod\":\"say-MEE-tha-lees\"}}', 'fine flour'),
(4485, 4586, 'σεμνός', '{\"def\":{\"short\":\"venerable, i.e., honorable\",\"long\":[\"august, venerable, reverend\",\"to be venerated for character, honorable\",[\"of persons\",\"of deeds\"]]},\"deriv\":\"from G4576\",\"pronun\":{\"ipa\":\"sɛmˈnos\",\"ipa_mod\":\"se̞mˈnows\",\"sbl\":\"semnos\",\"dic\":\"sem-NOSE\",\"dic_mod\":\"same-NOSE\"},\"see\":[\"G4576\"]}', 'grave, honest'),
(4486, 4587, 'σεμνότης', '{\"def\":{\"short\":\"venerableness, i.e., probity\",\"long\":[\"the characteristic of a thing or person which entitles to reverence and respect, dignity, majesty, sanctity\",\"honor, purity\"]},\"deriv\":\"from G4586\",\"pronun\":{\"ipa\":\"sɛmˈno.tes\",\"ipa_mod\":\"se̞mˈnow.te̞s\",\"sbl\":\"semnotēs\",\"dic\":\"sem-NOH-tase\",\"dic_mod\":\"same-NOH-tase\"},\"see\":[\"G4586\"]}', 'gravity, honesty'),
(4487, 4588, 'Σέργιος', '{\"def\":{\"lit\":\"earth-born, born a wonder\",\"short\":\"Sergius, a Roman\",\"long\":[\"surnamed Paulus, a deputy or proconsul of Cyprus and converted to Christianity by Paul\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"ˈsɛr.ɣi.os\",\"ipa_mod\":\"ˈse̞r.ʝi.ows\",\"sbl\":\"sergios\",\"dic\":\"SER-gee-ose\",\"dic_mod\":\"SARE-gee-ose\"}}', 'Sergius'),
(4488, 4589, 'Σήθ', '{\"def\":{\"lit\":\"compensation\",\"short\":\"Seth (i.e., Sheth), a patriarch\",\"long\":[\"the third son of Adam and the father of Enos\"]},\"deriv\":\"of Hebrew origin (H8352)\",\"pronun\":{\"ipa\":\"seθ\",\"ipa_mod\":\"se̞θ\",\"sbl\":\"sēth\",\"dic\":\"sayth\",\"dic_mod\":\"sayth\"},\"see\":[\"H8352\"]}', 'Seth'),
(4489, 4590, 'Σήμ', '{\"def\":{\"lit\":\"name\",\"short\":\"Sem (i.e., Shem), a patriarch\",\"long\":[\"the eldest son of Noah\"]},\"deriv\":\"of Hebrew origin (H8035)\",\"pronun\":{\"ipa\":\"sem\",\"ipa_mod\":\"se̞m\",\"sbl\":\"sēm\",\"dic\":\"same\",\"dic_mod\":\"same\"},\"see\":[\"H8035\"]}', 'Sem'),
(4490, 4591, 'σημαίνω', '{\"def\":{\"short\":\"to indicate\",\"long\":[\"to give a sign, to signify, indicate\",\"to make known\"]},\"deriv\":\"from σῆμα (a mark; of uncertain derivation)\",\"pronun\":{\"ipa\":\"seˈmɛ.no\",\"ipa_mod\":\"se̞ˈme.now\",\"sbl\":\"sēmainō\",\"dic\":\"say-MEH-noh\",\"dic_mod\":\"say-MAY-noh\"}}', 'signify'),
(4491, 4592, 'σημεῖον', '{\"def\":{\"short\":\"an indication, especially ceremonially or supernaturally\",\"long\":[\"a sign, mark, token\",[\"that by which a person or a thing is distinguished from others and is known\",\"a sign, prodigy, portent, i.e., an unusual occurrence, transcending the common course of nature\",[\"of signs portending remarkable events soon to happen\",\"of miracles and wonders by which God authenticates the men sent by him, or by which men prove that the cause they are pleading is God\'s\"]]]},\"deriv\":\"neuter of a presumed derivative of the base of G4591\",\"pronun\":{\"ipa\":\"seˈmi.on\",\"ipa_mod\":\"se̞ˈmi.own\",\"sbl\":\"sēmeion\",\"dic\":\"say-MEE-one\",\"dic_mod\":\"say-MEE-one\"},\"see\":[\"G4591\"]}', 'miracle, sign, token, wonder'),
(4492, 4593, 'σημειόω', '{\"def\":{\"short\":\"to distinguish, i.e., mark (for avoidance)\",\"long\":[\"to mark, to note, distinguish by marking\",\"to mark or note for one\'s self\"]},\"deriv\":\"from G4592\",\"pronun\":{\"ipa\":\"se.miˈo.o\",\"ipa_mod\":\"se̞.miˈow.ow\",\"sbl\":\"sēmeioō\",\"dic\":\"say-mee-OH-oh\",\"dic_mod\":\"say-mee-OH-oh\"},\"see\":[\"G4592\"]}', 'note'),
(4493, 4594, 'σήμερον', '{\"def\":{\"short\":\"on the (i.e., this) day (or night current or just passed); generally, now (i.e., at present, hitherto)\",\"long\":[\"this (very) day)\",\"what has happened today\"]},\"deriv\":\"neuter (as adverb) of a presumed compound of the article G3588 (t changed to s) and G2250\",\"pronun\":{\"ipa\":\"ˈse.mɛ.ron\",\"ipa_mod\":\"ˈse̞.me̞.rown\",\"sbl\":\"sēmeron\",\"dic\":\"SAY-meh-rone\",\"dic_mod\":\"SAY-may-rone\"},\"see\":[\"G2250\",\"G3588\"]}', 'this (to-)day'),
(4494, 4595, 'σήπω', '{\"def\":{\"short\":\"to putrefy, i.e., (figuratively) perish\",\"long\":[\"to make corrupt, to destroy\",\"to become corrupt, corrupted, rotten\"]},\"deriv\":\"apparently a primary verb\",\"pronun\":{\"ipa\":\"ˈse.po\",\"ipa_mod\":\"ˈse̞.pow\",\"sbl\":\"sēpō\",\"dic\":\"SAY-poh\",\"dic_mod\":\"SAY-poh\"}}', 'be corrupted'),
(4495, 4596, 'σηρικός', '{\"def\":{\"short\":\"Seric, i.e., silken (neuter as noun, a silky fabric)\",\"long\":[\"made of silk\",\"silk, i.e., the fabric, silken garments\"]},\"deriv\":\"from Σήρ (an Indian tribe from whom silk was procured; hence the name of the silk-worm)\",\"pronun\":{\"ipa\":\"se.riˈkos\",\"ipa_mod\":\"se̞.riˈkows\",\"sbl\":\"sērikos\",\"dic\":\"say-ree-KOSE\",\"dic_mod\":\"say-ree-KOSE\"}}', 'silk'),
(4496, 4597, 'σής', '{\"def\":{\"short\":\"a moth\",\"long\":[\"a moth, the clothes moth\"]},\"deriv\":\"apparently of Hebrew origin (H5580)\",\"pronun\":{\"ipa\":\"ses\",\"ipa_mod\":\"se̞s\",\"sbl\":\"sēs\",\"dic\":\"sase\",\"dic_mod\":\"sase\"},\"see\":[\"H5580\"]}', 'moth'),
(4497, 4598, 'σητόβρωτος', '{\"def\":{\"short\":\"moth-eaten\",\"long\":[\"moth eaten\"]},\"deriv\":\"from G4597 and a derivative of G0977\",\"pronun\":{\"ipa\":\"seˈto.βro.tos\",\"ipa_mod\":\"se̞ˈtow.vrow.tows\",\"sbl\":\"sētobrōtos\",\"dic\":\"say-TOH-vroh-tose\",\"dic_mod\":\"say-TOH-vroh-tose\"},\"see\":[\"G0977\",\"G4597\"]}', 'motheaten'),
(4498, 4599, 'σθενόω', '{\"def\":{\"short\":\"to strengthen, i.e., (figuratively) confirm (in spiritual knowledge and power)\",\"long\":[\"to make strong, strengthen\",[\"of one\'s soul\"]]},\"deriv\":\"from σθένος (bodily vigor; probably akin to the base of G2476)\",\"pronun\":{\"ipa\":\"sθɛˈno.o\",\"ipa_mod\":\"sθe̞ˈnow.ow\",\"sbl\":\"sthenoō\",\"dic\":\"stheh-NOH-oh\",\"dic_mod\":\"sthay-NOH-oh\"},\"see\":[\"G2476\"]}', 'strengthen'),
(4499, 4600, 'σιαγών', '{\"def\":{\"short\":\"the jaw-bone, i.e., (by implication) the cheek or side of the face\",\"long\":[\"the jaw, the jaw bone\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"si.ɑˈɣon\",\"ipa_mod\":\"si.ɑˈɣown\",\"sbl\":\"siagōn\",\"dic\":\"see-ah-GONE\",\"dic_mod\":\"see-ah-GONE\"}}', 'cheek'),
(4500, 4601, 'σιγάω', '{\"def\":{\"short\":\"to keep silent (transitively or intransitively)\",\"long\":[\"to keep silence, hold one\'s peace\",\"to be kept in silence, be concealed\"]},\"deriv\":\"from G4602\",\"pronun\":{\"ipa\":\"siˈɣɑ.o\",\"ipa_mod\":\"siˈɣɑ.ow\",\"sbl\":\"sigaō\",\"dic\":\"see-GA-oh\",\"dic_mod\":\"see-GA-oh\"},\"see\":[\"G4602\"]}', 'keep close (secret, silence), hold peace'),
(4501, 4602, 'σιγή', '{\"def\":{\"short\":\"silence\",\"long\":[\"silence\"]},\"deriv\":\"apparently from σίζω (to hiss, i.e. hist or hush)\",\"pronun\":{\"ipa\":\"siˈɣe\",\"ipa_mod\":\"siˈɣe̞\",\"sbl\":\"sigē\",\"dic\":\"see-GAY\",\"dic_mod\":\"see-GAY\"},\"see\":[\"G4623\"],\"comment\":\"Compare G4623.\"}', 'silence'),
(4502, 4603, 'σιδήρεος', '{\"def\":{\"short\":\"made of iron\",\"long\":[\"made of iron\"]},\"deriv\":\"from G4604\",\"pronun\":{\"ipa\":\"siˈðe.rɛ.os\",\"ipa_mod\":\"siˈðe̞.re̞.ows\",\"sbl\":\"sidēreos\",\"dic\":\"see-THAY-reh-ose\",\"dic_mod\":\"see-THAY-ray-ose\"},\"see\":[\"G4604\"]}', '(of) iron'),
(4503, 4604, 'σίδηρος', '{\"def\":{\"short\":\"iron\",\"long\":[\"iron\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈsi.ðe.ros\",\"ipa_mod\":\"ˈsi.ðe̞.rows\",\"sbl\":\"sidēros\",\"dic\":\"SEE-thay-rose\",\"dic_mod\":\"SEE-thay-rose\"}}', 'iron'),
(4504, 4605, 'Σιδών', '{\"def\":{\"lit\":\"hunting\",\"short\":\"Sidon (i.e., Tsidon), a place in Palestine\",\"long\":[\"an ancient and wealthy city of Phoenicia, on the east coast of the Mediterranean Sea, less than 20 miles (30 km) north of Tyre\"]},\"deriv\":\"of Hebrew origin (H6721)\",\"pronun\":{\"ipa\":\"siˈðon\",\"ipa_mod\":\"siˈðown\",\"sbl\":\"sidōn\",\"dic\":\"see-THONE\",\"dic_mod\":\"see-THONE\"},\"see\":[\"H6721\"]}', 'Sidon'),
(4505, 4606, 'Σιδώνιος', '{\"def\":{\"short\":\"a Sidonian, i.e., inhabitant of Sidon\",\"long\":[\"an inhabitant of Sidon, a Sidonian\"]},\"deriv\":\"from G4605\",\"pronun\":{\"ipa\":\"siˈðo.ni.os\",\"ipa_mod\":\"siˈðow.ni.ows\",\"sbl\":\"sidōnios\",\"dic\":\"see-THOH-nee-ose\",\"dic_mod\":\"see-THOH-nee-ose\"},\"see\":[\"G4605\"]}', 'of Sidon'),
(4506, 4607, 'σικάριος', '{\"def\":{\"short\":\"a dagger-man or assassin; a freebooter (Jewish fanatic outlawed by the Romans)\",\"long\":[\"an assassin\",[\"one who carries a dagger or short sword under his clothing, that he may kill secretly and treacherously any one he wishes to\",\"a cutthroat\"]]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"siˈkɑ.ri.os\",\"ipa_mod\":\"siˈkɑ.ri.ows\",\"sbl\":\"sikarios\",\"dic\":\"see-KA-ree-ose\",\"dic_mod\":\"see-KA-ree-ose\"},\"see\":[\"G5406\"],\"comment\":\"Compare G5406.\"}', 'murderer'),
(4507, 4608, 'σίκερα', '{\"def\":{\"short\":\"an intoxicant, i.e., intensely fermented liquor\",\"long\":[\"strong drink, an intoxicating beverage, different from wine; it was a artificial product, made of a mixture of sweet ingredients, whether derived from grain and vegetables, or from the juice of fruits (dates), or a decoction of honey\"]},\"deriv\":\"of Hebrew origin (H7941)\",\"pronun\":{\"ipa\":\"ˈsi.kɛ.rɑ\",\"ipa_mod\":\"ˈsi.ce̞.rɑ\",\"sbl\":\"sikera\",\"dic\":\"SEE-keh-ra\",\"dic_mod\":\"SEE-kay-ra\"},\"see\":[\"H7941\"]}', 'strong drink'),
(4508, 4609, 'Σίλας', '{\"def\":{\"lit\":\"woody\",\"short\":\"Silas, a Christian\",\"long\":[\"a Roman citizen, the companion of the apostle Paul on several of his missionary journeys\"]},\"deriv\":\"contraction for G4610\",\"pronun\":{\"ipa\":\"ˈsi.lɑs\",\"ipa_mod\":\"ˈsi.lɑs\",\"sbl\":\"silas\",\"dic\":\"SEE-lahs\",\"dic_mod\":\"SEE-lahs\"},\"see\":[\"G4610\"]}', 'Silas'),
(4509, 4610, 'Σιλουανός', '{\"def\":{\"lit\":\"woody\",\"short\":\"\\\"silvan\\\"; Silvanus, a Christian\",\"long\":[\"a Roman citizen, the companion of the apostle Paul on several of his missionary journeys\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"si.lu.ɑˈnos\",\"ipa_mod\":\"si.lu.ɑˈnows\",\"sbl\":\"silouanos\",\"dic\":\"see-loo-ah-NOSE\",\"dic_mod\":\"see-loo-ah-NOSE\"},\"see\":[\"G4609\"],\"comment\":\"Compare G4609.\"}', 'Silvanus'),
(4510, 4611, 'Σιλωάμ', '{\"def\":{\"lit\":\"sent\",\"short\":\"Siloam (i.e., Shiloach), a pool of Jerusalem\",\"long\":[\"the Pool of Siloam, a fountain of water in Jerusalem (also called Shiloah in Isaiah 8:6)\",\"the Tower of Siloam (may have been a tower near or over the Pool of Siloam)\"]},\"deriv\":\"of Hebrew origin (H7975)\",\"pronun\":{\"ipa\":\"si.loˈɑm\",\"ipa_mod\":\"si.lowˈɑm\",\"sbl\":\"silōam\",\"dic\":\"see-loh-AM\",\"dic_mod\":\"see-loh-AM\"},\"see\":[\"H7975\"],\"comment\":\"The Tower of Siloam fell down killing 18 men.\"}', 'Siloam'),
(4511, 4612, 'σιμικίνθιον', '{\"def\":{\"short\":\"a semicinctium or half-girding, i.e., narrow covering (apron)\",\"long\":[\"a narrow apron, or linen covering, which workmen and servants were accustomed to wear\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"si.miˈkin.θi.on\",\"ipa_mod\":\"si.miˈkin̪.θi.own\",\"sbl\":\"simikinthion\",\"dic\":\"see-mee-KEEN-thee-one\",\"dic_mod\":\"see-mee-KEEN-thee-one\"}}', 'apron'),
(4512, 4613, 'Σίμων', '{\"def\":{\"lit\":\"a rock, stone\",\"short\":\"Simon (i.e., Shimon), the name of nine Israelites\",\"long\":[\"Peter was one of the apostles\",\"Simon called Zelotes or the Kanaites\",\"Simon, father of Judas who betrayed Jesus\",\"Simon Magus, the Samaritan wizard\",\"Simon the tanner, Acts 10\",\"Simon the Pharisee, Luke 7:40–44\",\"Simon of Cyrene who carried the cross of Christ\",\"Simon the cousin of Jesus, the son of Cleophas\",\"Simon the leper, so called to distinguish him from others of the same name\"]},\"deriv\":\"of Hebrew origin (H8095)\",\"pronun\":{\"ipa\":\"ˈsi.mon\",\"ipa_mod\":\"ˈsi.mown\",\"sbl\":\"simōn\",\"dic\":\"SEE-mone\",\"dic_mod\":\"SEE-mone\"},\"see\":[\"H8095\",\"G4826\"],\"comment\":\"Compare G4826.\"}', 'Simon'),
(4513, 4614, 'Σινᾶ', '{\"def\":{\"lit\":\"thorny\",\"short\":\"Sina (i.e., Sinai), a mountain in Arabia\",\"long\":[\"a mountain or rather a mountainous region in the peninsula of Arabia Petraea, made famous by the giving of the Mosaic law\"]},\"deriv\":\"of Hebrew origin (H5514)\",\"pronun\":{\"ipa\":\"siˈnɑ\",\"ipa_mod\":\"siˈnɑ\",\"sbl\":\"sina\",\"dic\":\"see-NA\",\"dic_mod\":\"see-NA\"},\"see\":[\"H5514\"]}', 'Sina'),
(4514, 4615, 'σίναπι', '{\"def\":{\"short\":\"mustard (the plant)\",\"long\":[\"mustard, the name of a plant which in Middle Eastern countries grows from a very small seed and attains to the height of a tree, 10 feet (3 m) and more; hence a very small quantity of a thing is likened to a mustard seed, and also a thing which grows to a remarkable size\"]},\"deriv\":\"perhaps from σίνομαι (to hurt, i.e. sting)\",\"pronun\":{\"ipa\":\"ˈsi.nɑ.pi\",\"ipa_mod\":\"ˈsi.nɑ.pi\",\"sbl\":\"sinapi\",\"dic\":\"SEE-na-pee\",\"dic_mod\":\"SEE-na-pee\"}}', 'mustard'),
(4515, 4616, 'σινδών', '{\"def\":{\"short\":\"byssos, i.e., bleached linen (the cloth or a garment of it)\",\"long\":[\"linen cloth, especially that which was fine and costly, in which the bodies of the dead were wrapped\",\"thing made of fine cloth\",[\"of a light and loose garment worn at night over a naked body\"]]},\"deriv\":\"of uncertain (perhaps foreign) origin\",\"pronun\":{\"ipa\":\"sinˈðon\",\"ipa_mod\":\"sin̪ˈðown\",\"sbl\":\"sindōn\",\"dic\":\"seen-THONE\",\"dic_mod\":\"seen-THONE\"}}', '(fine) linen (cloth)'),
(4516, 4617, 'σινιάζω', '{\"def\":{\"short\":\"to riddle (figuratively)\",\"long\":[\"to sift, shake in a sieve\",\"figuratively by inward agitation to try one\'s faith to the verge of overthrow\"]},\"deriv\":\"from σινίον (a sieve)\",\"pronun\":{\"ipa\":\"si.niˈɑ.zo\",\"ipa_mod\":\"si.niˈɑ.zow\",\"sbl\":\"siniazō\",\"dic\":\"see-nee-AH-zoh\",\"dic_mod\":\"see-nee-AH-zoh\"}}', 'sift'),
(4517, 4618, 'σιτευτός', '{\"def\":{\"short\":\"grain-fed, i.e., fattened\",\"long\":[\"fattened, fatted\"]},\"deriv\":\"from a derivative of G4621\",\"pronun\":{\"ipa\":\"si.tɛβˈtos\",\"ipa_mod\":\"si.tefˈtows\",\"sbl\":\"siteutos\",\"dic\":\"see-tev-TOSE\",\"dic_mod\":\"see-tayf-TOSE\"},\"see\":[\"G4621\"]}', 'fatted'),
(4518, 4619, 'σιτιστός', '{\"def\":{\"short\":\"grained, i.e., fatted\",\"long\":[\"fattened\"]},\"deriv\":\"from a derivative of G4621\",\"pronun\":{\"ipa\":\"si.tiˈstos\",\"ipa_mod\":\"si.tiˈstows\",\"sbl\":\"sitistos\",\"dic\":\"see-tee-STOSE\",\"dic_mod\":\"see-tee-STOSE\"},\"see\":[\"G4621\"]}', 'fatling'),
(4519, 4620, 'σιτόμετρον', '{\"def\":{\"short\":\"a grain-measure, i.e., (by implication) ration (allowance of food)\",\"long\":[\"a measured \'portion of\' grain or \'food\'\"]},\"deriv\":\"from G4621 and G3358\",\"pronun\":{\"ipa\":\"siˈto.mɛ.tron\",\"ipa_mod\":\"siˈtow.me̞.trown\",\"sbl\":\"sitometron\",\"dic\":\"see-TOH-meh-trone\",\"dic_mod\":\"see-TOH-may-trone\"},\"see\":[\"G3358\",\"G4621\"]}', 'portion of meat'),
(4520, 4621, 'σῖτος', '{\"def\":{\"short\":\"grain, especially wheat\",\"long\":[\"wheat, grain\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈsi.tos\",\"ipa_mod\":\"ˈsi.tows\",\"sbl\":\"sitos\",\"dic\":\"SEE-tose\",\"dic_mod\":\"SEE-tose\"}}', 'corn, wheat'),
(4521, 4622, 'Σιών', '{\"def\":{\"lit\":\"a parched place\",\"short\":\"Sion (i.e., Tsijon), a hill of Jerusalem; figuratively, the Church (militant or triumphant)\",\"long\":[\"the hill on which the higher and more ancient part of Jerusalem was built\",[\"the southwestern most and highest of the hills on which the city was built\"],\"often used of the entire city of Jerusalem\",\"since Jerusalem because the temple stood there, was called the dwelling place of God\"]},\"deriv\":\"of Hebrew origin (H6726)\",\"pronun\":{\"ipa\":\"siˈon\",\"ipa_mod\":\"siˈown\",\"sbl\":\"siōn\",\"dic\":\"see-ONE\",\"dic_mod\":\"see-ONE\"},\"see\":[\"H6726\"]}', 'Sion'),
(4522, 4623, 'σιωπάω', '{\"def\":{\"short\":\"to be dumb (but not deaf also, like 2974 properly); figuratively, to be calm (as quiet water)\",\"long\":[\"to be silent, hold one\'s peace\",[\"used of one\'s silence because dumb\"],\"metaphorically of a calm, quiet sea\"]},\"deriv\":\"from σιωπή (silence, i.e. a hush; properly, muteness, i.e. involuntary stillness, or inability to speak; and thus differing from G4602, which is rather a voluntary refusal or indisposition to speak, although the terms are often used synonymously)\",\"pronun\":{\"ipa\":\"si.oˈpɑ.o\",\"ipa_mod\":\"si.owˈpɑ.ow\",\"sbl\":\"siōpaō\",\"dic\":\"see-oh-PA-oh\",\"dic_mod\":\"see-oh-PA-oh\"},\"see\":[\"G4602\"]}', 'dumb, (hold) peace'),
(4523, 4624, 'σκανδαλίζω', '{\"def\":{\"short\":\"to entrap, i.e., trip up (figuratively, stumble (transitively) or entice to sin, apostasy or displeasure)\",\"long\":[\"to put a stumbling block or impediment in the way, upon which another may trip and fall, metaphorically to offend\",[\"to entice to sin\",\"to cause a person to begin to distrust and desert one whom he ought to trust and obey\",[\"to cause to fall away\",\"to be offended in one, i.e., to see in another what I disapprove of and what hinders me from acknowledging his authority\",\"to cause one to judge unfavorably or unjustly of another\"],\"since one who stumbles or whose foot gets entangled feels annoyed\",[\"to cause one displeasure at a thing\",\"to make indignant\",\"to be displeased, indignant\"]]]},\"deriv\":\"from G4625\",\"pronun\":{\"ipa\":\"skɑn.ðɑˈli.zo\",\"ipa_mod\":\"skɑn̪.ðɑˈli.zow\",\"sbl\":\"skandalizō\",\"dic\":\"skahn-tha-LEE-zoh\",\"dic_mod\":\"skahn-tha-LEE-zoh\"},\"see\":[\"G4625\"]}', '(make to) offend'),
(4524, 4625, 'σκάνδαλον', '{\"def\":{\"short\":\"a trap-stick (bent sapling), i.e., snare (figuratively, cause of displeasure or sin)\",\"long\":[\"the movable stick or trigger of a trap, a trap stick\",[\"a trap, snare\",\"any impediment placed in the way and causing one to stumble or fall, (a stumbling block, occasion of stumbling) i.e., a rock which is a cause of stumbling\",\"figuratively applied to Jesus Christ, whose person and career were so contrary to the expectations of the Jews concerning the Messiah, that they rejected him and by their obstinacy made shipwreck of their salvation\"],\"any person or thing by which one is (entrapped) drawn into error or sin\"]},\"deriv\":\"(\\\"scandal\\\"); probably from a derivative of G2578\",\"pronun\":{\"ipa\":\"ˈskɑn.ðɑ.lon\",\"ipa_mod\":\"ˈskɑn̪.ðɑ.lown\",\"sbl\":\"skandalon\",\"dic\":\"SKAHN-tha-lone\",\"dic_mod\":\"SKAHN-tha-lone\"},\"see\":[\"G2578\"]}', 'occasion to fall (of stumbling), offence, thing that offends, stumblingblock'),
(4525, 4626, 'σκάπτω', '{\"def\":{\"short\":\"to dig\",\"long\":[\"to dig\"]},\"deriv\":\"apparently a primary verb\",\"pronun\":{\"ipa\":\"ˈskɑ.pto\",\"ipa_mod\":\"ˈskɑ.ptow\",\"sbl\":\"skaptō\",\"dic\":\"SKA-ptoh\",\"dic_mod\":\"SKA-ptoh\"}}', 'dig'),
(4526, 4627, 'σκάφη', '{\"def\":{\"short\":\"a \\\"skiff\\\" (as if dug out), or yawl (carried aboard a large vessel for landing)\",\"long\":[\"anything dug out, hollow vessel, trough, tray, tub\",\"of a boat\"]},\"pronun\":{\"ipa\":\"ˈskɑ.fe\",\"ipa_mod\":\"ˈskɑ.fe̞\",\"sbl\":\"skaphē\",\"dic\":\"SKA-fay\",\"dic_mod\":\"SKA-fay\"}}', 'boat'),
(4527, 4628, 'σκέλος', '{\"def\":{\"short\":\"through the idea of leanness); the leg (as lank)\",\"long\":[\"the leg, from the hip to the toes inclusive\"]},\"deriv\":\"apparently from σκέλλω (to parch\",\"pronun\":{\"ipa\":\"ˈskɛ.los\",\"ipa_mod\":\"ˈske̞.lows\",\"sbl\":\"skelos\",\"dic\":\"SKEH-lose\",\"dic_mod\":\"SKAY-lose\"}}', 'leg'),
(4528, 4629, 'σκέπασμα', '{\"def\":{\"short\":\"clothing\",\"long\":[\"a covering, spec. clothing\"]},\"deriv\":\"from a derivative of σκέπας (a covering; perhaps akin to the base of G4649 through the idea of noticeableness)\",\"pronun\":{\"ipa\":\"ˈskɛ.pɑ.smɑ\",\"ipa_mod\":\"ˈske̞.pɑ.smɑ\",\"sbl\":\"skepasma\",\"dic\":\"SKEH-pa-sma\",\"dic_mod\":\"SKAY-pa-sma\"},\"see\":[\"G4649\"]}', 'raiment'),
(4529, 4630, 'Σκευᾶς', '{\"def\":{\"lit\":\"mind reader\",\"short\":\"left-handed; Scevas (i.e., Scaevus), an Israelite\",\"long\":[\"a certain chief priest residing at Ephesus\"]},\"deriv\":\"apparently of Latin origin\",\"pronun\":{\"ipa\":\"skɛβˈɑs\",\"ipa_mod\":\"scevˈɑs\",\"sbl\":\"skeuas\",\"dic\":\"skev-AS\",\"dic_mod\":\"skave-AS\"}}', 'Sceva'),
(4530, 4631, 'σκευή', '{\"def\":{\"short\":\"furniture, i.e., spare tackle\",\"long\":[\"any apparatus, equipment, or furniture\",[\"of the utensils or tackling of a ship\"]]},\"deriv\":\"from G4632\",\"pronun\":{\"ipa\":\"skɛβˈe\",\"ipa_mod\":\"scevˈe̞\",\"sbl\":\"skeuē\",\"dic\":\"skev-A\",\"dic_mod\":\"skave-A\"},\"see\":[\"G4632\"]}', 'tackling'),
(4531, 4632, 'σκεῦος', '{\"def\":{\"short\":\"a vessel, implement, equipment or apparatus (literally or figuratively (specially, a wife as contributing to the usefulness of the husband))\",\"long\":[\"a vessel\",\"an implement\",[\"in the plural\",[\"household utensils, domestic gear\",\"the tackle and armament of vessels, used specifically of sails and ropes\"]],\"metaphorically\",[\"a man of quality, a chosen instrument\",\"in a bad sense, an assistant in accomplishing an evil deed\"]]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ˈskɛβ.os\",\"ipa_mod\":\"ˈscev.ows\",\"sbl\":\"skeuos\",\"dic\":\"SKEV-ose\",\"dic_mod\":\"SKAVE-ose\"}}', 'goods, sail, stuff, vessel'),
(4532, 4633, 'σκηνή', '{\"def\":{\"short\":\"a tent or cloth hut (literally or figuratively)\",\"long\":[\"tent, tabernacle, (made of green boughs, or skins or other materials)\",\"of that well known movable temple of God after the pattern of which the temple at Jerusalem was built\"]},\"deriv\":\"apparently akin to G4632 and G4639\",\"pronun\":{\"ipa\":\"skeˈne\",\"ipa_mod\":\"ske̞ˈne̞\",\"sbl\":\"skēnē\",\"dic\":\"skay-NAY\",\"dic_mod\":\"skay-NAY\"},\"see\":[\"G4632\",\"G4639\"]}', 'habitation, tabernacle'),
(4533, 4634, 'σκηνοπηγία', '{\"def\":{\"short\":\"the Festival of Tabernacles (so called from the custom of erecting booths for temporary homes)\",\"long\":[\"the construction of a tabernacle or tabernacles\",\"the feast of tabernacles\"]},\"deriv\":\"from G4636 and G4078\",\"pronun\":{\"ipa\":\"ske.no.peˈɣi.ɑ\",\"ipa_mod\":\"ske̞.now.pe̞ˈɣi.ɑ\",\"sbl\":\"skēnopēgia\",\"dic\":\"skay-noh-pay-GEE-ah\",\"dic_mod\":\"skay-noh-pay-GEE-ah\"},\"see\":[\"G4078\",\"G4636\"],\"comment\":\"The feast of tabernacles was observed by the Jews yearly for seven days, beginning with the 15th of the month Tisri (approximately our October) partly to perpetuate the memory of the time when their ancestors after leaving Egypt dwelt in tents on their way through the Arabian desert and partly as a season of festivity and joy on the completion of the harvest and the vintage (the festival of ingatherings). In celebrating the festival the Jews were accustomed to construct booths of the leafy branches of trees (perhaps on the roofs or in the courts of their dwellings or in the streets and squares) and to adorn them with flowers and fruits of all kinds under which, throughout the period of the festival, they feasted and gave themselves up to rejoicing.\"}', 'tabernacles'),
(4534, 4635, 'σκηνοποιός', '{\"def\":{\"short\":\"a manufacturer of tents\",\"long\":[\"a tentmaker\",\"one that makes small portable tents, of leather or cloth of goat\'s hair or linen, for the use of travellers\"]},\"deriv\":\"from G4633 and G4160\",\"pronun\":{\"ipa\":\"ske.no.pyˈos\",\"ipa_mod\":\"ske̞.now.pyˈows\",\"sbl\":\"skēnopoios\",\"dic\":\"skay-noh-poo-OSE\",\"dic_mod\":\"skay-noh-poo-OSE\"},\"see\":[\"G4160\",\"G4633\"]}', 'tent-maker'),
(4535, 4636, 'σκῆνος', '{\"def\":{\"short\":\"a hut or temporary residence, i.e., (figuratively) the human body (as the abode of the spirit)\",\"long\":[\"a tabernacle, a tent\",\"metaphorically of the human body, in which the soul dwells as in a tent, and which is taken down at death\"]},\"deriv\":\"from G4633\",\"pronun\":{\"ipa\":\"ˈske.nos\",\"ipa_mod\":\"ˈske̞.nows\",\"sbl\":\"skēnos\",\"dic\":\"SKAY-nose\",\"dic_mod\":\"SKAY-nose\"},\"see\":[\"G4633\"]}', 'tabernacle'),
(4536, 4637, 'σκηνόω', '{\"def\":{\"short\":\"to tent or encamp, i.e., (figuratively) to occupy (as a mansion) or (specially), to reside (as God did in the Tabernacle of old, a symbol of protection and communion)\",\"long\":[\"to fix one\'s tabernacle, have one\'s tabernacle, abide (or live) in a tabernacle (or tent), tabernacle\",\"to dwell\"]},\"deriv\":\"from G4636\",\"pronun\":{\"ipa\":\"skeˈno.o\",\"ipa_mod\":\"ske̞ˈnow.ow\",\"sbl\":\"skēnoō\",\"dic\":\"skay-NOH-oh\",\"dic_mod\":\"skay-NOH-oh\"},\"see\":[\"G4636\"]}', 'dwell'),
(4537, 4638, 'σκήνωμα', '{\"def\":{\"short\":\"an encampment, i.e., (figuratively) the Temple (as God\'s residence), the body (as a tenement for the soul)\",\"long\":[\"a tent, a tabernacle\",[\"of the temple as God\'s habitation\",\"of the tabernacle of the covenant\",\"metaphorically of the human body as the dwelling of the soul\"]]},\"deriv\":\"from G4637\",\"pronun\":{\"ipa\":\"ˈske.no.mɑ\",\"ipa_mod\":\"ˈske̞.now.mɑ\",\"sbl\":\"skēnōma\",\"dic\":\"SKAY-noh-ma\",\"dic_mod\":\"SKAY-noh-ma\"},\"see\":[\"G4637\"]}', 'tabernacle'),
(4538, 4639, 'σκιά', '{\"def\":{\"short\":\"\\\"shade\\\" or a shadow (literally or figuratively (darkness of error or an adumbration))\",\"long\":[\"shadow\",[\"shade caused by the interception of light\",\"an image cast by an object and representing the form of that object\",\"a sketch, outline, adumbration\"]]},\"deriv\":\"apparently a primary word\",\"pronun\":{\"ipa\":\"skiˈɑ\",\"ipa_mod\":\"sciˈɑ\",\"sbl\":\"skia\",\"dic\":\"skee-AH\",\"dic_mod\":\"skee-AH\"}}', 'shadow'),
(4539, 4640, 'σκιρτάω', '{\"def\":{\"short\":\"to jump, i.e., sympathetically move (as the quickening of a fetus)\",\"long\":[\"to leap\"]},\"deriv\":\"akin to σκαίρω (to skip)\",\"pronun\":{\"ipa\":\"skirˈtɑ.o\",\"ipa_mod\":\"scirˈtɑ.ow\",\"sbl\":\"skirtaō\",\"dic\":\"skeer-TA-oh\",\"dic_mod\":\"skeer-TA-oh\"}}', 'leap (for joy)'),
(4540, 4641, 'σκληροκαρδία', '{\"def\":{\"short\":\"hard-heartedness, i.e., (specially), destitution of (spiritual) perception\",\"long\":[\"hardness of heart\"]},\"deriv\":\"feminine of a compound of G4642 and G2588\",\"pronun\":{\"ipa\":\"skle.ro.kɑrˈði.ɑ\",\"ipa_mod\":\"skle̞.row.kɑrˈði.ɑ\",\"sbl\":\"sklērokardia\",\"dic\":\"sklay-roh-kahr-THEE-ah\",\"dic_mod\":\"sklay-roh-kahr-THEE-ah\"},\"see\":[\"G2588\",\"G4642\"]}', 'hardness of heart'),
(4541, 4642, 'σκληρός', '{\"def\":{\"short\":\"dry, i.e., hard or tough (figuratively, harsh, severe)\",\"long\":[\"hard, harsh, rough, stiff\",[\"of men: metaphorically harsh, stern, hard\",\"of things: violent, rough, offensive, intolerable\"]]},\"deriv\":\"from the base of G4628\",\"pronun\":{\"ipa\":\"skleˈros\",\"ipa_mod\":\"skle̞ˈrows\",\"sbl\":\"sklēros\",\"dic\":\"sklay-ROSE\",\"dic_mod\":\"sklay-ROSE\"},\"see\":[\"G4628\"]}', 'fierce, hard'),
(4542, 4643, 'σκληρότης', '{\"def\":{\"short\":\"callousness, i.e., (figuratively) stubbornness\",\"long\":[\"hardness\",\"obstinacy, stubbornness\"]},\"deriv\":\"from G4642\",\"pronun\":{\"ipa\":\"skleˈro.tes\",\"ipa_mod\":\"skle̞ˈrow.te̞s\",\"sbl\":\"sklērotēs\",\"dic\":\"sklay-ROH-tase\",\"dic_mod\":\"sklay-ROH-tase\"},\"see\":[\"G4642\"]}', 'hardness'),
(4543, 4644, 'σκληροτράχηλος', '{\"def\":{\"short\":\"hardnaped, i.e., (figuratively) obstinate\",\"long\":[\"stiffnecked\",\"stubborn, headstrong, obstinate\"]},\"deriv\":\"from G4642 and G5137\",\"pronun\":{\"ipa\":\"skle.roˈtrɑ.xe.los\",\"ipa_mod\":\"skle̞.rowˈtrɑ.xe̞.lows\",\"sbl\":\"sklērotrachēlos\",\"dic\":\"sklay-roh-TRA-hay-lose\",\"dic_mod\":\"sklay-roh-TRA-hay-lose\"},\"see\":[\"G4642\",\"G5137\"]}', 'stiffnecked'),
(4544, 4645, 'σκληρύνω', '{\"def\":{\"short\":\"to indurate, i.e., (figuratively) render stubborn\",\"long\":[\"to make hard, harden\",\"metaphorically\",[\"to render obstinate, stubborn\",\"to be hardened\",\"to become obstinate or stubborn\"]]},\"deriv\":\"from G4642\",\"pronun\":{\"ipa\":\"skleˈry.no\",\"ipa_mod\":\"skle̞ˈrju.now\",\"sbl\":\"sklērynō\",\"dic\":\"sklay-ROO-noh\",\"dic_mod\":\"sklay-RYOO-noh\"},\"see\":[\"G4642\"]}', 'harden'),
(4545, 4646, 'σκολιός', '{\"def\":{\"short\":\"warped, i.e., winding; figuratively, perverse\",\"long\":[\"crooked, curved\",\"metaphorically\",[\"perverse, wicked\",\"unfair, surly, froward\"]]},\"deriv\":\"from the base of G4628\",\"pronun\":{\"ipa\":\"sko.liˈos\",\"ipa_mod\":\"skow.liˈows\",\"sbl\":\"skolios\",\"dic\":\"skoh-lee-OSE\",\"dic_mod\":\"skoh-lee-OSE\"},\"see\":[\"G4628\"]}', 'crooked, froward, untoward'),
(4546, 4647, 'σκόλοψ', '{\"def\":{\"short\":\"withered at the front, i.e., a point or prickle (figuratively, a bodily annoyance or disability)\",\"long\":[\"a pointed piece of wood, a pale, a stake\",\"a sharp stake, splinter\"]},\"deriv\":\"perhaps from the base of G4628 and G3700\",\"pronun\":{\"ipa\":\"ˈsko.lop͡s\",\"ipa_mod\":\"ˈskow.lowp͡s\",\"sbl\":\"skolops\",\"dic\":\"SKOH-lohps\",\"dic_mod\":\"SKOH-lohps\"},\"see\":[\"G3700\",\"G4628\"]}', 'thorn'),
(4547, 4648, 'σκοπέω', '{\"def\":{\"short\":\"to take aim at (spy), i.e., (figuratively) regard\",\"long\":[\"to look at, observe, contemplate\",\"to mark\",\"to fix one\'s eyes upon, direct one\'s attention to, any one\",\"to look to, take heed to thyself\"]},\"deriv\":\"from G4649\",\"pronun\":{\"ipa\":\"skoˈpɛ.o\",\"ipa_mod\":\"skowˈpe̞.ow\",\"sbl\":\"skopeō\",\"dic\":\"skoh-PEH-oh\",\"dic_mod\":\"skoh-PAY-oh\"},\"see\":[\"G3700\",\"G4649\"],\"comment\":\"Compare G3700.\"}', 'consider, take heed, look at (on), mark'),
(4548, 4649, 'σκοπός', '{\"def\":{\"short\":\"perhaps akin to G4626 through the idea of concealment; compare G4629); a watch (sentry or scout), i.e., (by implication) a goal\",\"long\":[\"an observer, a watchman\",\"the distant mark looked at, the goal or end one has in view\"]},\"deriv\":\"from σκέπτομαι (to peer about (\\\"skeptic\\\")\",\"pronun\":{\"ipa\":\"skoˈpos\",\"ipa_mod\":\"skowˈpows\",\"sbl\":\"skopos\",\"dic\":\"skoh-POSE\",\"dic_mod\":\"skoh-POSE\"},\"see\":[\"G4626\",\"G4629\"]}', 'mark'),
(4549, 4650, 'σκορπίζω', '{\"def\":{\"short\":\"to dissipate, i.e., (figuratively) put to flight, waste, be liberal\",\"long\":[\"to scatter\",[\"of those who, routed or terror stricken or driven by some other impulses, fly in every direction\",\"to scatter abroad (what others may collect for themselves), or one dispensing blessings literally\"]]},\"deriv\":\"apparently from the same as G4651 (through the idea of penetrating)\",\"pronun\":{\"ipa\":\"skorˈpi.zo\",\"ipa_mod\":\"skowrˈpi.zow\",\"sbl\":\"skorpizō\",\"dic\":\"skore-PEE-zoh\",\"dic_mod\":\"skore-PEE-zoh\"},\"see\":[\"G4651\"]}', 'disperse abroad, scatter (abroad)'),
(4550, 4651, 'σκορπίος', '{\"def\":{\"short\":\"a \\\"scorpion\\\" (from its sting)\",\"long\":[\"a scorpion, the name of a little animal, somewhat resembling a lobster, which in warm regions lurk, especially in stone walls; it has a poisonous sting in its tail\"]},\"deriv\":\"probably from an obsolete σκέρπω (perhaps strengthened from the base of G4649 and meaning to pierce)\",\"pronun\":{\"ipa\":\"skorˈpi.os\",\"ipa_mod\":\"skowrˈpi.ows\",\"sbl\":\"skorpios\",\"dic\":\"skore-PEE-ose\",\"dic_mod\":\"skore-PEE-ose\"},\"see\":[\"G4649\"]}', 'scorpion'),
(4551, 4652, 'σκοτεινός', '{\"def\":{\"short\":\"opaque, i.e., (figuratively) benighted\",\"long\":[\"full of darkness, covered with darkness\"]},\"deriv\":\"from G4655\",\"pronun\":{\"ipa\":\"sko.tiˈnos\",\"ipa_mod\":\"skow.tiˈnows\",\"sbl\":\"skoteinos\",\"dic\":\"skoh-tee-NOSE\",\"dic_mod\":\"skoh-tee-NOSE\"},\"see\":[\"G4655\"]}', 'dark, full of darkness'),
(4552, 4653, 'σκοτία', '{\"def\":{\"short\":\"dimness, obscurity (literally or figuratively)\",\"long\":[\"darkness\",\"the darkness due to want of light\",\"metaphorically used of ignorance of divine things, and its associated wickedness, and the resultant misery in hell\"]},\"deriv\":\"from G4655\",\"pronun\":{\"ipa\":\"skoˈti.ɑ\",\"ipa_mod\":\"skowˈti.ɑ\",\"sbl\":\"skotia\",\"dic\":\"skoh-TEE-ah\",\"dic_mod\":\"skoh-TEE-ah\"},\"see\":[\"G4655\"]}', 'dark(-ness)'),
(4553, 4654, 'σκοτίζω', '{\"def\":{\"short\":\"to obscure (literally or figuratively)\",\"long\":[\"to cover with darkness, to darken\",\"to be covered with darkness, be darkened\",[\"of heavenly bodies as deprived of light\",\"metaphorically\",[\"of the eyes\",\"of the understanding\",\"of the mind\"]]]},\"deriv\":\"from G4655\",\"pronun\":{\"ipa\":\"skoˈti.zo\",\"ipa_mod\":\"skowˈti.zow\",\"sbl\":\"skotizō\",\"dic\":\"skoh-TEE-zoh\",\"dic_mod\":\"skoh-TEE-zoh\"},\"see\":[\"G4655\"]}', 'darken'),
(4554, 4655, 'σκότος', '{\"def\":{\"short\":\"shadiness, i.e., obscurity (literally or figuratively)\",\"long\":[\"darkness\",[\"of night darkness\",\"of darkened eyesight or blindness\"],\"metaphorically\",[\"of ignorance respecting divine things and human duties, and the accompanying ungodliness and immorality, together with their consequent misery in hell\",\"persons in whom darkness becomes visible and holds sway\"]]},\"deriv\":\"from the base of G4639\",\"pronun\":{\"ipa\":\"ˈsko.tos\",\"ipa_mod\":\"ˈskow.tows\",\"sbl\":\"skotos\",\"dic\":\"SKOH-tose\",\"dic_mod\":\"SKOH-tose\"},\"see\":[\"G4639\"]}', 'darkness'),
(4555, 4656, 'σκοτόω', '{\"def\":{\"short\":\"to obscure or blind (literally or figuratively)\",\"long\":[\"to darken, cover with darkness\",\"metaphorically to darken or blind the mind\"]},\"deriv\":\"from G4655\",\"pronun\":{\"ipa\":\"skoˈto.o\",\"ipa_mod\":\"skowˈtow.ow\",\"sbl\":\"skotoō\",\"dic\":\"skoh-TOH-oh\",\"dic_mod\":\"skoh-TOH-oh\"},\"see\":[\"G4655\"]}', 'be full of darkness'),
(4556, 4657, 'σκύβαλον', '{\"def\":{\"short\":\"what is thrown to the dogs, i.e., refuse (ordure)\",\"long\":[\"any refuse, as the excrement of animals, offscourings, rubbish, dregs\",[\"of things worthless and detestable\"]]},\"deriv\":\"neuter of a presumed derivative of G1519 and G2965 and G0906\",\"pronun\":{\"ipa\":\"ˈsky.βɑ.lon\",\"ipa_mod\":\"ˈskju.vɑ.lown\",\"sbl\":\"skybalon\",\"dic\":\"SKOO-va-lone\",\"dic_mod\":\"SKYOO-va-lone\"},\"see\":[\"G0906\",\"G1519\",\"G2965\"]}', 'dung'),
(4557, 4658, 'Σκύθης', '{\"def\":{\"lit\":\"rude, rough\",\"short\":\"a Scythene or Scythian, i.e., (by implication) a savage\",\"long\":[\"a Scythian, an inhabitant of Scythia or modern day Russia\",[\"by the more civilised nations of antiquity the Scythians were regarded as the wildest of barbarians\"]]},\"deriv\":\"probably of foreign origin\",\"pronun\":{\"ipa\":\"ˈsky.θes\",\"ipa_mod\":\"ˈskju.θe̞s\",\"sbl\":\"skythēs\",\"dic\":\"SKOO-thase\",\"dic_mod\":\"SKYOO-thase\"}}', 'Scythian'),
(4558, 4659, 'σκυθρωπός', '{\"def\":{\"short\":\"angry-visaged, i.e., gloomy or affecting a mournful appearance\",\"long\":[\"of a sad and gloomy countenance\"]},\"deriv\":\"from σκυθρός (sullen) and a derivative of G3700\",\"pronun\":{\"ipa\":\"sky.θroˈpos\",\"ipa_mod\":\"scju.θrowˈpows\",\"sbl\":\"skythrōpos\",\"dic\":\"skoo-throh-POSE\",\"dic_mod\":\"skyoo-throh-POSE\"},\"see\":[\"G3700\"]}', 'of a sad countenance'),
(4559, 4660, 'σκύλλω', '{\"def\":{\"short\":\"to flay, i.e., (figuratively) to harass\",\"long\":[\"to skin, flay\",\"to rend, mangle\",[\"to vex, trouble, annoy\",\"to give one\'s self trouble, trouble one\'s self\"]]},\"deriv\":\"apparently a primary verb\",\"pronun\":{\"ipa\":\"ˈskyl.lo\",\"ipa_mod\":\"ˈskjul.low\",\"sbl\":\"skyllō\",\"dic\":\"SKOOL-loh\",\"dic_mod\":\"SKYOOL-loh\"}}', 'trouble(self)');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(4560, 4661, 'σκῦλον', '{\"def\":{\"short\":\"something stripped (as a hide), i.e., booty\",\"long\":[\"a (beast\'s) skin stripped off, a pelt\",\"the weapons and valuables stripped off from an enemy, spoils\"]},\"deriv\":\"neuter from G4660\",\"pronun\":{\"ipa\":\"ˈsky.lon\",\"ipa_mod\":\"ˈskju.lown\",\"sbl\":\"skylon\",\"dic\":\"SKOO-lone\",\"dic_mod\":\"SKYOO-lone\"},\"see\":[\"G4660\"]}', 'spoil'),
(4561, 4662, 'σκωληκόβρωτος', '{\"def\":{\"short\":\"worm-eaten, i.e., diseased with maggots\",\"long\":[\"eaten of worms\"]},\"deriv\":\"from G4663 and a derivative of G0977\",\"pronun\":{\"ipa\":\"sko.leˈko.βro.tos\",\"ipa_mod\":\"skow.le̞ˈkow.vrow.tows\",\"sbl\":\"skōlēkobrōtos\",\"dic\":\"skoh-lay-KOH-vroh-tose\",\"dic_mod\":\"skoh-lay-KOH-vroh-tose\"},\"see\":[\"G0977\",\"G4663\"]}', 'eaten of worms'),
(4562, 4663, 'σκώληξ', '{\"def\":{\"short\":\"a grub, maggot or earth-worm\",\"long\":[\"a worm, spec. that kind which preys upon dead bodies\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈsko.lek͡s\",\"ipa_mod\":\"ˈskow.le̞k͡s\",\"sbl\":\"skōlēx\",\"dic\":\"SKOH-layks\",\"dic_mod\":\"SKOH-layks\"}}', 'worm'),
(4563, 4664, 'σμαράγδινος', '{\"def\":{\"short\":\"consisting of emerald\",\"long\":[\"of emerald, made of emerald\"]},\"deriv\":\"from G4665\",\"pronun\":{\"ipa\":\"smɑˈrɑ.ɣði.nos\",\"ipa_mod\":\"smɑˈrɑ.ɣði.nows\",\"sbl\":\"smaragdinos\",\"dic\":\"sma-RA-gthee-nose\",\"dic_mod\":\"sma-RA-gthee-nose\"},\"see\":[\"G4665\"]}', 'emerald'),
(4564, 4665, 'σμάραγδος', '{\"def\":{\"short\":\"the emerald or green gem so called\",\"long\":[\"a transparent precious stone noted especially for its light green color\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈsmɑ.rɑ.ɣðos\",\"ipa_mod\":\"ˈsmɑ.rɑ.ɣðows\",\"sbl\":\"smaragdos\",\"dic\":\"SMA-ra-gthose\",\"dic_mod\":\"SMA-ra-gthose\"}}', 'emerald'),
(4565, 4666, 'σμύρνα', '{\"def\":{\"short\":\"myrrh\",\"long\":[\"myrrh, a bitter gum and costly perfume which exudes from a certain tree or shrub in Arabia and Ethiopia, or is obtained by incisions made in the bark: as an antiseptic it was used for embalming\"]},\"deriv\":\"apparently strengthened for G3464\",\"pronun\":{\"ipa\":\"ˈsmyr.nɑ\",\"ipa_mod\":\"ˈsmjur.nɑ\",\"sbl\":\"smyrna\",\"dic\":\"SMOOR-na\",\"dic_mod\":\"SMYOOR-na\"},\"see\":[\"G3464\"]}', 'myrrh'),
(4566, 4667, 'Σμύρνα', '{\"def\":{\"lit\":\"myrrh\",\"short\":\"Smyrna, a place in Asia Minor\",\"long\":[\"an Ionian city of Asia Minor, on the Aegean Sea, 40 miles (65 km) north of Ephesus\"]},\"deriv\":\"the same as G4666\",\"pronun\":{\"ipa\":\"ˈsmyr.nɑ\",\"ipa_mod\":\"ˈsmjur.nɑ\",\"sbl\":\"smyrna\",\"dic\":\"SMOOR-na\",\"dic_mod\":\"SMYOOR-na\"},\"see\":[\"G4666\"]}', 'Smyrna'),
(4567, 4668, 'Σμυρναῖος', '{\"def\":{\"short\":\"a Smyrnaean\",\"long\":[\"of or belonging to Smyrna, an inhabitant of Smyrna\"]},\"deriv\":\"from G4667\",\"pronun\":{\"ipa\":\"smyrˈnɛ.os\",\"ipa_mod\":\"smjurˈne.ows\",\"sbl\":\"smyrnaios\",\"dic\":\"smoor-NEH-ose\",\"dic_mod\":\"smyoor-NAY-ose\"},\"see\":[\"G4667\"]}', 'in Smyrna'),
(4568, 4669, 'σμυρνίζω', '{\"def\":{\"short\":\"to tincture with myrrh, i.e., embitter (as a narcotic)\",\"long\":[\"to be like myrrh\",\"to mix and so flavor with myrrh\",[\"wine with myrrh: i.e., wine flavored with myrrh\"]]},\"deriv\":\"from G4667\",\"pronun\":{\"ipa\":\"smyrˈni.zo\",\"ipa_mod\":\"smjurˈni.zow\",\"sbl\":\"smyrnizō\",\"dic\":\"smoor-NEE-zoh\",\"dic_mod\":\"smyoor-NEE-zoh\"},\"see\":[\"G4667\"],\"comment\":\"The ancients used to infuse myrrh into wine to give it a more agreeable fragrance and flavor.\"}', 'mingle with myrrh'),
(4569, 4670, 'Σόδομα', '{\"def\":{\"lit\":\"burning\",\"short\":\"Sodoma (i.e., Sedom), a place in Palestine\",\"long\":[\"a city destroyed by the Lord raining fire and brimstone on it\",\"metaphorically Jerusalem Revelation 11:8\"]},\"deriv\":\"plural of Hebrew origin (H5467)\",\"pronun\":{\"ipa\":\"ˈso.ðo.mɑ\",\"ipa_mod\":\"ˈsow.ðow.mɑ\",\"sbl\":\"sodoma\",\"dic\":\"SOH-thoh-ma\",\"dic_mod\":\"SOH-thoh-ma\"},\"see\":[\"H5467\"]}', 'Sodom'),
(4570, 4671, 'σοί', '{\"def\":{\"short\":\"to thee\",\"long\":[\"to you\"]},\"deriv\":\"dative case of G4771\",\"pronun\":{\"ipa\":\"sy\",\"ipa_mod\":\"sy\",\"sbl\":\"soi\",\"dic\":\"soo\",\"dic_mod\":\"soo\"},\"see\":[\"G4771\"]}', 'thee, thine own, thou, thy'),
(4571, 4672, 'Σολομών', '{\"def\":{\"lit\":\"peaceful\",\"short\":\"Solomon (i.e., Shelomoh), the son of David\",\"long\":[\"the son of David and was the wisest and richest king that ever lived\"]},\"deriv\":\"of Hebrew origin (H8010)\",\"pronun\":{\"ipa\":\"so.loˈmon\",\"ipa_mod\":\"sow.lowˈmown\",\"sbl\":\"solomōn\",\"dic\":\"soh-loh-MONE\",\"dic_mod\":\"soh-loh-MONE\"},\"see\":[\"H8010\"]}', 'Solomon'),
(4572, 4673, 'σορός', '{\"def\":{\"short\":\"a funereal receptacle (urn, coffin), i.e., (by analogy) a bier\",\"long\":[\"an urn or receptacle for keeping the bones of the dead\",\"the funeral couch or bier on which the Jews carried their dead forth to burial\"]},\"deriv\":\"probably akin to the base of G4987\",\"pronun\":{\"ipa\":\"soˈros\",\"ipa_mod\":\"sowˈrows\",\"sbl\":\"soros\",\"dic\":\"soh-ROSE\",\"dic_mod\":\"soh-ROSE\"},\"see\":[\"G4987\"]}', 'bier'),
(4573, 4674, 'σός', '{\"def\":{\"short\":\"thine\",\"long\":[\"thy, thine\"]},\"deriv\":\"from G4771\",\"pronun\":{\"ipa\":\"sos\",\"ipa_mod\":\"sows\",\"sbl\":\"sos\",\"dic\":\"sose\",\"dic_mod\":\"sose\"},\"see\":[\"G4771\"]}', 'thine (own), thy (friend)'),
(4574, 4675, 'σοῦ', '{\"def\":{\"short\":\"of thee, thy\",\"long\":[\"thy, thee\"]},\"deriv\":\"genitive case of G4771\",\"pronun\":{\"ipa\":\"su\",\"ipa_mod\":\"su\",\"sbl\":\"sou\",\"dic\":\"soo\",\"dic_mod\":\"soo\"},\"see\":[\"G4771\"]}', 'X home, thee, thine (own), thou, thy'),
(4575, 4676, 'σουδάριον', '{\"def\":{\"short\":\"a sudarium (sweat-cloth), i.e., towel (for wiping the perspiration from the face, or binding the face of a corpse)\",\"long\":[\"a handkerchief\",\"a cloth for wiping perspiration from the face and for cleaning the nose and also used in swathing the head of a corpse\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"suˈðɑ.ri.on\",\"ipa_mod\":\"suˈðɑ.ri.own\",\"sbl\":\"soudarion\",\"dic\":\"soo-THA-ree-one\",\"dic_mod\":\"soo-THA-ree-one\"}}', 'handkerchief, napkin'),
(4576, 4677, 'Σουσάννα', '{\"def\":{\"lit\":\"a lily\",\"short\":\"lily; Susannah (i.e., Shoshannah), an Israelitess\",\"long\":[\"one of the women who ministered to Christ\"]},\"deriv\":\"of Hebrew origin (H7799 feminine)\",\"pronun\":{\"ipa\":\"suˈsɑn.nɑ\",\"ipa_mod\":\"suˈsɑn.nɑ\",\"sbl\":\"sousanna\",\"dic\":\"soo-SAHN-na\",\"dic_mod\":\"soo-SAHN-na\"},\"see\":[\"H7799\"]}', 'Susanna'),
(4577, 4678, 'σοφία', '{\"def\":{\"short\":\"wisdom (higher or lower, worldly or spiritual)\",\"long\":[\"wisdom, broad and full of intelligence; used of the knowledge of very diverse matters\",[\"the wisdom which belongs to men\",[\"specifically the varied knowledge of things human and divine, acquired by acuteness and experience, and summed up in maxims and proverbs\",\"the science and learning\",\"the act of interpreting dreams and always giving the sagest advice\",\"the intelligence evinced in discovering the meaning of some mysterious number or vision\",\"skill in the management of affairs\",\"devout and proper prudence in intercourse with men not disciples of Christ, skill and discretion in imparting Christian truth\",\"the knowledge and practice of the requisites for godly and upright living\"],\"supreme intelligence, such as belongs to God\",[\"to Christ\",\"the wisdom of God as evinced in forming and executing counsels in the formation and government of the world and the scriptures\"]]]},\"deriv\":\"from G4680\",\"pronun\":{\"ipa\":\"soˈfi.ɑ\",\"ipa_mod\":\"sowˈfi.ɑ\",\"sbl\":\"sophia\",\"dic\":\"soh-FEE-ah\",\"dic_mod\":\"soh-FEE-ah\"},\"see\":[\"G4680\"]}', 'wisdom'),
(4578, 4679, 'σοφίζω', '{\"def\":{\"short\":\"to render wise; in a sinister acceptation, to form \\\"sophisms\\\", i.e., continue plausible error\",\"long\":[\"to make wise, teach\",\"to become wise, to have understanding\",[\"to invent, play the sophist\",\"to devise cleverly or cunningly\"]]},\"deriv\":\"from G4680\",\"pronun\":{\"ipa\":\"soˈfi.zo\",\"ipa_mod\":\"sowˈfi.zow\",\"sbl\":\"sophizō\",\"dic\":\"soh-FEE-zoh\",\"dic_mod\":\"soh-FEE-zoh\"},\"see\":[\"G4680\"]}', 'cunningly devised, make wise'),
(4579, 4680, 'σοφός', '{\"def\":{\"short\":\"wise (in a most general application)\",\"long\":[\"wise\",[\"skilled, expert: of artificers\",\"wise, skilled in letters, cultivated, learned\",[\"of the Greek philosophers and orators\",\"of Jewish theologians\",\"of Christian teachers\"],\"forming the best plans and using the best means for their execution\"]]},\"deriv\":\"akin to σαφής (clear)\",\"pronun\":{\"ipa\":\"soˈfos\",\"ipa_mod\":\"sowˈfows\",\"sbl\":\"sophos\",\"dic\":\"soh-FOSE\",\"dic_mod\":\"soh-FOSE\"},\"see\":[\"G5429\"],\"comment\":\"Compare G5429.\"}', 'wise'),
(4580, 4681, 'Σπανία', '{\"def\":{\"lit\":\"scarceness\",\"short\":\"Spania, a region of Europe\",\"long\":[\"the whole peninsula south of the Pyrenees\"]},\"deriv\":\"probably of foreign origin\",\"pronun\":{\"ipa\":\"spɑˈni.ɑ\",\"ipa_mod\":\"spɑˈni.ɑ\",\"sbl\":\"spania\",\"dic\":\"spa-NEE-ah\",\"dic_mod\":\"spa-NEE-ah\"}}', 'Spain'),
(4581, 4682, 'σπαράσσω', '{\"def\":{\"short\":\"apparently strengthened from G4685 through the idea of spasmodic contraction); to mangle, i.e., convluse with epilepsy\",\"long\":[\"to convulse, tear\"]},\"deriv\":\"prolongation from σπαίρω (to grasp\",\"pronun\":{\"ipa\":\"spɑˈrɑs.so\",\"ipa_mod\":\"spɑˈrɑs.sow\",\"sbl\":\"sparassō\",\"dic\":\"spa-RAHS-soh\",\"dic_mod\":\"spa-RAHS-soh\"},\"see\":[\"G4685\"]}', 'rend, tear'),
(4582, 4683, 'σπαργανόω', '{\"def\":{\"short\":\"to wrap tightly in cloths (especially a new born baby)\",\"long\":[\"to swathe (an infant after the Middle Eastern custom), to wrap in swaddling clothes (especially an infant just born)\"]},\"deriv\":\"from σπάργανον (a strip; from a derivative of the base of G4682 meaning to strap or wrap with strips)\",\"pronun\":{\"ipa\":\"spɑr.ɣɑˈno.o\",\"ipa_mod\":\"spɑr.ɣɑˈnow.ow\",\"sbl\":\"sparganoō\",\"dic\":\"spahr-ga-NOH-oh\",\"dic_mod\":\"spahr-ga-NOH-oh\"},\"see\":[\"G4682\"]}', 'wrap in swaddling clothes'),
(4583, 4684, 'σπαταλάω', '{\"def\":{\"short\":\"to be voluptuous\",\"long\":[\"to live luxuriously, lead a voluptuous life, (give one\'s self to pleasure)\"]},\"deriv\":\"from σπατάλη (luxury)\",\"pronun\":{\"ipa\":\"spɑ.tɑˈlɑ.o\",\"ipa_mod\":\"spɑ.tɑˈlɑ.ow\",\"sbl\":\"spatalaō\",\"dic\":\"spa-ta-LA-oh\",\"dic_mod\":\"spa-ta-LA-oh\"}}', 'live in pleasure, be wanton'),
(4584, 4685, 'σπάω', '{\"def\":{\"short\":\"to draw\",\"long\":[\"to draw\",\"to draw (one\'s sword)\"]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈspɑ.o\",\"ipa_mod\":\"ˈspɑ.ow\",\"sbl\":\"spaō\",\"dic\":\"SPA-oh\",\"dic_mod\":\"SPA-oh\"}}', 'draw (out)'),
(4585, 4686, 'σπεῖρα', '{\"def\":{\"short\":\"a coil (spira, \\\"spire\\\"), i.e., (figuratively) a mass of men (a Roman military cohort; also (by analogy) a squad of Levitical janitors)\",\"long\":[\"anything rolled into a circle or ball, anything wound, rolled up, folded together\",\"a military cohort\",[\"the tenth part of legion\",[\"about 600 men i.e., legionaries\",\"if auxiliaries either 500 or 1000\",\"a maniple, or the thirtieth part of a legion\"],\"any band, company, or detachment, of soldiers\"]]},\"deriv\":\"of immediate Latin origin, but ultimately a derivative of G0138 in the sense of its cognate G1507\",\"pronun\":{\"ipa\":\"ˈspi.rɑ\",\"ipa_mod\":\"ˈspi.rɑ\",\"sbl\":\"speira\",\"dic\":\"SPEE-ra\",\"dic_mod\":\"SPEE-ra\"},\"see\":[\"G0138\",\"G1507\"]}', 'band'),
(4586, 4687, 'σπείρω', '{\"def\":{\"short\":\"to scatter, i.e., sow (literally or figuratively)\",\"long\":[\"to sow, scatter, seed\",\"metaphorically of proverbial sayings\"]},\"deriv\":\"probably strengthened from G4685 (through the idea of extending)\",\"pronun\":{\"ipa\":\"ˈspi.ro\",\"ipa_mod\":\"ˈspi.row\",\"sbl\":\"speirō\",\"dic\":\"SPEE-roh\",\"dic_mod\":\"SPEE-roh\"},\"see\":[\"G4685\"]}', 'sow(-er), receive seed'),
(4587, 4688, 'σπεκουλάτωρ', '{\"def\":{\"short\":\"a speculator, i.e., military scout (spy or (by extension) life-guardsman)\",\"long\":[\"a spy, scout\",\"under the emperors an attendant and member of the body guard, employed as messengers, watchers, and executioners\",\"the name is transferred to an attendant of Herod Antipas that acted as executioner\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"spɛ.kuˈlɑ.tor\",\"ipa_mod\":\"spe̞.kuˈlɑ.towr\",\"sbl\":\"spekoulatōr\",\"dic\":\"speh-koo-LA-tore\",\"dic_mod\":\"spay-koo-LA-tore\"}}', 'executioner'),
(4588, 4689, 'σπένδω', '{\"def\":{\"short\":\"to pour out as a libation, i.e., (figuratively) to devote (one\'s life or blood, as a sacrifice) (\\\"spend\\\")\",\"long\":[\"to pour out as a drink offering, make a libation\",\"in the New Testament to be offered as a libation\",\"figuratively used of one whose blood is poured out in a violent death for the cause of God\"]},\"deriv\":\"apparently a primary verb\",\"pronun\":{\"ipa\":\"ˈspɛn.ðo\",\"ipa_mod\":\"ˈspe̞n̪.ðow\",\"sbl\":\"spendō\",\"dic\":\"SPEN-thoh\",\"dic_mod\":\"SPANE-thoh\"}}', '(be ready to) be offered'),
(4589, 4690, 'σπέρμα', '{\"def\":{\"short\":\"something sown, i.e., seed (including the male \\\"sperm\\\"); by implication, offspring; specially, a remnant (figuratively, as if kept over for planting)\",\"long\":[\"from which a plant germinates\",[\"the seed i.e., the grain or kernel which contains within itself the germ of the future plants\",[\"of the grains or kernels sown\"],\"metaphorically a seed i.e., a residue, or a few survivors reserved as the germ of a new race (just as seed is kept from the harvest for the sowing)\"],\"the semen virile\",[\"the product of this semen, seed, children, offspring, progeny\",\"family, race, posterity\",\"whatever possesses vital force or life giving power\",[\"of divine energy of the Holy Spirit operating within the soul by which we are regenerated\"]]]},\"deriv\":\"from G4687\",\"pronun\":{\"ipa\":\"ˈspɛr.mɑ\",\"ipa_mod\":\"ˈspe̞r.mɑ\",\"sbl\":\"sperma\",\"dic\":\"SPER-ma\",\"dic_mod\":\"SPARE-ma\"},\"see\":[\"G4687\"]}', 'issue, seed'),
(4590, 4691, 'σπερμολόγος', '{\"def\":{\"short\":\"a seed-picker (as the crow), i.e., (figuratively) a sponger, loafer (specially, a gossip or trifler in talk)\",\"long\":[\"picking up seed\",[\"of birds, especially of the crow or daw that picks up grain in fields\"],\"metaphorically\",[\"lounging about the market place and picking up a substance by whatever may chance to fall from the loads of merchandise\",\"hence, beggarly, abject, vile, (a parasite)\",\"getting a living by flattery and bufferoonery\",\"an empty talker, babbler\"]]},\"deriv\":\"from G4690 and G3004\",\"pronun\":{\"ipa\":\"spɛr.moˈlo.ɣos\",\"ipa_mod\":\"spe̞r.mowˈlow.ɣows\",\"sbl\":\"spermologos\",\"dic\":\"sper-moh-LOH-gose\",\"dic_mod\":\"spare-moh-LOH-gose\"},\"see\":[\"G3004\",\"G4690\"]}', 'babbler'),
(4591, 4692, 'σπεύδω', '{\"def\":{\"short\":\"to \\\"speed\\\" (\\\"study\\\"), i.e., urge on (diligently or earnestly); by implication, to await eagerly\",\"long\":[\"to haste, make haste\",\"to desire earnestly\"]},\"deriv\":\"probably strengthened from G4228\",\"pronun\":{\"ipa\":\"ˈspɛβ.ðo\",\"ipa_mod\":\"ˈspev.ðow\",\"sbl\":\"speudō\",\"dic\":\"SPEV-thoh\",\"dic_mod\":\"SPAVE-thoh\"},\"see\":[\"G4228\"]}', '(make, with) haste unto'),
(4592, 4693, 'σπήλαιον', '{\"def\":{\"short\":\"a cavern; by implication, a hiding-place or resort\",\"long\":[\"a cave, den\"]},\"deriv\":\"neuter of a presumed derivative of σπέος (a grotto)\",\"pronun\":{\"ipa\":\"ˈspe.lɛ.on\",\"ipa_mod\":\"ˈspe̞.le.own\",\"sbl\":\"spēlaion\",\"dic\":\"SPAY-leh-one\",\"dic_mod\":\"SPAY-lay-one\"}}', 'cave, den'),
(4593, 4694, 'σπιλάς', '{\"def\":{\"short\":\"a ledge or reef of rock in the sea\",\"long\":[\"a rock in the sea, ledge, reef\",\"metaphorically of men who by their conduct damage others morally, wreck them as it were\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"spiˈlɑs\",\"ipa_mod\":\"spiˈlɑs\",\"sbl\":\"spilas\",\"dic\":\"spee-LAHS\",\"dic_mod\":\"spee-LAHS\"},\"see\":[\"G4696\"]}', 'spot (by confusion with G4696)'),
(4594, 4695, 'σπιλόω', '{\"def\":{\"short\":\"to stain or soil (literally or figuratively)\",\"long\":[\"to defile, spot\"]},\"deriv\":\"from G4696\",\"pronun\":{\"ipa\":\"spiˈlo.o\",\"ipa_mod\":\"spiˈlow.ow\",\"sbl\":\"spiloō\",\"dic\":\"spee-LOH-oh\",\"dic_mod\":\"spee-LOH-oh\"},\"see\":[\"G4696\"]}', 'defile, spot'),
(4595, 4696, 'σπίλος', '{\"def\":{\"short\":\"a stain or blemish, i.e., (figuratively) defect, disgrace\",\"long\":[\"a spot\",\"a fault, moral blemish\",[\"of base and gluttonous men\"]]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈspi.los\",\"ipa_mod\":\"ˈspi.lows\",\"sbl\":\"spilos\",\"dic\":\"SPEE-lose\",\"dic_mod\":\"SPEE-lose\"}}', 'spot'),
(4596, 4697, 'σπλαγχνίζομαι', '{\"def\":{\"short\":\"to have the bowels yearn, i.e., (figuratively) feel sympathy, to pity\",\"long\":[\"to be moved as to one\'s bowels, hence to be moved with compassion, have compassion (for the bowels were thought to be the seat of love and pity)\"]},\"deriv\":\"middle voice from G4698\",\"pronun\":{\"ipa\":\"splɑŋˈxni.zo.mɛ\",\"ipa_mod\":\"splɑŋˈxni.zow.me\",\"sbl\":\"splanchnizomai\",\"dic\":\"splahng-HNEE-zoh-meh\",\"dic_mod\":\"splahng-HNEE-zoh-may\"},\"see\":[\"G4698\"]}', 'have (be moved with) compassion'),
(4597, 4698, 'σπλάγχνον', '{\"def\":{\"short\":\"an intestine (plural); figuratively, pity or sympathy\",\"long\":[\"bowels, intestines, (the heart, lungs, liver, etc.)\",[\"bowels\",\"the bowels were regarded as the seat of the more violent passions, such as anger and love; but by the Hebrews as the seat of the tenderer affections, especially kindness, benevolence, compassion; hence our heart (tender mercies, affections, etc.)\",\"a heart in which mercy resides\"]]},\"deriv\":\"probably strengthened from σπλήν (the \\\"spleen\\\")\",\"pronun\":{\"ipa\":\"ˈsplɑŋ.xnon\",\"ipa_mod\":\"ˈsplɑŋ.xnown\",\"sbl\":\"splanchnon\",\"dic\":\"SPLAHNG-hnone\",\"dic_mod\":\"SPLAHNG-hnone\"}}', 'bowels, inward affection, + tender mercy'),
(4598, 4699, 'σπόγγος', '{\"def\":{\"short\":\"a \\\"sponge\\\"\",\"long\":[\"sponge\"]},\"deriv\":\"perhaps of foreign origin\",\"pronun\":{\"ipa\":\"ˈspoŋ.ɣos\",\"ipa_mod\":\"ˈspowŋ.ɣows\",\"sbl\":\"spongos\",\"dic\":\"SPOHNG-gose\",\"dic_mod\":\"SPOHNG-gose\"}}', 'spunge'),
(4599, 4700, 'σποδός', '{\"def\":{\"short\":\"ashes\",\"long\":[\"ashes: placed on sackcloth as a token of grief\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"spoˈðos\",\"ipa_mod\":\"spowˈðows\",\"sbl\":\"spodos\",\"dic\":\"spoh-THOSE\",\"dic_mod\":\"spoh-THOSE\"}}', 'ashes'),
(4600, 4701, 'σπορά', '{\"def\":{\"short\":\"a sowing, i.e., (by implication) parentage\",\"long\":[\"seed\"]},\"deriv\":\"from G4687\",\"pronun\":{\"ipa\":\"spoˈrɑ\",\"ipa_mod\":\"spowˈrɑ\",\"sbl\":\"spora\",\"dic\":\"spoh-RA\",\"dic_mod\":\"spoh-RA\"},\"see\":[\"G4687\"]}', 'seed'),
(4601, 4702, 'σπόριμος', '{\"def\":{\"short\":\"sown, i.e., (neuter plural) a planted field\",\"long\":[\"fit for sowing, sown\",\"sown fields, growing crops\"]},\"deriv\":\"from G4703\",\"pronun\":{\"ipa\":\"ˈspo.ri.mos\",\"ipa_mod\":\"ˈspow.ri.mows\",\"sbl\":\"sporimos\",\"dic\":\"SPOH-ree-mose\",\"dic_mod\":\"SPOH-ree-mose\"},\"see\":[\"G4703\"]}', 'corn(-field)'),
(4602, 4703, 'σπόρος', '{\"def\":{\"short\":\"a scattering (of seed), i.e., (concretely) seed (as sown)\",\"long\":[\"a sowing\",\"seed (used in sowing)\"]},\"deriv\":\"from G4687\",\"pronun\":{\"ipa\":\"ˈspo.ros\",\"ipa_mod\":\"ˈspow.rows\",\"sbl\":\"sporos\",\"dic\":\"SPOH-rose\",\"dic_mod\":\"SPOH-rose\"},\"see\":[\"G4687\"]}', 'seed (X sown)'),
(4603, 4704, 'σπουδάζω', '{\"def\":{\"short\":\"to use speed, i.e., to make effort, be prompt or earnest\",\"long\":[\"to hasten, make haste\",\"to exert one\'s self, endeavor, give diligence\"]},\"deriv\":\"from G4710\",\"pronun\":{\"ipa\":\"spuˈðɑ.zo\",\"ipa_mod\":\"spuˈðɑ.zow\",\"sbl\":\"spoudazō\",\"dic\":\"spoo-THA-zoh\",\"dic_mod\":\"spoo-THA-zoh\"},\"see\":[\"G4710\"]}', 'do (give) diligence, be diligent (forward), endeavour, labour, study'),
(4604, 4705, 'σπουδαῖος', '{\"def\":{\"short\":\"prompt, energetic, earnest\",\"long\":[\"active, diligent, zealous, earnest\",\"very diligent\"]},\"deriv\":\"from G4710\",\"pronun\":{\"ipa\":\"spuˈðɛ.os\",\"ipa_mod\":\"spuˈðe.ows\",\"sbl\":\"spoudaios\",\"dic\":\"spoo-THEH-ose\",\"dic_mod\":\"spoo-THAY-ose\"},\"see\":[\"G4710\"]}', 'diligent'),
(4605, 4706, 'σπουδαιότερον', '{\"def\":{\"short\":\"more earnestly than others), i.e., very promptly\",\"long\":[\"very diligently\"]},\"deriv\":\"neuter of G4707 as adverb\",\"pronun\":{\"ipa\":\"spu.ðɛˈo.tɛ.ron\",\"ipa_mod\":\"spu.ðeˈow.te̞.rown\",\"sbl\":\"spoudaioteron\",\"dic\":\"spoo-theh-OH-teh-rone\",\"dic_mod\":\"spoo-thay-OH-tay-rone\"},\"see\":[\"G4707\"]}', 'very diligently'),
(4606, 4707, 'σπουδαιότερος', '{\"def\":{\"short\":\"more prompt, more earnest\",\"long\":[\"active, diligent, zealous, earnest\",\"very diligent\"]},\"deriv\":\"comparative of G4705\",\"pronun\":{\"ipa\":\"spu.ðɛˈo.tɛ.ros\",\"ipa_mod\":\"spu.ðeˈow.te̞.rows\",\"sbl\":\"spoudaioteros\",\"dic\":\"spoo-theh-OH-teh-rose\",\"dic_mod\":\"spoo-thay-OH-tay-rose\"},\"see\":[\"G4705\"]}', 'more diligent (forward)'),
(4607, 4708, 'σπουδαιοτέρως', '{\"def\":{\"short\":\"more speedily, i.e., sooner than otherwise\",\"long\":[\"hastily, with haste\",\"diligently, earnestly\"]},\"deriv\":\"adverb from G4707\",\"pronun\":{\"ipa\":\"spu.ðɛ.oˈtɛ.ros\",\"ipa_mod\":\"spu.ðe.owˈte̞.rows\",\"sbl\":\"spoudaioterōs\",\"dic\":\"spoo-theh-oh-TEH-rose\",\"dic_mod\":\"spoo-thay-oh-TAY-rose\"},\"see\":[\"G4707\"]}', 'more carefully'),
(4608, 4709, 'σπουδαίως', '{\"def\":{\"short\":\"earnestly, promptly\",\"long\":[\"hastily, with haste\",\"diligently\",\"earnestly\"]},\"deriv\":\"adverb from G4705\",\"pronun\":{\"ipa\":\"spuˈðɛ.os\",\"ipa_mod\":\"spuˈðe.ows\",\"sbl\":\"spoudaiōs\",\"dic\":\"spoo-THEH-ose\",\"dic_mod\":\"spoo-THAY-ose\"},\"see\":[\"G4705\"]}', 'diligently, instantly'),
(4609, 4710, 'σπουδή', '{\"def\":{\"short\":\"\\\"speed\\\", i.e., (by implication) despatch, eagerness, earnestness\",\"long\":[\"haste, with haste\",\"earnestness, diligence\",[\"earnestness in accomplishing, promoting, or striving after anything\",\"to give all diligence, interest one\'s self most earnestly\"]]},\"deriv\":\"from G4692\",\"pronun\":{\"ipa\":\"spuˈðe\",\"ipa_mod\":\"spuˈðe̞\",\"sbl\":\"spoudē\",\"dic\":\"spoo-THAY\",\"dic_mod\":\"spoo-THAY\"},\"see\":[\"G4692\"]}', 'business, (earnest) care(-fulness), diligence, forwardness, haste'),
(4610, 4711, 'σπυρίς', '{\"def\":{\"short\":\"a hamper or lunch-receptacle\",\"long\":[\"a reed basket, (a plaited basket, a lunch basket, hamper)\"]},\"deriv\":\"from G4687 (as woven)\",\"pronun\":{\"ipa\":\"spyˈris\",\"ipa_mod\":\"spjuˈris\",\"sbl\":\"spyris\",\"dic\":\"spoo-REES\",\"dic_mod\":\"spyoo-REES\"},\"see\":[\"G4687\"]}', 'basket'),
(4611, 4712, 'στάδιον', '{\"def\":{\"short\":\"a stade or certain measure of distance; by implication, a stadium or race-course\",\"long\":[\"a space or distance of about 600 feet (185 m)\",\"a race course\",[\"place in which contests in running were held, the one who outstripped the rest and reached the goal first, receiving the prize\"]]},\"deriv\":\"from the base of G2476 (as fixed)\",\"pronun\":{\"ipa\":\"ˈstɑ.ði.on\",\"ipa_mod\":\"ˈstɑ.ði.own\",\"sbl\":\"stadion\",\"dic\":\"STA-thee-one\",\"dic_mod\":\"STA-thee-one\"},\"see\":[\"G2476\"],\"comment\":\"Courses of this description were found in most of the larger Greek cities and were like that at Olympia: 600 Greek feet in length.\"}', 'furlong, race'),
(4612, 4713, 'στάμνος', '{\"def\":{\"short\":\"a jar or earthen tank\",\"long\":[\"among the Greeks an earthen jar, into which wine was drawn off for keeping but also used for other purposes\",[\"of the little jar in which the manna was kept, laid up in the ark of the covenant\"]]},\"deriv\":\"from the base of G2476 (as stationary)\",\"pronun\":{\"ipa\":\"ˈstɑm.nos\",\"ipa_mod\":\"ˈstɑm.nows\",\"sbl\":\"stamnos\",\"dic\":\"STAHM-nose\",\"dic_mod\":\"STAHM-nose\"},\"see\":[\"G2476\"]}', 'pot'),
(4613, 4714, 'στάσις', '{\"def\":{\"short\":\"a standing (properly, the act), i.e., (by analogy) position (existence); by implication, a popular uprising; figuratively, controversy\",\"long\":[\"a standing, station, state\",\"an insurrection\",\"strife, insurrection\"]},\"deriv\":\"from the base of G2476\",\"pronun\":{\"ipa\":\"ˈstɑ.sis\",\"ipa_mod\":\"ˈstɑ.sis\",\"sbl\":\"stasis\",\"dic\":\"STA-sees\",\"dic_mod\":\"STA-sees\"},\"see\":[\"G2476\"]}', 'dissension, insurrection, X standing, uproar'),
(4614, 4715, 'στατήρ', '{\"def\":{\"short\":\"a stander (standard of value), i.e., (specially), a stater or certain coin\",\"long\":[\"a stater, a coin\",[\"in the New Testament a silver stater equal to four Attic or two Alexandrian drachmas, a Jewish shekel\"]]},\"deriv\":\"from the base of G2746\",\"pronun\":{\"ipa\":\"stɑˈter\",\"ipa_mod\":\"stɑˈte̞r\",\"sbl\":\"statēr\",\"dic\":\"sta-TARE\",\"dic_mod\":\"sta-TARE\"},\"see\":[\"G2746\"]}', 'piece of money'),
(4615, 4716, 'σταυρός', '{\"def\":{\"short\":\"a stake or post (as set upright), i.e., (specially), a pole or cross (as an instrument of capital punishment); figuratively, exposure to death, i.e., self-denial; by implication, the atonement of Christ\",\"long\":[\"an upright stake, especially a pointed one\",\"a cross\",[\"a well known instrument of most cruel and ignominious punishment, borrowed by the Greeks and Romans from the Phoenicians; to it were affixed among the Romans, down to the time of Constantine the Great, the guiltiest criminals, particularly the basest slaves, robbers, the authors and abetters of insurrections, and occasionally in the provinces, at the arbitrary pleasure of the governors, upright and peaceable men also, and even Roman citizens themselves\",\"the crucifixion which Christ underwent\"]]},\"deriv\":\"from the base of G2476\",\"pronun\":{\"ipa\":\"stɛβˈros\",\"ipa_mod\":\"staˈrows\",\"sbl\":\"stauros\",\"dic\":\"stev-ROSE\",\"dic_mod\":\"sta-ROSE\"},\"see\":[\"G2476\"]}', 'cross'),
(4616, 4717, 'σταυρόω', '{\"def\":{\"short\":\"to impale on the cross; figuratively, to extinguish (subdue) passion or selfishness\",\"long\":[\"to stake, drive down stakes\",\"to fortify with driven stakes, to palisade\",\"to crucify\",[\"to crucify one\",\"metaphorically to crucify the flesh, destroy its power utterly (the nature of the figure implying that the destruction is attended with intense pain)\"]]},\"deriv\":\"from G4716\",\"pronun\":{\"ipa\":\"stɛβˈro.o\",\"ipa_mod\":\"staˈrow.ow\",\"sbl\":\"stauroō\",\"dic\":\"stev-ROH-oh\",\"dic_mod\":\"sta-ROH-oh\"},\"see\":[\"G4716\"]}', 'crucify'),
(4617, 4718, 'σταφυλή', '{\"def\":{\"short\":\"a cluster of grapes (as if intertwined)\",\"long\":[\"grapes, bunch of grapes\"]},\"deriv\":\"probably from the base of G4735\",\"pronun\":{\"ipa\":\"stɑ.fyˈle\",\"ipa_mod\":\"stɑ.fjuˈle̞\",\"sbl\":\"staphylē\",\"dic\":\"sta-foo-LAY\",\"dic_mod\":\"sta-fyoo-LAY\"},\"see\":[\"G4735\"]}', 'grapes'),
(4618, 4719, 'στάχυς', '{\"def\":{\"short\":\"a head of grain (as standing out from the stalk)\",\"long\":[\"an ear of corn or of growing grain\"]},\"deriv\":\"from the base of G2476\",\"pronun\":{\"ipa\":\"ˈstɑ.xys\",\"ipa_mod\":\"ˈstɑ.çjus\",\"sbl\":\"stachys\",\"dic\":\"STA-hoos\",\"dic_mod\":\"STA-hyoos\"},\"see\":[\"G2476\"]}', 'ear (of corn)'),
(4619, 4720, 'Στάχυς', '{\"def\":{\"lit\":\"a head of grain\",\"short\":\"Stachys, a Christian\",\"long\":[\"a man with a Greek name, he is said to be one of the seventy disciples, and bishop of Byzantium (Gill)\"]},\"deriv\":\"the same as G4719\",\"pronun\":{\"ipa\":\"ˈstɑ.xys\",\"ipa_mod\":\"ˈstɑ.çjus\",\"sbl\":\"stachys\",\"dic\":\"STA-hoos\",\"dic_mod\":\"STA-hyoos\"},\"see\":[\"G4719\"]}', 'Stachys'),
(4620, 4721, 'στέγη', '{\"def\":{\"short\":\"a roof\",\"long\":[\"a roof: of a house\"]},\"deriv\":\"strengthened from a primary τέγος (a \\\"thatch\\\" or \\\"deck\\\" of a building)\",\"pronun\":{\"ipa\":\"ˈstɛ.ɣe\",\"ipa_mod\":\"ˈste̞.ɣe̞\",\"sbl\":\"stegē\",\"dic\":\"STEH-gay\",\"dic_mod\":\"STAY-gay\"}}', 'roof'),
(4621, 4722, 'στέγω', '{\"def\":{\"short\":\"to roof over, i.e., (figuratively) to cover with silence (endure patiently)\",\"long\":[\"deck, thatch, to cover\",[\"to protect or keep by covering, to preserve\"],\"to cover over with silence\",[\"to keep secret\",\"to hide, conceal\",[\"of the errors and faults of others\"]],\"by covering to keep off something which threatens, to bear up against, hold out against, and so endure, bear, forbear\"]},\"deriv\":\"from G4721\",\"pronun\":{\"ipa\":\"ˈstɛ.ɣo\",\"ipa_mod\":\"ˈste̞.ɣow\",\"sbl\":\"stegō\",\"dic\":\"STEH-goh\",\"dic_mod\":\"STAY-goh\"},\"see\":[\"G4721\"]}', '(for-)bear, suffer'),
(4622, 4723, 'στείρος', '{\"def\":{\"short\":\"\\\"sterile\\\"\",\"long\":[\"hard, stiff\",[\"of men and animals\"],\"barren\",[\"of woman who does not conceive\"]]},\"deriv\":\"a contraction from G4731 (as stiff and unnatural)\",\"pronun\":{\"ipa\":\"ˈsti.ros\",\"ipa_mod\":\"ˈsti.rows\",\"sbl\":\"steiros\",\"dic\":\"STEE-rose\",\"dic_mod\":\"STEE-rose\"},\"see\":[\"G4731\"]}', 'barren'),
(4623, 4724, 'στέλλω', '{\"def\":{\"short\":\"properly, to set fast (\\\"stall\\\"), i.e., (figuratively) to repress (reflexively, abstain from associating with)\",\"long\":[\"to set, place, set in order, arrange\",[\"to fit out, to prepare, equip\",\"to prepare one\'s self, to fit out for one\'s self\",\"to fit out for one\'s own use\",\"to prepare one\'s self, to fit out for one\'s self\",\"to fit out for one\'s own use\",[\"arranging, providing for this, etc.\"]],\"to bring together, contract, shorten\",[\"to diminish, check, cause to cease\",\"to cease to exist\",\"to remove one\'s self, withdraw one\'s self, to depart\",\"to abstain from familiar intercourse with one\"]]},\"deriv\":\"probably strengthened from the base of G2476\",\"pronun\":{\"ipa\":\"ˈstɛl.lo\",\"ipa_mod\":\"ˈste̞l.low\",\"sbl\":\"stellō\",\"dic\":\"STEL-loh\",\"dic_mod\":\"STALE-loh\"},\"see\":[\"G2476\"]}', 'avoid, withdraw self'),
(4624, 4725, 'στέμμα', '{\"def\":{\"short\":\"a wreath for show\",\"long\":[\"a fillet, a garland, put upon victims\"]},\"deriv\":\"from the base of G4735\",\"pronun\":{\"ipa\":\"ˈstɛm.mɑ\",\"ipa_mod\":\"ˈste̞m.mɑ\",\"sbl\":\"stemma\",\"dic\":\"STEM-ma\",\"dic_mod\":\"STAME-ma\"},\"see\":[\"G4735\"]}', 'garland'),
(4625, 4726, 'στεναγμός', '{\"def\":{\"short\":\"a sigh\",\"long\":[\"a groaning, a sigh\"]},\"deriv\":\"from G4727\",\"pronun\":{\"ipa\":\"stɛ.nɑɣˈmos\",\"ipa_mod\":\"ste̞.nɑɣˈmows\",\"sbl\":\"stenagmos\",\"dic\":\"steh-nahg-MOSE\",\"dic_mod\":\"stay-nahg-MOSE\"},\"see\":[\"G4727\"]}', 'groaning'),
(4626, 4727, 'στενάζω', '{\"def\":{\"short\":\"to make (intransitively, be) in straits, i.e., (by implication) to sigh, murmur, pray inaudibly\",\"long\":[\"a sigh, to groan\"]},\"deriv\":\"from G4728\",\"pronun\":{\"ipa\":\"stɛˈnɑ.zo\",\"ipa_mod\":\"ste̞ˈnɑ.zow\",\"sbl\":\"stenazō\",\"dic\":\"steh-NA-zoh\",\"dic_mod\":\"stay-NA-zoh\"},\"see\":[\"G4728\"]}', 'with grief, groan, grudge, sigh'),
(4627, 4728, 'στενός', '{\"def\":{\"short\":\"narrow (from obstacles standing close about)\",\"long\":[\"narrow, strait\"]},\"deriv\":\"probably from the base of G2476\",\"pronun\":{\"ipa\":\"stɛˈnos\",\"ipa_mod\":\"ste̞ˈnows\",\"sbl\":\"stenos\",\"dic\":\"steh-NOSE\",\"dic_mod\":\"stay-NOSE\"},\"see\":[\"G2476\"]}', 'strait'),
(4628, 4729, 'στενοχωρέω', '{\"def\":{\"short\":\"to hem in closely, i.e., (figuratively) cramp\",\"long\":[\"to be in a narrow place\",\"to straiten, compress, cramp, reduce to straits\",[\"to be sorely straitened in spirit\"]]},\"deriv\":\"from the same as G4730\",\"pronun\":{\"ipa\":\"stɛ.no.xoˈrɛ.o\",\"ipa_mod\":\"ste̞.now.xowˈre̞.ow\",\"sbl\":\"stenochōreō\",\"dic\":\"steh-noh-hoh-REH-oh\",\"dic_mod\":\"stay-noh-hoh-RAY-oh\"},\"see\":[\"G4730\"]}', 'distress, straiten'),
(4629, 4730, 'στενοχωρία', '{\"def\":{\"short\":\"narrowness of room, i.e., (figuratively) calamity\",\"long\":[\"narrowness of place, a narrow place\",\"metaphorically dire calamity, extreme affliction\"]},\"deriv\":\"from a compound of G4728 and G5561\",\"pronun\":{\"ipa\":\"stɛ.no.xoˈri.ɑ\",\"ipa_mod\":\"ste̞.now.xowˈri.ɑ\",\"sbl\":\"stenochōria\",\"dic\":\"steh-noh-hoh-REE-ah\",\"dic_mod\":\"stay-noh-hoh-REE-ah\"},\"see\":[\"G4728\",\"G5561\"]}', 'anguish, distress'),
(4630, 4731, 'στερεός', '{\"def\":{\"short\":\"stiff, i.e., solid, stable (literally or figuratively)\",\"long\":[\"strong, firm, immovable, solid, hard, rigid\",[\"in a bad sense, cruel, stiff, stubborn, hard\",\"in a good sense, firm, steadfast\"]]},\"deriv\":\"from G2476\",\"pronun\":{\"ipa\":\"stɛ.rɛˈos\",\"ipa_mod\":\"ste̞.re̞ˈows\",\"sbl\":\"stereos\",\"dic\":\"steh-reh-OSE\",\"dic_mod\":\"stay-ray-OSE\"},\"see\":[\"G2476\"]}', 'stedfast, strong, sure'),
(4631, 4732, 'στερεόω', '{\"def\":{\"short\":\"to solidify, i.e., confirm (literally or figuratively)\",\"long\":[\"to make solid, make firm, strengthen, make strong\",[\"of the body of anyone\"]]},\"deriv\":\"from G4731\",\"pronun\":{\"ipa\":\"stɛ.rɛˈo.o\",\"ipa_mod\":\"ste̞.re̞ˈow.ow\",\"sbl\":\"stereoō\",\"dic\":\"steh-reh-OH-oh\",\"dic_mod\":\"stay-ray-OH-oh\"},\"see\":[\"G4731\"]}', 'establish, receive strength, make strong'),
(4632, 4733, 'στερέωμα', '{\"def\":{\"short\":\"something established, i.e., (abstractly) confirmation (stability)\",\"long\":[\"that which has been made firm\",[\"the firmament, the arch of the sky, which in early times was thought to be solid\",[\"a fortified place\"],\"that which furnishes a foundation\",[\"on which a thing rests firmly, support\"],\"firmness, steadfastness\",[\"metaphorically in a military sense: solid front\"]]]},\"deriv\":\"from G4732\",\"pronun\":{\"ipa\":\"stɛˈrɛ.o.mɑ\",\"ipa_mod\":\"ste̞ˈre̞.ow.mɑ\",\"sbl\":\"stereōma\",\"dic\":\"steh-REH-oh-ma\",\"dic_mod\":\"stay-RAY-oh-ma\"},\"see\":[\"G4732\"]}', 'stedfastness'),
(4633, 4734, 'Στεφανᾶς', '{\"def\":{\"lit\":\"crowned\",\"short\":\"Stephanas, a Christian\",\"long\":[\"a Christian convert of Corinth\"]},\"deriv\":\"probably contraction for στεφανωτός (crowned; from G4737)\",\"pronun\":{\"ipa\":\"stɛ.fɑˈnɑs\",\"ipa_mod\":\"ste̞.fɑˈnɑs\",\"sbl\":\"stephanas\",\"dic\":\"steh-fa-NAHS\",\"dic_mod\":\"stay-fa-NAHS\"},\"see\":[\"G4737\"]}', 'Stephanas'),
(4634, 4735, 'στέφανος', '{\"def\":{\"short\":\"a chaplet (as a badge of royalty, a prize in the public games or a symbol of honor generally; but more conspicuous and elaborate than the simple fillet, G1238), literally or figuratively\",\"long\":[\"a crown\",[\"a mark of royal or (generally) exalted rank\",[\"the wreath or garland which was given as a prize to victors in public games\"],\"metaphorically the eternal blessedness which will be given as a prize to the genuine servants of God and Christ: the crown (wreath) which is the reward of the righteous\",\"that which is an ornament and honor to one\"]]},\"deriv\":\"from an apparently primary στέφω (to twine or wreathe)\",\"pronun\":{\"ipa\":\"ˈstɛ.fɑ.nos\",\"ipa_mod\":\"ˈste̞.fɑ.nows\",\"sbl\":\"stephanos\",\"dic\":\"STEH-fa-nose\",\"dic_mod\":\"STAY-fa-nose\"},\"see\":[\"G1238\"]}', 'crown'),
(4635, 4736, 'Στέφανος', '{\"def\":{\"lit\":\"crowned\",\"short\":\"Stephanus, a Christian\",\"long\":[\"one of the seven deacons in Jerusalem and the first Christian martyr\"]},\"deriv\":\"the same as G4735\",\"pronun\":{\"ipa\":\"ˈstɛ.fɑ.nos\",\"ipa_mod\":\"ˈste̞.fɑ.nows\",\"sbl\":\"stephanos\",\"dic\":\"STEH-fa-nose\",\"dic_mod\":\"STAY-fa-nose\"},\"see\":[\"G4735\"]}', 'Stephen'),
(4636, 4737, 'στεφανόω', '{\"def\":{\"short\":\"to adorn with an honorary wreath (literally or figuratively)\",\"long\":[\"to encircle with a crown, to crown: the victor in a contest\",\"to adorn, to honor\"]},\"deriv\":\"from G4735\",\"pronun\":{\"ipa\":\"stɛ.fɑˈno.o\",\"ipa_mod\":\"ste̞.fɑˈnow.ow\",\"sbl\":\"stephanoō\",\"dic\":\"steh-fa-NOH-oh\",\"dic_mod\":\"stay-fa-NOH-oh\"},\"see\":[\"G4735\"]}', 'crown'),
(4637, 4738, 'στῆθος', '{\"def\":{\"short\":\"the (entire external) bosom, i.e., chest\",\"long\":[\"the breast\"]},\"deriv\":\"from G2476 (as standing prominently)\",\"pronun\":{\"ipa\":\"ˈste.θos\",\"ipa_mod\":\"ˈste̞.θows\",\"sbl\":\"stēthos\",\"dic\":\"STAY-those\",\"dic_mod\":\"STAY-those\"},\"see\":[\"G2476\"]}', 'breast'),
(4638, 4739, 'στήκω', '{\"def\":{\"short\":\"to be stationary, i.e., (figuratively) to persevere\",\"long\":[\"to stand firm\",\"to persevere, to persist\",\"to keep one\'s standing\"]},\"deriv\":\"from the perfect tense of G2476\",\"pronun\":{\"ipa\":\"ˈste.ko\",\"ipa_mod\":\"ˈste̞.kow\",\"sbl\":\"stēkō\",\"dic\":\"STAY-koh\",\"dic_mod\":\"STAY-koh\"},\"see\":[\"G2476\"]}', 'stand (fast)'),
(4639, 4740, 'στηριγμός', '{\"def\":{\"short\":\"stability (figuratively)\",\"long\":[\"firm condition, steadfastness: of mind\"]},\"deriv\":\"from G4741\",\"pronun\":{\"ipa\":\"ste.riɣˈmos\",\"ipa_mod\":\"ste̞.riɣˈmows\",\"sbl\":\"stērigmos\",\"dic\":\"stay-reeg-MOSE\",\"dic_mod\":\"stay-reeg-MOSE\"},\"see\":[\"G4741\"]}', 'stedfastness'),
(4640, 4741, 'στηρίζω', '{\"def\":{\"short\":\"to set fast, i.e., (literally) to turn resolutely in a certain direction, or (figuratively) to confirm\",\"long\":[\"to make stable, place firmly, set fast, fix\",\"to strengthen, make firm\",\"to render constant, confirm, one\'s mind\"]},\"deriv\":\"from a presumed derivative of G2476 (like G4731)\",\"pronun\":{\"ipa\":\"steˈri.zo\",\"ipa_mod\":\"ste̞ˈri.zow\",\"sbl\":\"stērizō\",\"dic\":\"stay-REE-zoh\",\"dic_mod\":\"stay-REE-zoh\"},\"see\":[\"G2476\",\"G4731\"]}', 'fix, (e-)stablish, stedfastly set, strengthen'),
(4641, 4742, 'στίγμα', '{\"def\":{\"short\":\"a mark incised or punched (for recognition of ownership), i.e., (figuratively) scar of service\",\"long\":[\"a mark pricked in or branded upon the body\"]},\"deriv\":\"from a primary στίζω (to \\\"stick\\\", i.e. prick)\",\"pronun\":{\"ipa\":\"ˈstiɣ.mɑ\",\"ipa_mod\":\"ˈstiɣ.mɑ\",\"sbl\":\"stigma\",\"dic\":\"STEEG-ma\",\"dic_mod\":\"STEEG-ma\"},\"comment\":\"According to ancient Middle Eastern usage, slaves and soldiers bore the name or the stamp of their master or commander branded or pricked (cut) into their bodies to indicate what master or general they belonged to and there were even some devotee\'s who stamped themselves in this way with the token of their gods.\"}', 'mark'),
(4642, 4743, 'στιγμή', '{\"def\":{\"short\":\"a point of time, i.e., an instant\",\"long\":[\"an instant (i.e., a moment) of time\"]},\"deriv\":\"feminine of G4742\",\"pronun\":{\"ipa\":\"stiɣˈme\",\"ipa_mod\":\"stiɣˈme̞\",\"sbl\":\"stigmē\",\"dic\":\"steeg-MAY\",\"dic_mod\":\"steeg-MAY\"},\"see\":[\"G4742\"]}', 'moment'),
(4643, 4744, 'στίλβω', '{\"def\":{\"short\":\"to gleam, i.e., flash intensely\",\"long\":[\"to shine, glisten\",[\"of garments\"]]},\"deriv\":\"apparently a primary verb\",\"pronun\":{\"ipa\":\"ˈstil.βo\",\"ipa_mod\":\"ˈstil.vow\",\"sbl\":\"stilbō\",\"dic\":\"STEEL-voh\",\"dic_mod\":\"STEEL-voh\"}}', 'shining'),
(4644, 4745, 'στοά', '{\"def\":{\"short\":\"a colonnade or interior piazza\",\"long\":[\"a portico, a covered colonnade where people can stand or walk protected from the weather and the heat of the sun\",\"the portico built by Solomon in the eastern part of the temple\"]},\"deriv\":\"probably from G2476\",\"pronun\":{\"ipa\":\"stoˈɑ\",\"ipa_mod\":\"stowˈɑ\",\"sbl\":\"stoa\",\"dic\":\"stoh-AH\",\"dic_mod\":\"stoh-AH\"},\"see\":[\"G2476\"],\"comment\":\"The porch built by Solomon on the eastern part of the temple survived the temple\'s destruction by the Babylonians and remained unto the time of King Agrippa, to whom the care of the temple was entrusted by the emperor Claudius, and who on account of its antiquity dare not demolish it and build anew.\"}', 'porch'),
(4645, 4746, 'στοιβάς', '{\"def\":{\"short\":\"a spread (as if tramped flat) of loose materials for a couch, i.e., (by implication) a bough of a tree so employed\",\"long\":[\"branch, leafy limb\"]},\"deriv\":\"from a primary στείβω (to \\\"step\\\" or \\\"stamp\\\")\",\"pronun\":{\"ipa\":\"styˈβɑs\",\"ipa_mod\":\"styˈvɑs\",\"sbl\":\"stoibas\",\"dic\":\"stoo-VAHS\",\"dic_mod\":\"stoo-VAHS\"}}', 'branch'),
(4646, 4747, 'στοιχεῖον', '{\"def\":{\"short\":\"something orderly in arrangement, i.e., (by implication) a serial (basal, fundamental, initial) constituent (literally), proposition (figuratively)\",\"long\":[\"any first thing, from which the others belonging to some series or composite whole take their rise, an element, first principal\",[\"the letters of the alphabet as the elements of speech, not however the written characters, but the spoken sounds\",\"the elements from which all things have come, the material causes of the universe\",\"the heavenly bodies, either as parts of the heavens or (as others think) because in them the elements of man, life and destiny were supposed to reside\",\"the elements, rudiments, primary and fundamental principles of any art, science, or discipline\",[\"i.e., of mathematics, Euclid\'s geometry\"]]]},\"deriv\":\"neuter of a presumed derivative of the base of G4748\",\"pronun\":{\"ipa\":\"styˈxi.on\",\"ipa_mod\":\"styˈçi.own\",\"sbl\":\"stoicheion\",\"dic\":\"stoo-HEE-one\",\"dic_mod\":\"stoo-HEE-one\"},\"see\":[\"G4748\"]}', 'element, principle, rudiment'),
(4647, 4748, 'στοιχέω', '{\"def\":{\"short\":\"to march in (military) rank (keep step), i.e., (figuratively) to conform to virtue and piety\",\"long\":[\"to proceed in a row as the march of a soldier, go in order\",[\"metaphorically to go on prosperously, to turn out well\"],\"to walk\",[\"to direct one\'s life, to live\"]]},\"deriv\":\"from a derivative of στείχω (to range in regular line)\",\"pronun\":{\"ipa\":\"styˈxɛ.o\",\"ipa_mod\":\"styˈxe̞.ow\",\"sbl\":\"stoicheō\",\"dic\":\"stoo-HEH-oh\",\"dic_mod\":\"stoo-HAY-oh\"}}', 'walk (orderly)'),
(4648, 4749, 'στολή', '{\"def\":{\"short\":\"equipment, i.e., (specially), a \\\"stole\\\" or long-fitting gown (as a mark of dignity)\",\"long\":[\"an equipment\",\"an equipment in clothes, clothing\",[\"specifically a loose outer garment for men extending to the feet, worn by kings, priests, and persons of rank\"]]},\"deriv\":\"from G4724\",\"pronun\":{\"ipa\":\"stoˈle\",\"ipa_mod\":\"stowˈle̞\",\"sbl\":\"stolē\",\"dic\":\"stoh-LAY\",\"dic_mod\":\"stoh-LAY\"},\"see\":[\"G4724\"]}', 'long clothing (garment), (long) robe'),
(4649, 4750, 'στόμα', '{\"def\":{\"short\":\"the mouth (as if a gash in the face); by implication, language (and its relations); figuratively, an opening (in the earth); specially, the front or edge (of a weapon)\",\"long\":[\"the mouth, as part of the body: of man, of animals, of fish, etc.\",[\"since thoughts of a man\'s soul find verbal utterance by his mouth, the \\\"heart\\\" or \\\"soul\\\" and the mouth are distinguished\"],\"the edge of a sword\"]},\"deriv\":\"probably strengthened from a presumed derivative of the base of G5114\",\"pronun\":{\"ipa\":\"ˈsto.mɑ\",\"ipa_mod\":\"ˈstow.mɑ\",\"sbl\":\"stoma\",\"dic\":\"STOH-ma\",\"dic_mod\":\"STOH-ma\"},\"see\":[\"G5114\"]}', 'edge, face, mouth'),
(4650, 4751, 'στόμαχος', '{\"def\":{\"short\":\"an orifice (the gullet), i.e., (specially), the \\\"stomach\\\"\",\"long\":[\"the throat\",\"an opening, orifice, especially of the stomach\",\"the stomach\"]},\"deriv\":\"from G4750\",\"pronun\":{\"ipa\":\"ˈsto.mɑ.xos\",\"ipa_mod\":\"ˈstow.mɑ.xows\",\"sbl\":\"stomachos\",\"dic\":\"STOH-ma-hose\",\"dic_mod\":\"STOH-ma-hose\"},\"see\":[\"G4750\"]}', 'stomach'),
(4651, 4752, 'στρατεία', '{\"def\":{\"short\":\"military service, i.e., (figuratively) the apostolic career (as one of hardship and danger)\",\"long\":[\"an expedition, campaign, military service, warfare\",\"metaphorically Paul likens his contest with the difficulties that oppose him in the discharge of his apostolic duties, as warfare\"]},\"deriv\":\"from G4754\",\"pronun\":{\"ipa\":\"strɑˈti.ɑ\",\"ipa_mod\":\"strɑˈti.ɑ\",\"sbl\":\"strateia\",\"dic\":\"stra-TEE-ah\",\"dic_mod\":\"stra-TEE-ah\"},\"see\":[\"G4754\"]}', 'warfare'),
(4652, 4753, 'στράτευμα', '{\"def\":{\"short\":\"an armament, i.e., (by implication) a body of troops (more or less extensive or systematic)\",\"long\":[\"an army\",\"a band of soldiers\",\"bodyguard, guards men\"]},\"deriv\":\"from G4754\",\"pronun\":{\"ipa\":\"ˈstrɑ.tɛβ.mɑ\",\"ipa_mod\":\"ˈstrɑ.tev.mɑ\",\"sbl\":\"strateuma\",\"dic\":\"STRA-tev-ma\",\"dic_mod\":\"STRA-tave-ma\"},\"see\":[\"G4754\"]}', 'army, soldier, man of war'),
(4653, 4754, 'στρατεύομαι', '{\"def\":{\"short\":\"to serve in a military campaign; figuratively, to execute the apostolate (with its arduous duties and functions), to contend with carnal inclinations\",\"long\":[\"to make a military expedition, to lead soldiers to war or to battle, (spoken of a commander)\",\"to do military duty, be on active service, be a soldier\",\"to fight\"]},\"deriv\":\"middle voice from the base of G4756\",\"pronun\":{\"ipa\":\"strɑˈtɛβ.o.mɛ\",\"ipa_mod\":\"strɑˈtev.ow.me\",\"sbl\":\"strateuomai\",\"dic\":\"stra-TEV-oh-meh\",\"dic_mod\":\"stra-TAVE-oh-may\"},\"see\":[\"G4756\"]}', 'soldier, (go to) war(-fare)'),
(4654, 4755, 'στρατηγός', '{\"def\":{\"short\":\"a general, i.e., (by implication or analogy) a (military) governor (praetor), the chief (praefect) of the (Levitical) temple-wardens\",\"long\":[\"the commander of an army\",\"in the New Testament a civic commander, a governor\",[\"the name the highest magistrate in the municipia or colonies; they had the power of administering justice in the less important cases\",\"of civil magistrates\"],\"captain of the temple, i.e., the commander of the Levites who kept guard in and around the temple\"]},\"deriv\":\"from the base of G4756 and G0071 or G2233\",\"pronun\":{\"ipa\":\"strɑ.teˈɣos\",\"ipa_mod\":\"strɑ.te̞ˈɣows\",\"sbl\":\"stratēgos\",\"dic\":\"stra-tay-GOSE\",\"dic_mod\":\"stra-tay-GOSE\"},\"see\":[\"G0071\",\"G2233\",\"G4756\"]}', 'captain, magistrate'),
(4655, 4756, 'στρατιά', '{\"def\":{\"short\":\"from the base of G4766, as encamped); camp-likeness, i.e., an army, i.e., (figuratively) the angels, the celestial luminaries\",\"long\":[\"an army, band of soldiers\",\"in the New Testament, the hosts of heaven\",[\"troops of angels\",\"the heavenly bodies, stars of heaven (so called on account of their number and their order)\"]]},\"deriv\":\"feminine of a derivative of στρατός (an army\",\"pronun\":{\"ipa\":\"strɑ.tiˈɑ\",\"ipa_mod\":\"strɑ.tiˈɑ\",\"sbl\":\"stratia\",\"dic\":\"stra-tee-AH\",\"dic_mod\":\"stra-tee-AH\"},\"see\":[\"G4766\"]}', 'host'),
(4656, 4757, 'στρατιώτης', '{\"def\":{\"short\":\"a camper-out, i.e., a (common) warrior (literally or figuratively)\",\"long\":[\"a (common) soldier\",\"metaphorically a champion of the cause of Christ\"]},\"deriv\":\"from a presumed derivative of the same as G4756\",\"pronun\":{\"ipa\":\"strɑ.tiˈo.tes\",\"ipa_mod\":\"strɑ.tiˈow.te̞s\",\"sbl\":\"stratiōtēs\",\"dic\":\"stra-tee-OH-tase\",\"dic_mod\":\"stra-tee-OH-tase\"},\"see\":[\"G4756\"]}', 'soldier'),
(4657, 4758, 'στρατολογέω', '{\"def\":{\"short\":\"to gather (or select) as a warrior, i.e., enlist in the army\",\"long\":[\"to gather (collect) an army, to enlist soldiers\",[\"of the commander\"]]},\"deriv\":\"from a compound of the base of G4756 and G3004 (in its original sense)\",\"pronun\":{\"ipa\":\"strɑ.to.loˈɣɛ.o\",\"ipa_mod\":\"strɑ.tow.lowˈɣe̞.ow\",\"sbl\":\"stratologeō\",\"dic\":\"stra-toh-loh-GEH-oh\",\"dic_mod\":\"stra-toh-loh-GAY-oh\"},\"see\":[\"G3004\",\"G4756\"]}', 'choose to be a soldier'),
(4658, 4759, 'στρατοπεδάρχης', '{\"def\":{\"short\":\"a ruler of an army, i.e., (specially), a Praetorian praefect\",\"long\":[\"the commander of a camp and army, a military tribune\",\"Praetorian praefect (or prefect), commander of the praetorian cohorts, captain of the Roman emperor\'s body guard\"]},\"deriv\":\"from G4760 and G0757\",\"pronun\":{\"ipa\":\"strɑ.to.pɛˈðɑr.xes\",\"ipa_mod\":\"strɑ.tow.pe̞ˈðɑr.xe̞s\",\"sbl\":\"stratopedarchēs\",\"dic\":\"stra-toh-peh-THAHR-hase\",\"dic_mod\":\"stra-toh-pay-THAHR-hase\"},\"see\":[\"G0757\",\"G4760\"],\"comment\":\"There were two praetorian praefects, to whose custody prisoners sent bound to the emperor were consigned.\"}', 'captain of the guard'),
(4659, 4760, 'στρατόπεδον', '{\"def\":{\"short\":\"a camping-ground, i.e., (by implication) a body of troops\",\"long\":[\"a military camp\",\"soldiers in camp, an army\"]},\"deriv\":\"from the base of G4756 and the same as G3977\",\"pronun\":{\"ipa\":\"strɑˈto.pɛ.ðon\",\"ipa_mod\":\"strɑˈtow.pe̞.ðown\",\"sbl\":\"stratopedon\",\"dic\":\"stra-TOH-peh-thone\",\"dic_mod\":\"stra-TOH-pay-thone\"},\"see\":[\"G3977\",\"G4756\"]}', 'army'),
(4660, 4761, 'στρεβλόω', '{\"def\":{\"short\":\"to wrench, i.e., (specially), to torture (by the rack), but only figuratively, to pervert\",\"long\":[\"to twist, turn awry\",\"to torture, put to the rack\",\"metaphorically to pervert, of one who wrests or tortures language in a false sense\"]},\"deriv\":\"from a derivative of G4762\",\"pronun\":{\"ipa\":\"strɛˈβlo.o\",\"ipa_mod\":\"stre̞ˈvlow.ow\",\"sbl\":\"strebloō\",\"dic\":\"streh-VLOH-oh\",\"dic_mod\":\"stray-VLOH-oh\"},\"see\":[\"G4762\"]}', 'wrest'),
(4661, 4762, 'στρέφω', '{\"def\":{\"short\":\"to twist, i.e., turn quite around or reverse (literally or figuratively)\",\"long\":[\"to turn, turn around\",\"to turn one\'s self (i.e., to turn the back to one\",[\"of one who no longer cares for another)\",\"metaphorically to turn one\'s self from one\'s course of conduct, i.e., to change one\'s mind\"]]},\"deriv\":\"strengthened from the base of G5157\",\"pronun\":{\"ipa\":\"ˈstrɛ.fo\",\"ipa_mod\":\"ˈstre̞.fow\",\"sbl\":\"strephō\",\"dic\":\"STREH-foh\",\"dic_mod\":\"STRAY-foh\"},\"see\":[\"G5157\"]}', 'convert, turn (again, back again, self, self about)');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(4662, 4763, 'στρηνιάω', '{\"def\":{\"short\":\"to be luxurious\",\"long\":[\"to be wanton, to live luxuriously\"]},\"deriv\":\"from a presumed derivative of G4764\",\"pronun\":{\"ipa\":\"stre.niˈɑ.o\",\"ipa_mod\":\"stre̞.niˈɑ.ow\",\"sbl\":\"strēniaō\",\"dic\":\"stray-nee-AH-oh\",\"dic_mod\":\"stray-nee-AH-oh\"},\"see\":[\"G4764\"]}', 'live deliciously'),
(4663, 4764, 'στρῆνος', '{\"def\":{\"short\":\"a \\\"straining\\\", \\\"strenuousness\\\" or \\\"strength\\\", i.e., (figuratively) luxury (voluptuousness)\",\"long\":[\"excessive strength which longs to break forth, over strength\",\"luxury\",\"eager desire\"]},\"deriv\":\"akin to G4731\",\"pronun\":{\"ipa\":\"ˈstre.nos\",\"ipa_mod\":\"ˈstre̞.nows\",\"sbl\":\"strēnos\",\"dic\":\"STRAY-nose\",\"dic_mod\":\"STRAY-nose\"},\"see\":[\"G4731\"]}', 'delicacy'),
(4664, 4765, 'στρουθίον', '{\"def\":{\"short\":\"a little sparrow\",\"long\":[\"a little bird, especially of the sparrow sort, a sparrow\"]},\"deriv\":\"diminutive of στρουθός (a sparrow)\",\"pronun\":{\"ipa\":\"struˈθi.on\",\"ipa_mod\":\"struˈθi.own\",\"sbl\":\"strouthion\",\"dic\":\"stroo-THEE-one\",\"dic_mod\":\"stroo-THEE-one\"}}', 'sparrow'),
(4665, 4766, 'στρώννυμι', '{\"def\":{\"short\":\"to \\\"strew,\\\" i.e., spread (as a carpet or couch)\",\"long\":[\"to spread\",\"furnish\",\"to spread with couches or divans\"]},\"deriv\":\"prolongation from a still simpler στρόω <pronunciation strongs=\\\"stro\'-o\\\"/>, (used only as an alternate in certain tenses) (probably akin to G4731 through the idea of positing)\",\"pronun\":{\"ipa\":\"ˈstron.ny.mi\",\"ipa_mod\":\"ˈstrown.nju.mi\",\"sbl\":\"strōnnymi\",\"dic\":\"STRONE-noo-mee\",\"dic_mod\":\"STRONE-nyoo-mee\"},\"see\":[\"G4731\"]}', 'make bed, furnish, spread, strew'),
(4666, 4767, 'στυγνητός', '{\"def\":{\"short\":\"hated, i.e., odious\",\"long\":[\"hated, detestable\"]},\"deriv\":\"from a derivative of an obsolete apparently primary στύγω (to hate)\",\"pronun\":{\"ipa\":\"sty.ɣneˈtos\",\"ipa_mod\":\"stju.ɣne̞ˈtows\",\"sbl\":\"stygnētos\",\"dic\":\"stoo-gnay-TOSE\",\"dic_mod\":\"styoo-gnay-TOSE\"}}', 'hateful'),
(4667, 4768, 'στυγνάζω', '{\"def\":{\"short\":\"to render gloomy, i.e., (by implication) glower (be overcast with clouds, or sombreness of speech)\",\"long\":[\"to be sad, sorrowful\",\"metaphorically of the sky covered with clouds\"]},\"deriv\":\"from the same as G4767\",\"pronun\":{\"ipa\":\"styˈɣnɑ.zo\",\"ipa_mod\":\"stjuˈɣnɑ.zow\",\"sbl\":\"stygnazō\",\"dic\":\"stoo-GNA-zoh\",\"dic_mod\":\"styoo-GNA-zoh\"},\"see\":[\"G4767\"]}', 'lower, be sad'),
(4668, 4769, 'στῦλος', '{\"def\":{\"short\":\"a post (\\\"style\\\"), i.e., (figuratively) support\",\"long\":[\"a pillar\",\"a column\",[\"pillars of fire i.e., flames rising like pillars\"],\"a prop or support\"]},\"deriv\":\"from στύω (to stiffen; properly akin to the base of G2476)\",\"pronun\":{\"ipa\":\"ˈsty.los\",\"ipa_mod\":\"ˈstju.lows\",\"sbl\":\"stylos\",\"dic\":\"STOO-lose\",\"dic_mod\":\"STYOO-lose\"},\"see\":[\"G2476\"]}', 'pillar'),
(4669, 4770, 'Στωϊκός', '{\"def\":{\"lit\":\"of the portico\",\"short\":\"a \\\"Stoic\\\" (as occupying a particular porch in Athens), i.e., adherent of a certin philosophy\",\"long\":[\"pertaining to the Stoic philosophy, the author of which, Zeno of Citium, taught at Athens\"]},\"deriv\":\"from G4745\",\"pronun\":{\"ipa\":\"sto.iˈkos\",\"ipa_mod\":\"stow.iˈkows\",\"sbl\":\"stōikos\",\"dic\":\"stoh-ee-KOSE\",\"dic_mod\":\"stoh-ee-KOSE\"},\"see\":[\"G4745\"]}', 'Stoick'),
(4670, 4771, 'σύ', '{\"def\":{\"short\":\"thou\",\"long\":[\"you\"]},\"deriv\":\"the personal pronoun of the second person singular\",\"pronun\":{\"ipa\":\"sy\",\"ipa_mod\":\"sju\",\"sbl\":\"sy\",\"dic\":\"soo\",\"dic_mod\":\"syoo\"},\"see\":[\"G4571\",\"G4671\",\"G4675\",\"G5209\",\"G5210\",\"G5213\",\"G5216\"]}', 'thou'),
(4671, 4772, 'συγγένεια', '{\"def\":{\"short\":\"relationship, i.e., (concretely) relatives\",\"long\":[\"a kinship, relationship\",\"kindred, relations collectively, family\"]},\"deriv\":\"from G4773\",\"pronun\":{\"ipa\":\"syŋˈɣɛ.ni.ɑ\",\"ipa_mod\":\"sjuŋˈɣe̞.ni.ɑ\",\"sbl\":\"syngeneia\",\"dic\":\"soong-GEH-nee-ah\",\"dic_mod\":\"syoong-GAY-nee-ah\"},\"see\":[\"G4773\"]}', 'kindred'),
(4672, 4773, 'συγγενής', '{\"def\":{\"short\":\"a relative (by blood); by extension, a fellow countryman\",\"long\":[\"of the same kin, akin to, related by blood\",\"in a wider sense, of the same race, a fellow countryman\"]},\"deriv\":\"from G4862 and G1085\",\"pronun\":{\"ipa\":\"syŋ.ɣɛˈnes\",\"ipa_mod\":\"sjuŋ.ʝe̞ˈne̞s\",\"sbl\":\"syngenēs\",\"dic\":\"soong-geh-NASE\",\"dic_mod\":\"syoong-gay-NASE\"},\"see\":[\"G1085\",\"G4862\"]}', 'cousin, kin(-sfolk, -sman)'),
(4673, 4774, 'συγγνώμη', '{\"def\":{\"short\":\"fellow knowledge, i.e., concession\",\"long\":[\"pardon, indulgence\"]},\"deriv\":\"from a compound of G4862 and G1097\",\"pronun\":{\"ipa\":\"syŋˈɣno.me\",\"ipa_mod\":\"sjuŋˈɣnow.me̞\",\"sbl\":\"syngnōmē\",\"dic\":\"soong-GNOH-may\",\"dic_mod\":\"syoong-GNOH-may\"},\"see\":[\"G1097\",\"G4862\"]}', 'permission'),
(4674, 4775, 'συγκάθημαι', '{\"def\":{\"short\":\"to seat oneself in company with\",\"long\":[\"to sit together, to sit with one another\"]},\"deriv\":\"from G4862 and G2521\",\"pronun\":{\"ipa\":\"syŋˈkɑ.θe.mɛ\",\"ipa_mod\":\"sjuŋˈkɑ.θe̞.me\",\"sbl\":\"synkathēmai\",\"dic\":\"soong-KA-thay-meh\",\"dic_mod\":\"syoong-KA-thay-may\"},\"see\":[\"G2521\",\"G4862\"]}', 'sit with'),
(4675, 4776, 'συγκαθίζω', '{\"def\":{\"short\":\"to give (or take) a seat in company with\",\"long\":[\"to cause to sit down together, place together\",\"to sit down together\"]},\"deriv\":\"from G4862 and G2523\",\"pronun\":{\"ipa\":\"syŋ.kɑˈθi.zo\",\"ipa_mod\":\"sjuŋ.kɑˈθi.zow\",\"sbl\":\"synkathizō\",\"dic\":\"soong-ka-THEE-zoh\",\"dic_mod\":\"syoong-ka-THEE-zoh\"},\"see\":[\"G2523\",\"G4862\"]}', '(make) sit (down) together'),
(4676, 4777, 'συγκακοπαθέω', '{\"def\":{\"short\":\"to suffer hardship in company with\",\"long\":[\"to suffer hardship together with one\"]},\"deriv\":\"from G4862 and G2553\",\"pronun\":{\"ipa\":\"syŋ.kɑ.ko.pɑˈθɛ.o\",\"ipa_mod\":\"sjuŋ.kɑ.kow.pɑˈθe̞.ow\",\"sbl\":\"synkakopatheō\",\"dic\":\"soong-ka-koh-pa-THEH-oh\",\"dic_mod\":\"syoong-ka-koh-pa-THAY-oh\"},\"see\":[\"G2553\",\"G4862\"]}', 'be partaker of afflictions'),
(4677, 4778, 'συγκακουχέω', '{\"def\":{\"short\":\"to maltreat in company with, i.e., (passively) endure persecution together\",\"long\":[\"to treat ill with another\",\"to be ill treated in company with, share persecutions or come into a fellowship of ills\"]},\"deriv\":\"from G4862 and G2558\",\"pronun\":{\"ipa\":\"syŋ.kɑ.kuˈxɛ.o\",\"ipa_mod\":\"sjuŋ.kɑ.kuˈxe̞.ow\",\"sbl\":\"synkakoucheō\",\"dic\":\"soong-ka-koo-HEH-oh\",\"dic_mod\":\"syoong-ka-koo-HAY-oh\"},\"see\":[\"G2558\",\"G4862\"]}', 'suffer affliction with'),
(4678, 4779, 'συγκαλέω', '{\"def\":{\"short\":\"to convoke\",\"long\":[\"to call together, assemble\",\"to call together to one\'s self\"]},\"deriv\":\"from G4862 and G2564\",\"pronun\":{\"ipa\":\"syŋ.kɑˈlɛ.o\",\"ipa_mod\":\"sjuŋ.kɑˈle̞.ow\",\"sbl\":\"synkaleō\",\"dic\":\"soong-ka-LEH-oh\",\"dic_mod\":\"syoong-ka-LAY-oh\"},\"see\":[\"G2564\",\"G4862\"]}', 'call together'),
(4679, 4780, 'συγκαλύπτω', '{\"def\":{\"short\":\"to conceal altogether\",\"long\":[\"to cover on all sides, to conceal entirely, to cover up completely\"]},\"deriv\":\"from G4862 and G2572\",\"pronun\":{\"ipa\":\"syŋ.kɑˈly.pto\",\"ipa_mod\":\"sjuŋ.kɑˈlju.ptow\",\"sbl\":\"synkalyptō\",\"dic\":\"soong-ka-LOO-ptoh\",\"dic_mod\":\"syoong-ka-LYOO-ptoh\"},\"see\":[\"G2572\",\"G4862\"]}', 'cover'),
(4680, 4781, 'συγκάμπτω', '{\"def\":{\"short\":\"to bend together, i.e., (figuratively) to afflict\",\"long\":[\"to bend together, to bend completely\",\"metaphorically to subject one to error and hardness of heart, a figure taken from bowing the back by captives compelled to pass under the yoke\"]},\"deriv\":\"from G4862 and G2578\",\"pronun\":{\"ipa\":\"syŋˈkɑm.pto\",\"ipa_mod\":\"sjuŋˈkɑm.ptow\",\"sbl\":\"synkamptō\",\"dic\":\"soong-KAHM-ptoh\",\"dic_mod\":\"syoong-KAHM-ptoh\"},\"see\":[\"G2578\",\"G4862\"]}', 'bow down'),
(4681, 4782, 'συγκαταβαίνω', '{\"def\":{\"short\":\"to descend in company with\",\"long\":[\"to go down with\",[\"of those who descend from a high place to a lower place as from Jerusalem to Caesarea\"]]},\"deriv\":\"from G4862 and G2597\",\"pronun\":{\"ipa\":\"syŋ.kɑ.tɑˈβɛ.no\",\"ipa_mod\":\"sjuŋ.kɑ.tɑˈve.now\",\"sbl\":\"synkatabainō\",\"dic\":\"soong-ka-ta-VEH-noh\",\"dic_mod\":\"syoong-ka-ta-VAY-noh\"},\"see\":[\"G2597\",\"G4862\"]}', 'go down with'),
(4682, 4783, 'συγκατάθεσις', '{\"def\":{\"short\":\"a deposition (of sentiment) in company with, i.e., (figuratively) accord with\",\"long\":[\"a putting together or joint deposit (of votes)\",[\"hence approval, assent, agreement\"]]},\"deriv\":\"from G4784\",\"pronun\":{\"ipa\":\"syŋ.kɑˈtɑ.θɛ.sis\",\"ipa_mod\":\"sjuŋ.kɑˈtɑ.θe̞.sis\",\"sbl\":\"synkatathesis\",\"dic\":\"soong-ka-TA-theh-sees\",\"dic_mod\":\"syoong-ka-TA-thay-sees\"},\"see\":[\"G4784\"]}', 'agreement'),
(4683, 4784, 'συγκατατίθεμαι', '{\"def\":{\"short\":\"to deposit (one\'s vote or opinion) in company with, i.e., (figuratively) to accord with\",\"long\":[\"to deposit together with another\",\"to deposit one\'s vote in the urn with another\",\"to consent to, vote for, agree with\"]},\"deriv\":\"middle from G4862 and G2698\",\"pronun\":{\"ipa\":\"syŋ.kɑ.tɑˈti.θɛ.mɛ\",\"ipa_mod\":\"sjuŋ.kɑ.tɑˈti.θe̞.me\",\"sbl\":\"synkatatithemai\",\"dic\":\"soong-ka-ta-TEE-theh-meh\",\"dic_mod\":\"syoong-ka-ta-TEE-thay-may\"},\"see\":[\"G2698\",\"G4862\"]}', 'consent'),
(4684, 4785, 'συγκαταψηφίζω', '{\"def\":{\"short\":\"to count down in company with, i.e., enroll among\",\"long\":[\"be depositing a ballot in the urn (i.e., by voting for) to assign one a place among, to vote one a place among\",\"to vote against with others, i.e., to condemn with others\"]},\"deriv\":\"from G4862 and a compound of G2596 and G5585\",\"pronun\":{\"ipa\":\"syŋ.kɑ.tɑ.p͡seˈfi.zo\",\"ipa_mod\":\"sjuŋ.kɑ.tɑ.p͡se̞ˈfi.zow\",\"sbl\":\"synkatapsēphizō\",\"dic\":\"soong-ka-ta-psay-FEE-zoh\",\"dic_mod\":\"syoong-ka-ta-psay-FEE-zoh\"},\"see\":[\"G2596\",\"G4862\",\"G5585\"]}', 'number with'),
(4685, 4786, 'συγκεράννυμι', '{\"def\":{\"short\":\"to commingle, i.e., (figuratively) to combine or assimilate\",\"long\":[\"to mix together, commingle\",\"to unite\",[\"caused the several parts to combine into an organic structure, which is the body\",\"to unite one thing to another\"]]},\"deriv\":\"from G4862 and G2767\",\"pronun\":{\"ipa\":\"syŋ.kɛˈrɑn.ny.mi\",\"ipa_mod\":\"sjuŋ.ce̞ˈrɑn.nju.mi\",\"sbl\":\"synkerannymi\",\"dic\":\"soong-keh-RAHN-noo-mee\",\"dic_mod\":\"syoong-kay-RAHN-nyoo-mee\"},\"see\":[\"G2767\",\"G4862\"]}', 'mix with, temper together'),
(4686, 4787, 'συγκινέω', '{\"def\":{\"short\":\"to move together, i.e., (specially), to excite as a mass (to sedition)\",\"long\":[\"to move together with others\",\"to throw into commotion, excite, stir up\"]},\"deriv\":\"from G4682 and G2795\",\"pronun\":{\"ipa\":\"syŋ.kiˈnɛ.o\",\"ipa_mod\":\"sjuŋ.ciˈne̞.ow\",\"sbl\":\"synkineō\",\"dic\":\"soong-kee-NEH-oh\",\"dic_mod\":\"syoong-kee-NAY-oh\"},\"see\":[\"G2795\",\"G4682\"]}', 'stir up'),
(4687, 4788, 'συγκλείω', '{\"def\":{\"short\":\"to shut together, i.e., include or (figuratively) embrace in a common subjection to\",\"long\":[\"to shut up together, enclose\",[\"of a shoal of fishes in a net\"],\"to shut up on all sides, shut up completely\"]},\"deriv\":\"from G4862 and G2808\",\"pronun\":{\"ipa\":\"syŋˈkli.o\",\"ipa_mod\":\"sjuŋˈkli.ow\",\"sbl\":\"synkleiō\",\"dic\":\"soong-KLEE-oh\",\"dic_mod\":\"syoong-KLEE-oh\"},\"see\":[\"G2808\",\"G4862\"]}', 'conclude, inclose, shut up'),
(4688, 4789, 'συγκληρονόμος', '{\"def\":{\"short\":\"a co-heir, i.e., (by analogy) participant in common\",\"long\":[\"a fellow heir, a joint heir\",\"one who obtains something assigned to himself with others, a joint participant\"]},\"deriv\":\"from G4862 and G2818\",\"pronun\":{\"ipa\":\"syŋ.kle.roˈno.mos\",\"ipa_mod\":\"sjuŋ.kle̞.rowˈnow.mows\",\"sbl\":\"synklēronomos\",\"dic\":\"soong-klay-roh-NOH-mose\",\"dic_mod\":\"syoong-klay-roh-NOH-mose\"},\"see\":[\"G2818\",\"G4862\"]}', 'fellow (joint)-heir, heir together, heir with'),
(4689, 4790, 'συγκοινωνέω', '{\"def\":{\"short\":\"to share in company with, i.e., co-participate in\",\"long\":[\"to become a partaker together with others, or to have fellowship with a thing\"]},\"deriv\":\"from G4862 and G2841\",\"pronun\":{\"ipa\":\"syŋ.ky.noˈnɛ.o\",\"ipa_mod\":\"sjuŋ.ky.nowˈne̞.ow\",\"sbl\":\"synkoinōneō\",\"dic\":\"soong-koo-noh-NEH-oh\",\"dic_mod\":\"syoong-koo-noh-NAY-oh\"},\"see\":[\"G2841\",\"G4862\"]}', 'communicate (have fellowship) with, be partaker of'),
(4690, 4791, 'συγκοινωνός', '{\"def\":{\"short\":\"a co-participant\",\"long\":[\"participant with others in anything, joint partner\"]},\"deriv\":\"from G4862 and G2844\",\"pronun\":{\"ipa\":\"syŋ.ky.noˈnos\",\"ipa_mod\":\"sjuŋ.ky.nowˈnows\",\"sbl\":\"synkoinōnos\",\"dic\":\"soong-koo-noh-NOSE\",\"dic_mod\":\"syoong-koo-noh-NOSE\"},\"see\":[\"G2844\",\"G4862\"]}', 'companion, partake(-r (with))'),
(4691, 4792, 'συγκομίζω', '{\"def\":{\"short\":\"to convey together, i.e., collect or bear away in company with others\",\"long\":[\"to carry or bring together, to collect\",[\"to house crops, gather into granaries\"],\"to carry with others, help in carrying out, the dead to be buried or burned\",[\"to bury\"]]},\"deriv\":\"from G4862 and G2865\",\"pronun\":{\"ipa\":\"syŋ.koˈmi.zo\",\"ipa_mod\":\"sjuŋ.kowˈmi.zow\",\"sbl\":\"synkomizō\",\"dic\":\"soong-koh-MEE-zoh\",\"dic_mod\":\"syoong-koh-MEE-zoh\"},\"see\":[\"G2865\",\"G4862\"]}', 'carry'),
(4692, 4793, 'συγκρίνω', '{\"def\":{\"short\":\"to judge of one thing in connection with another, i.e., combine (spiritual ideas with appropriate expressions) or collate (one person with another by way of contrast or resemblance)\",\"long\":[\"to joint together fitly, compound, combine\",\"to interpret\",\"to compare\"]},\"deriv\":\"from G4862 and G2919\",\"pronun\":{\"ipa\":\"syŋˈkri.no\",\"ipa_mod\":\"sjuŋˈkri.now\",\"sbl\":\"synkrinō\",\"dic\":\"soong-KREE-noh\",\"dic_mod\":\"syoong-KREE-noh\"},\"see\":[\"G2919\",\"G4862\"]}', 'compare among (with)'),
(4693, 4794, 'συγκύπτω', '{\"def\":{\"short\":\"to stoop altogether, i.e., be completely overcome by\",\"long\":[\"to bend completely forwards, to be bowed together\"]},\"deriv\":\"from G4862 and G2955\",\"pronun\":{\"ipa\":\"syŋˈky.pto\",\"ipa_mod\":\"sjuŋˈkju.ptow\",\"sbl\":\"synkyptō\",\"dic\":\"soong-KOO-ptoh\",\"dic_mod\":\"syoong-KYOO-ptoh\"},\"see\":[\"G2955\",\"G4862\"]}', 'bow together'),
(4694, 4795, 'συγκυρία', '{\"def\":{\"short\":\"concurrence, i.e., accident\",\"long\":[\"accident, by chance\"]},\"deriv\":\"from a comparative of G4862 and κυρέω (to light or happen; from the base of G2962)\",\"pronun\":{\"ipa\":\"syŋ.kyˈri.ɑ\",\"ipa_mod\":\"sjuŋ.cjuˈri.ɑ\",\"sbl\":\"synkyria\",\"dic\":\"soong-koo-REE-ah\",\"dic_mod\":\"syoong-kyoo-REE-ah\"},\"see\":[\"G2962\",\"G4862\"]}', 'chance'),
(4695, 4796, 'συγχαίρω', '{\"def\":{\"short\":\"to sympathize in gladness, congratulate\",\"long\":[\"to rejoice with, take part in another\'s joy\",\"to rejoice together, to congratulate\"]},\"deriv\":\"from G4862 and G5463\",\"pronun\":{\"ipa\":\"syŋˈxɛ.ro\",\"ipa_mod\":\"sjuŋˈxe.row\",\"sbl\":\"synchairō\",\"dic\":\"soong-HEH-roh\",\"dic_mod\":\"syoong-HAY-roh\"},\"see\":[\"G4862\",\"G5463\"]}', 'rejoice in (with)'),
(4696, 4797, 'συγχέω', '{\"def\":{\"short\":\"to commingle promiscuously, i.e., (figuratively) to throw (an assembly) into disorder, to perplex (the mind)\",\"long\":[\"to pour together, commingle\",\"to disturb the mind of one, to stir up to tumult or outbreak\",\"to confound or bewilder\"]},\"deriv\":\"from G4862 and χέω (to pour) or its alternate\",\"pronun\":{\"ipa\":\"syŋˈxɛ.o\",\"ipa_mod\":\"sjuŋˈxe̞.ow\",\"sbl\":\"syncheō\",\"dic\":\"soong-HEH-oh\",\"dic_mod\":\"syoong-HAY-oh\"},\"see\":[\"G4862\"]}', 'confound, confuse, stir up, be in an uproar'),
(4697, 4798, 'συγχράομαι', '{\"def\":{\"short\":\"to use jointly, i.e., (by implication) to hold intercourse in common\",\"long\":[\"to use with anyone, use jointly\",\"to associate with, to have dealings with\"]},\"deriv\":\"from G4862 and G5530\",\"pronun\":{\"ipa\":\"syŋˈxrɑ.o.mɛ\",\"ipa_mod\":\"sjuŋˈxrɑ.ow.me\",\"sbl\":\"synchraomai\",\"dic\":\"soong-HRA-oh-meh\",\"dic_mod\":\"syoong-HRA-oh-may\"},\"see\":[\"G4862\",\"G5530\"]}', 'have dealings with'),
(4698, 4799, 'σύγχυσις', '{\"def\":{\"short\":\"commixture, i.e., (figuratively) riotous disturbance\",\"long\":[\"confusion, disturbance\",[\"of riotous persons\"]]},\"deriv\":\"from G4797\",\"pronun\":{\"ipa\":\"ˈsyŋ.xy.sis\",\"ipa_mod\":\"ˈsjuŋ.çju.sis\",\"sbl\":\"synchysis\",\"dic\":\"SOONG-hoo-sees\",\"dic_mod\":\"SYOONG-hyoo-sees\"},\"see\":[\"G4797\"]}', 'confusion'),
(4699, 4800, 'συζάω', '{\"def\":{\"short\":\"to continue to live in common with, i.e., co-survive (literally or figuratively)\",\"long\":[\"to live together with one\",[\"of physical life on earth\",\"to live a new life in union with Christ, i.e., dedicated to God\"]]},\"deriv\":\"from G4862 and G2198\",\"pronun\":{\"ipa\":\"syˈzɑ.o\",\"ipa_mod\":\"sjuˈzɑ.ow\",\"sbl\":\"syzaō\",\"dic\":\"soo-ZA-oh\",\"dic_mod\":\"syoo-ZA-oh\"},\"see\":[\"G2198\",\"G4862\"]}', 'live with'),
(4700, 4801, 'συζεύγνυμι', '{\"def\":{\"short\":\"to yoke together, i.e., (figuratively) conjoin (in marriage)\",\"long\":[\"to fasten to one yoke, yoke together\",\"to join together unite\",[\"of the marriage tie\"]]},\"deriv\":\"from G4862 and the base of G2201\",\"pronun\":{\"ipa\":\"syˈzɛβ.ɣny.mi\",\"ipa_mod\":\"sjuˈzev.ɣnju.mi\",\"sbl\":\"syzeugnymi\",\"dic\":\"soo-ZEV-gnoo-mee\",\"dic_mod\":\"syoo-ZAVE-gnyoo-mee\"},\"see\":[\"G2201\",\"G4862\"]}', 'join together'),
(4701, 4802, 'συζητέω', '{\"def\":{\"short\":\"to investigate jointly, i.e., discuss, controvert, cavil\",\"long\":[\"to seek or examine together\",\"in the New Testament to discuss, dispute, question\"]},\"deriv\":\"from G4862 and G2212\",\"pronun\":{\"ipa\":\"sy.zeˈtɛ.o\",\"ipa_mod\":\"sju.ze̞ˈte̞.ow\",\"sbl\":\"syzēteō\",\"dic\":\"soo-zay-TEH-oh\",\"dic_mod\":\"syoo-zay-TAY-oh\"},\"see\":[\"G2212\",\"G4862\"]}', 'dispute (with), enquire, question (with), reason (together)'),
(4702, 4803, 'συζήτησις', '{\"def\":{\"short\":\"mutual questioning, i.e., discussion\",\"long\":[\"mutual questioning, disputation, discussion\"]},\"deriv\":\"from G4802\",\"pronun\":{\"ipa\":\"syˈze.te.sis\",\"ipa_mod\":\"sjuˈze̞.te̞.sis\",\"sbl\":\"syzētēsis\",\"dic\":\"soo-ZAY-tay-sees\",\"dic_mod\":\"syoo-ZAY-tay-sees\"},\"see\":[\"G4802\"]}', 'disputation(-ting), reasoning'),
(4703, 4804, 'συζητητής', '{\"def\":{\"short\":\"a disputant, i.e., sophist\",\"long\":[\"a disputer, i.e., a learned disputant, sophist\"]},\"deriv\":\"from G4802\",\"pronun\":{\"ipa\":\"sy.ze.teˈtes\",\"ipa_mod\":\"sju.ze̞.te̞ˈte̞s\",\"sbl\":\"syzētētēs\",\"dic\":\"soo-zay-tay-TASE\",\"dic_mod\":\"syoo-zay-tay-TASE\"},\"see\":[\"G4802\"]}', 'disputer'),
(4704, 4805, 'σύζυγος', '{\"def\":{\"short\":\"co-yoked, i.e., (figuratively) as noun, a colleague; probably rather as a proper name; Syzygus, a Christian\",\"long\":[\"yoked together\",[\"of those united by the bond of marriage, relationship, office, labor, study, business, or the like\",\"of a yoke fellow, consort, comrade, colleague, partner\"]]},\"deriv\":\"from G4801\",\"pronun\":{\"ipa\":\"ˈsy.zy.ɣos\",\"ipa_mod\":\"ˈsju.zju.ɣows\",\"sbl\":\"syzygos\",\"dic\":\"SOO-zoo-gose\",\"dic_mod\":\"SYOO-zyoo-gose\"},\"see\":[\"G4801\"]}', 'yokefellow'),
(4705, 4806, 'συζωοποιέω', '{\"def\":{\"short\":\"to reanimate conjointly with (figuratively)\",\"long\":[\"to make one alive together\",[\"of Christians, with Christ\"]]},\"deriv\":\"from G4862 and G2227\",\"pronun\":{\"ipa\":\"sy.zo.o.pyˈɛ.o\",\"ipa_mod\":\"sju.zow.ow.pyˈe̞.ow\",\"sbl\":\"syzōopoieō\",\"dic\":\"soo-zoh-oh-poo-EH-oh\",\"dic_mod\":\"syoo-zoh-oh-poo-A-oh\"},\"see\":[\"G2227\",\"G4862\"]}', 'quicken together with'),
(4706, 4807, 'συκάμινος', '{\"def\":{\"short\":\"a sycamore-fig tree\",\"long\":[\"sycamine tree, having the form and foliage of the mulberry, but fruit resembling the fig\"]},\"deriv\":\"of Hebrew origin (H8256) in imitation of G4809\",\"pronun\":{\"ipa\":\"syˈkɑ.mi.nos\",\"ipa_mod\":\"sjuˈkɑ.mi.nows\",\"sbl\":\"sykaminos\",\"dic\":\"soo-KA-mee-nose\",\"dic_mod\":\"syoo-KA-mee-nose\"},\"see\":[\"H8256\",\"G4809\"]}', 'sycamine tree'),
(4707, 4808, 'συκῆ', '{\"def\":{\"short\":\"a fig-tree\",\"long\":[\"a fig tree\"]},\"deriv\":\"from G4810\",\"pronun\":{\"ipa\":\"syˈke\",\"ipa_mod\":\"sjuˈke̞\",\"sbl\":\"sykē\",\"dic\":\"soo-KAY\",\"dic_mod\":\"syoo-KAY\"},\"see\":[\"G4810\"]}', 'fig tree'),
(4708, 4809, 'συκομωραία', '{\"def\":{\"short\":\"the \\\"sycamore\\\"-fig tree\",\"long\":[\"a sycomore tree\"]},\"deriv\":\"from G4810 and μόρον (the mulberry)\",\"pronun\":{\"ipa\":\"sy.ko.moˈrɛ.ɑ\",\"ipa_mod\":\"sju.kow.mowˈre.ɑ\",\"sbl\":\"sykomōraia\",\"dic\":\"soo-koh-moh-REH-ah\",\"dic_mod\":\"syoo-koh-moh-RAY-ah\"},\"see\":[\"G4807\",\"G4810\"],\"comment\":\"Compare G4807.\"}', 'sycamore tree'),
(4709, 4810, 'σῦκον', '{\"def\":{\"short\":\"a fig\",\"long\":[\"a fig, the ripe fruit of a fig tree\"]},\"deriv\":\"apparently a primary word\",\"pronun\":{\"ipa\":\"ˈsy.kon\",\"ipa_mod\":\"ˈsju.kown\",\"sbl\":\"sykon\",\"dic\":\"SOO-kone\",\"dic_mod\":\"SYOO-kone\"}}', 'fig'),
(4710, 4811, 'συκοφαντέω', '{\"def\":{\"short\":\"to be a fig-informer (reporter of the law forbidding the exportation of figs from Greece), \\\"sycophant\\\", i.e., (genitive and by extension) to defraud (exact unlawfully, extort)\",\"long\":[\"to accuse wrongfully, to calumniate, to attack by malicious devices\",\"to exact money wrongfully\",[\"to extort from, defraud\"]]},\"deriv\":\"from a compound of G4810 and a derivative of G5316\",\"pronun\":{\"ipa\":\"sy.ko.fɑnˈtɛ.o\",\"ipa_mod\":\"sju.kow.fɑnˈte̞.ow\",\"sbl\":\"sykophanteō\",\"dic\":\"soo-koh-fahn-TEH-oh\",\"dic_mod\":\"syoo-koh-fahn-TAY-oh\"},\"see\":[\"G4810\",\"G5316\"],\"comment\":\"At Athens those were συκοφάντης whose business it was to inform against any one whom they might detect exporting figs out of Attica, and as sometimes they seemed to extort money from those loath to be exposed, the name \\\"συκοφάντης\\\" from the time of Aristophanes down was a general term of opprobrium to designate, a malignant and base accuser for love of gain.\"}', 'accuse falsely, take by false accusation'),
(4711, 4812, 'συλαγωγέω', '{\"def\":{\"short\":\"to lead away as booty, i.e., (figuratively) seduce\",\"long\":[\"to carry off booty\",[\"to carry one off as a captive (and slave)\",\"to lead away from the truth and subject to one\'s sway\"]]},\"deriv\":\"from the base of G4813 and (the reduplicated form of) G0071\",\"pronun\":{\"ipa\":\"sy.lɑ.ɣoˈɣɛ.o\",\"ipa_mod\":\"sju.lɑ.ɣowˈɣe̞.ow\",\"sbl\":\"sylagōgeō\",\"dic\":\"soo-la-goh-GEH-oh\",\"dic_mod\":\"syoo-la-goh-GAY-oh\"},\"see\":[\"G0071\",\"G4813\"]}', 'spoil'),
(4712, 4813, 'συλάω', '{\"def\":{\"short\":\"to despoil\",\"long\":[\"to rob, despoil\"]},\"deriv\":\"from a derivative of σύλλω (to strip; probably akin to G0138; compare G4661)\",\"pronun\":{\"ipa\":\"syˈlɑ.o\",\"ipa_mod\":\"sjuˈlɑ.ow\",\"sbl\":\"sylaō\",\"dic\":\"soo-LA-oh\",\"dic_mod\":\"syoo-LA-oh\"},\"see\":[\"G0138\",\"G4661\"]}', 'rob'),
(4713, 4814, 'συλλαλέω', '{\"def\":{\"short\":\"to talk together, i.e., converse\",\"long\":[\"to talk with\"]},\"deriv\":\"from G4862 and G2980\",\"pronun\":{\"ipa\":\"syl.lɑˈlɛ.o\",\"ipa_mod\":\"sjul.lɑˈle̞.ow\",\"sbl\":\"syllaleō\",\"dic\":\"sool-la-LEH-oh\",\"dic_mod\":\"syool-la-LAY-oh\"},\"see\":[\"G2980\",\"G4862\"]}', 'commune (confer, talk) with, speak among'),
(4714, 4815, 'συλλαμβάνω', '{\"def\":{\"short\":\"to clasp, i.e., seize (arrest, capture); specially, to conceive (literally or figuratively); by implication, to aid\",\"long\":[\"to seize, take: one as prisoner\",\"to conceive, of a woman\",[\"metaphorically of lust whose impulses a man indulges\"],\"to seize for one\'s self\",[\"in a hostile sense, to make (one a permanent) prisoner\"],\"to take hold together with one, to assist, help, to succour\"]},\"deriv\":\"from G4862 and G2983\",\"pronun\":{\"ipa\":\"syl.lɑmˈβɑ.no\",\"ipa_mod\":\"sjul.lɑɱˈvɑ.now\",\"sbl\":\"syllambanō\",\"dic\":\"sool-lahm-VA-noh\",\"dic_mod\":\"syool-lahm-VA-noh\"},\"see\":[\"G2983\",\"G4862\"]}', 'catch, conceive, help, take'),
(4715, 4816, 'συλλέγω', '{\"def\":{\"short\":\"to collect\",\"long\":[\"to gather up\",\"to collect in order to carry off\"]},\"deriv\":\"from G4862 and G3004 in its original sense\",\"pronun\":{\"ipa\":\"sylˈlɛ.ɣo\",\"ipa_mod\":\"sjulˈle̞.ɣow\",\"sbl\":\"syllegō\",\"dic\":\"sool-LEH-goh\",\"dic_mod\":\"syool-LAY-goh\"},\"see\":[\"G3004\",\"G4862\"]}', 'gather (together, up)'),
(4716, 4817, 'συλλογίζομαι', '{\"def\":{\"short\":\"to reckon together (with oneself), i.e., deliberate\",\"long\":[\"to bring together accounts, reckon up, compute\",\"to reckon with one\'s self, to reason\"]},\"deriv\":\"from G4862 and G3049\",\"pronun\":{\"ipa\":\"syl.loˈɣi.zo.mɛ\",\"ipa_mod\":\"sjul.lowˈɣi.zow.me\",\"sbl\":\"syllogizomai\",\"dic\":\"sool-loh-GEE-zoh-meh\",\"dic_mod\":\"syool-loh-GEE-zoh-may\"},\"see\":[\"G3049\",\"G4862\"]}', 'reason with'),
(4717, 4818, 'συλλυπέω', '{\"def\":{\"short\":\"to afflict jointly, i.e., (passive) sorrow at (on account of) someone\",\"long\":[\"to affect with grief together\",\"give with one\'s self\"]},\"deriv\":\"from G4862 and G3076\",\"pronun\":{\"ipa\":\"syl.lyˈpɛ.o\",\"ipa_mod\":\"sjul.ljuˈpe̞.ow\",\"sbl\":\"syllypeō\",\"dic\":\"sool-loo-PEH-oh\",\"dic_mod\":\"syool-lyoo-PAY-oh\"},\"see\":[\"G3076\",\"G4862\"]}', 'be grieved'),
(4718, 4819, 'συμβαίνω', '{\"def\":{\"short\":\"to walk (figuratively, transpire) together, i.e., concur (take place)\",\"long\":[\"to walk with the feet near together\",\"to come together, meet with one\",\"of things which fall out at the same time, to happen, turn out, come to pass\"]},\"deriv\":\"from G4862 and the base of G0939\",\"pronun\":{\"ipa\":\"symˈβɛ.no\",\"ipa_mod\":\"sjuɱˈve.now\",\"sbl\":\"symbainō\",\"dic\":\"soom-VEH-noh\",\"dic_mod\":\"syoom-VAY-noh\"},\"see\":[\"G0939\",\"G4862\"]}', 'be(-fall), happen (unto)'),
(4719, 4820, 'συμβάλλω', '{\"def\":{\"short\":\"to combine, i.e., (in speaking) to converse, consult, dispute, (mentally) to consider, (by implication) to aid, (personally) to join, attack\",\"long\":[\"to throw together, to bring together\",[\"to converse\",\"to bring together in one\'s mind, confer with one\'s self\",\"to come together, meet\",[\"to encounter in a hostile sense\",\"to fight with one\",\"to bring together of one\'s property, to contribute, aid, help\"]]]},\"deriv\":\"from G4862 and G0906\",\"pronun\":{\"ipa\":\"symˈβɑl.lo\",\"ipa_mod\":\"sjuɱˈvɑl.low\",\"sbl\":\"symballō\",\"dic\":\"soom-VAHL-loh\",\"dic_mod\":\"syoom-VAHL-loh\"},\"see\":[\"G0906\",\"G4862\"]}', 'confer, encounter, help, make, meet with, ponder'),
(4720, 4821, 'συμβασιλεύω', '{\"def\":{\"short\":\"to be co-regent (figuratively)\",\"long\":[\"to reign together\",\"metaphorically to possess supreme honor, liberty, blessedness, with one in the kingdom of God\"]},\"deriv\":\"from G4862 and G0936\",\"pronun\":{\"ipa\":\"sym.βɑ.siˈlɛβ.o\",\"ipa_mod\":\"sjuɱ.vɑ.siˈlev.ow\",\"sbl\":\"symbasileuō\",\"dic\":\"soom-va-see-LEV-oh\",\"dic_mod\":\"syoom-va-see-LAVE-oh\"},\"see\":[\"G0936\",\"G4862\"]}', 'reign with'),
(4721, 4822, 'συμβιβάζω', '{\"def\":{\"short\":\"causative (by reduplication) of the base of G0939); to drive together, i.e., unite (in association or affection), (mentally) to infer, show, teach\",\"long\":[\"to cause to coalesce, to join together, put together\",[\"to unite or knit together: in affection\"],\"to put together in one\'s mind\",[\"to compare\",\"to gather, conclude, consider\"],\"to cause a person to unite with one in a conclusion or come to the same opinion, to prove, demonstrate\",[\"to teach, instruct, one\"]]},\"deriv\":\"from G4862 and βιβάζω (to force\",\"pronun\":{\"ipa\":\"sym.βiˈβɑ.zo\",\"ipa_mod\":\"sjuɱ.viˈvɑ.zow\",\"sbl\":\"symbibazō\",\"dic\":\"soom-vee-VA-zoh\",\"dic_mod\":\"syoom-vee-VA-zoh\"},\"see\":[\"G0939\",\"G4862\"]}', 'compact, assuredly gather, intrust, knit together, prove'),
(4722, 4823, 'συμβουλεύω', '{\"def\":{\"short\":\"to give (or take) advice jointly, i.e., recommend, deliberate or determine\",\"long\":[\"to give counsel\",\"to take counsel with others, take counsel together, to consult, deliberate\"]},\"deriv\":\"from G4862 and G1011\",\"pronun\":{\"ipa\":\"sym.βuˈlɛβ.o\",\"ipa_mod\":\"sjuɱ.vuˈlev.ow\",\"sbl\":\"symbouleuō\",\"dic\":\"soom-voo-LEV-oh\",\"dic_mod\":\"syoom-voo-LAVE-oh\"},\"see\":[\"G1011\",\"G4862\"]}', 'consult, (give, take) counsel (together)'),
(4723, 4824, 'συμβούλιον', '{\"def\":{\"short\":\"advisement; specially, a deliberative body, i.e., the provincial assessors or lay-court\",\"long\":[\"counsel, which is given, taken, entered upon\",[\"consult, deliberate\"],\"a council\",[\"an assembly of counsellors or persons in consultation (the governors and procurators of provinces had a board of assessors or advisers with whom they took council before rendering judgment)\"]]},\"deriv\":\"neuter of a presumed derivative of G4825\",\"pronun\":{\"ipa\":\"symˈβu.li.on\",\"ipa_mod\":\"sjuɱˈvu.li.own\",\"sbl\":\"symboulion\",\"dic\":\"soom-VOO-lee-one\",\"dic_mod\":\"syoom-VOO-lee-one\"},\"see\":[\"G4825\"]}', 'consultation, counsel, council'),
(4724, 4825, 'σύμβουλος', '{\"def\":{\"short\":\"a consultor, i.e., adviser\",\"long\":[\"an adviser, counsellor\"]},\"deriv\":\"from G4862 and G1012\",\"pronun\":{\"ipa\":\"ˈsym.βu.los\",\"ipa_mod\":\"ˈsjuɱ.vu.lows\",\"sbl\":\"symboulos\",\"dic\":\"SOOM-voo-lose\",\"dic_mod\":\"SYOOM-voo-lose\"},\"see\":[\"G1012\",\"G4862\"]}', 'counsellor'),
(4725, 4826, 'Συμεών', '{\"def\":{\"lit\":\"harkening\",\"short\":\"Symeon (i.e., Shimon), the name of five Israelites\",\"long\":[\"the second son of Jacob by Leah\",\"one of Abraham\'s descendants\",\"the one who took the infant Jesus in his arms in the temple\",\"a teacher at the church of Antioch\",\"the original name of Peter the apostle\"]},\"deriv\":\"from the same as G4613\",\"pronun\":{\"ipa\":\"sy.mɛˈon\",\"ipa_mod\":\"sju.me̞ˈown\",\"sbl\":\"symeōn\",\"dic\":\"soo-meh-ONE\",\"dic_mod\":\"syoo-may-ONE\"},\"see\":[\"G4613\"]}', 'Simeon, Simon'),
(4726, 4827, 'συμμαθητής', '{\"def\":{\"short\":\"a co-learner (of Christianity)\",\"long\":[\"a fellow disciple\"]},\"deriv\":\"from a compound of G4862 and G3129\",\"pronun\":{\"ipa\":\"sym.mɑ.θeˈtes\",\"ipa_mod\":\"sjum.mɑ.θe̞ˈte̞s\",\"sbl\":\"symmathētēs\",\"dic\":\"soom-ma-thay-TASE\",\"dic_mod\":\"syoom-ma-thay-TASE\"},\"see\":[\"G3129\",\"G4862\"]}', 'fellow disciple'),
(4727, 4828, 'συμμαρτυρέω', '{\"def\":{\"short\":\"to testify jointly, i.e., corroborate by (concurrent) evidence\",\"long\":[\"to bear witness with, bear joint witness\"]},\"deriv\":\"from G4862 and G3140\",\"pronun\":{\"ipa\":\"sym.mɑr.tyˈrɛ.o\",\"ipa_mod\":\"sjum.mɑr.tjuˈre̞.ow\",\"sbl\":\"symmartyreō\",\"dic\":\"soom-mahr-too-REH-oh\",\"dic_mod\":\"syoom-mahr-tyoo-RAY-oh\"},\"see\":[\"G3140\",\"G4862\"]}', 'testify unto, (also) bear witness (with)'),
(4728, 4829, 'συμμερίζομαι', '{\"def\":{\"short\":\"to share jointly, i.e., participate in\",\"long\":[\"to divide at the same time, divide together\",\"to assign a portion\",\"to divide together with one (so that a part comes to me, a part to him)\"]},\"deriv\":\"middle voice from G4862 and G3307\",\"pronun\":{\"ipa\":\"sym.mɛˈri.zo.mɛ\",\"ipa_mod\":\"sjum.me̞ˈri.zow.me\",\"sbl\":\"symmerizomai\",\"dic\":\"soom-meh-REE-zoh-meh\",\"dic_mod\":\"syoom-may-REE-zoh-may\"},\"see\":[\"G3307\",\"G4862\"]}', 'be partaker with'),
(4729, 4830, 'συμμέτοχος', '{\"def\":{\"short\":\"a co-participant\",\"long\":[\"partaking together with one, a joint partaker\",[\"of something\"]]},\"deriv\":\"from G4862 and G3353\",\"pronun\":{\"ipa\":\"symˈmɛ.to.xos\",\"ipa_mod\":\"sjumˈme̞.tow.xows\",\"sbl\":\"symmetochos\",\"dic\":\"soom-MEH-toh-hose\",\"dic_mod\":\"syoom-MAY-toh-hose\"},\"see\":[\"G3353\",\"G4862\"]}', 'partaker'),
(4730, 4831, 'συμμιμητής', '{\"def\":{\"short\":\"a co-imitator, i.e., fellow votary\",\"long\":[\"an imitator of others\"]},\"deriv\":\"from a presumed compound of G4862 and G3401\",\"pronun\":{\"ipa\":\"sym.mi.meˈtes\",\"ipa_mod\":\"sjum.mi.me̞ˈte̞s\",\"sbl\":\"symmimētēs\",\"dic\":\"soom-mee-may-TASE\",\"dic_mod\":\"syoom-mee-may-TASE\"},\"see\":[\"G3401\",\"G4862\"]}', 'follower together'),
(4731, 4832, 'σύμμορφος', '{\"def\":{\"short\":\"jointly formed, i.e., (figuratively) similar\",\"long\":[\"having the same form as another, similar, conformed to\"]},\"deriv\":\"from G4862 and G3444\",\"pronun\":{\"ipa\":\"ˈsym.mor.fos\",\"ipa_mod\":\"ˈsjum.mowr.fows\",\"sbl\":\"symmorphos\",\"dic\":\"SOOM-more-fose\",\"dic_mod\":\"SYOOM-more-fose\"},\"see\":[\"G3444\",\"G4862\"]}', 'conformed to, fashioned like unto'),
(4732, 4833, 'συμμορφόω', '{\"def\":{\"short\":\"to render like, i.e., (figuratively) to assimilate\",\"long\":[\"to be conformed to, receive the same form as\"]},\"deriv\":\"from G4832\",\"pronun\":{\"ipa\":\"sym.morˈfo.o\",\"ipa_mod\":\"sjum.mowrˈfow.ow\",\"sbl\":\"symmorphoō\",\"dic\":\"soom-more-FOH-oh\",\"dic_mod\":\"syoom-more-FOH-oh\"},\"see\":[\"G4832\"]}', 'make conformable unto'),
(4733, 4834, 'συμπαθέω', '{\"def\":{\"short\":\"to feel \\\"sympathy\\\" with, i.e., (by implication) to commiserate\",\"long\":[\"to be affected with the same feeling as another, to sympathise with\",\"to feel for, have compassion on\"]},\"deriv\":\"from G4835\",\"pronun\":{\"ipa\":\"sym.pɑˈθɛ.o\",\"ipa_mod\":\"sjum.pɑˈθe̞.ow\",\"sbl\":\"sympatheō\",\"dic\":\"soom-pa-THEH-oh\",\"dic_mod\":\"syoom-pa-THAY-oh\"},\"see\":[\"G4835\"]}', 'have compassion, be touched with a feeling of'),
(4734, 4835, 'συμπαθής', '{\"def\":{\"short\":\"having a fellow-feeling (\\\"sympathetic\\\"), i.e., (by implication) mutually commiserative\",\"long\":[\"suffering or feeling the like with another, sympathetic\"]},\"deriv\":\"from G4841\",\"pronun\":{\"ipa\":\"sym.pɑˈθes\",\"ipa_mod\":\"sjum.pɑˈθe̞s\",\"sbl\":\"sympathēs\",\"dic\":\"soom-pa-THASE\",\"dic_mod\":\"syoom-pa-THASE\"},\"see\":[\"G4841\"]}', 'having compassion one of another'),
(4735, 4836, 'συμπαραγίνομαι', '{\"def\":{\"short\":\"to be present together, i.e., to convene; by implication, to appear in aid\",\"long\":[\"to come together\",\"to come to one\'s help\"]},\"deriv\":\"from G4862 and G3854\",\"pronun\":{\"ipa\":\"sym.pɑ.rɑˈɣi.no.mɛ\",\"ipa_mod\":\"sjum.pɑ.rɑˈɣi.now.me\",\"sbl\":\"symparaginomai\",\"dic\":\"soom-pa-ra-GEE-noh-meh\",\"dic_mod\":\"syoom-pa-ra-GEE-noh-may\"},\"see\":[\"G3854\",\"G4862\"]}', 'come together, stand with'),
(4736, 4837, 'συμπαρακαλέω', '{\"def\":{\"short\":\"to console jointly\",\"long\":[\"to call upon or invite or exhort at the same time or together\",\"to strengthen (comfort) with others\"]},\"deriv\":\"from G4862 and G3870\",\"pronun\":{\"ipa\":\"sym.pɑ.rɑ.kɑˈlɛ.o\",\"ipa_mod\":\"sjum.pɑ.rɑ.kɑˈle̞.ow\",\"sbl\":\"symparakaleō\",\"dic\":\"soom-pa-ra-ka-LEH-oh\",\"dic_mod\":\"syoom-pa-ra-ka-LAY-oh\"},\"see\":[\"G3870\",\"G4862\"]}', 'comfort together'),
