(4738, 4839, 'συμπαραμένω', '{\"def\":{\"short\":\"to remain in company, i.e., still live\",\"long\":[\"to abide together with\",\"to continue to live together\"]},\"deriv\":\"from G4862 and G3887\",\"pronun\":{\"ipa\":\"sym.pɑ.rɑˈmɛ.no\",\"ipa_mod\":\"sjum.pɑ.rɑˈme̞.now\",\"sbl\":\"symparamenō\",\"dic\":\"soom-pa-ra-MEH-noh\",\"dic_mod\":\"syoom-pa-ra-MAY-noh\"},\"see\":[\"G3887\",\"G4862\"]}', 'continue with'),
(4739, 4840, 'συμπάρειμι', '{\"def\":{\"short\":\"to be at hand together, i.e., now present\",\"long\":[\"to be present together\"]},\"deriv\":\"from G4862 and G3918\",\"pronun\":{\"ipa\":\"symˈpɑ.ri.mi\",\"ipa_mod\":\"sjumˈpɑ.ri.mi\",\"sbl\":\"sympareimi\",\"dic\":\"soom-PA-ree-mee\",\"dic_mod\":\"syoom-PA-ree-mee\"},\"see\":[\"G3918\",\"G4862\"]}', 'be here present with'),
(4740, 4841, 'συμπάσχω', '{\"def\":{\"short\":\"to experience pain jointly or of the same kind (specially, persecution; to \\\"sympathize\\\")\",\"long\":[\"to suffer or feel pain together\",\"to suffer evils (troubles, persecutions) in the like manner with another\"]},\"deriv\":\"from G4862 and G3958 (including its alternate)\",\"pronun\":{\"ipa\":\"symˈpɑ.sxo\",\"ipa_mod\":\"sjumˈpɑ.sxow\",\"sbl\":\"sympaschō\",\"dic\":\"soom-PA-skoh\",\"dic_mod\":\"syoom-PA-skoh\"},\"see\":[\"G3958\",\"G4862\"]}', 'suffer with'),
(4741, 4842, 'συμπέμπω', '{\"def\":{\"short\":\"to despatch in company\",\"long\":[\"to send together with\"]},\"deriv\":\"from G4862 and G3992\",\"pronun\":{\"ipa\":\"symˈpɛm.po\",\"ipa_mod\":\"sjumˈpe̞m.pow\",\"sbl\":\"sympempō\",\"dic\":\"soom-PEM-poh\",\"dic_mod\":\"syoom-PAME-poh\"},\"see\":[\"G3992\",\"G4862\"]}', 'send with'),
(4742, 4843, 'συμπεριλαμβάνω', '{\"def\":{\"short\":\"to take by enclosing altogether, i.e., earnestly throw the arms about one\",\"long\":[\"to comprehend at once\",\"to embrace completely\"]},\"deriv\":\"from G4862 and a compound of G4012 and G2983\",\"pronun\":{\"ipa\":\"sym.pɛ.ri.lɑmˈβɑ.no\",\"ipa_mod\":\"sjum.pe̞.ri.lɑɱˈvɑ.now\",\"sbl\":\"symperilambanō\",\"dic\":\"soom-peh-ree-lahm-VA-noh\",\"dic_mod\":\"syoom-pay-ree-lahm-VA-noh\"},\"see\":[\"G2983\",\"G4012\",\"G4862\"]}', 'embrace'),
(4743, 4844, 'συμπίνω', '{\"def\":{\"short\":\"to partake a beverage in company\",\"long\":[\"to drink with\"]},\"deriv\":\"from G4862 and G4095\",\"pronun\":{\"ipa\":\"symˈpi.no\",\"ipa_mod\":\"sjumˈpi.now\",\"sbl\":\"sympinō\",\"dic\":\"soom-PEE-noh\",\"dic_mod\":\"syoom-PEE-noh\"},\"see\":[\"G4095\",\"G4862\"]}', 'drink with'),
(4744, 4845, 'συμπληρόω', '{\"def\":{\"short\":\"to implenish completely, i.e., (of space) to swamp (a boat), or (of time) to accomplish (passive, be complete)\",\"long\":[\"to fill completely\",[\"of the hold of a ship\"],\"to complete entirely, be fulfilled: of time\"]},\"deriv\":\"from G4862 and G4137\",\"pronun\":{\"ipa\":\"sym.pleˈro.o\",\"ipa_mod\":\"sjum.ple̞ˈrow.ow\",\"sbl\":\"symplēroō\",\"dic\":\"soom-play-ROH-oh\",\"dic_mod\":\"syoom-play-ROH-oh\"},\"see\":[\"G4137\",\"G4862\"]}', '(fully) come, fill up'),
(4745, 4846, 'συμπνίγω', '{\"def\":{\"short\":\"to strangle completely, i.e., (literally) to drown, or (figuratively) to crowd\",\"long\":[\"to choke utterly\",[\"metaphorically the seed of the divine word sown in the mind\",\"to press round or throng one so as almost to suffocate him\"]]},\"deriv\":\"from G4862 and G4155\",\"pronun\":{\"ipa\":\"symˈpni.ɣo\",\"ipa_mod\":\"sjumˈpni.ɣow\",\"sbl\":\"sympnigō\",\"dic\":\"soom-PNEE-goh\",\"dic_mod\":\"syoom-PNEE-goh\"},\"see\":[\"G4155\",\"G4862\"]}', 'choke, throng'),
