(4885, 4986, 'Σώπατρος', '{\"def\":{\"lit\":\"saviour of his father\",\"short\":\"of a safe father; Sopatrus, a Christian\",\"long\":[\"the son of Pyrhus of Berea, a companion of Paul\"]},\"deriv\":\"from the base of G4982 and G3962\",\"pronun\":{\"ipa\":\"ˈso.pɑ.tros\",\"ipa_mod\":\"ˈsow.pɑ.trows\",\"sbl\":\"sōpatros\",\"dic\":\"SOH-pa-trose\",\"dic_mod\":\"SOH-pa-trose\"},\"see\":[\"G3962\",\"G4982\",\"G4989\"],\"comment\":\"Compare G4989.\"}', 'Sopater'),
