(5024, 5125, 'τούτοις', '{\"def\":{\"short\":\"to (for, in, with or by) these (persons or things)\",\"long\":[\"these\"]},\"deriv\":\"dative case plural masculine or neuter of G3778\",\"pronun\":{\"ipa\":\"ˈtu.tys\",\"ipa_mod\":\"ˈtu.tys\",\"sbl\":\"toutois\",\"dic\":\"TOO-toos\",\"dic_mod\":\"TOO-toos\"},\"see\":[\"G3778\"]}', 'such, them, there(-in, -with), these, this, those'),
(5025, 5126, 'τοῦτον', '{\"def\":{\"short\":\"this (person, as objective of verb or preposition)\",\"long\":[\"this\"]},\"deriv\":\"accusative case singular masculine of G3778\",\"pronun\":{\"ipa\":\"ˈtu.ton\",\"ipa_mod\":\"ˈtu.town\",\"sbl\":\"touton\",\"dic\":\"TOO-tone\",\"dic_mod\":\"TOO-tone\"},\"see\":[\"G3778\"]}', 'him, the same, that, this'),
