INSERT INTO `mounce` (`id`, `word`, `gkNr`, `strongs`, `lemma`, `transliteration`, `frequenceyCount`, `definition`) VALUES
(1039, 'γαρ', 1142, '1063', 'γάρ', 'gar', 1, 'for; it is, however, frequently used with an ellipsis of the clause to which it has reference, and its force must then be variously expressed: Mt. 15:27; 27:23, et al.; it is also sometimes epexegetic, or introductory of an intimated detail of circumstances, now, then, Mt. 1:18'),
(1040, 'γαστηρ', 1143, '1064', 'γαστήρ', 'gastēr', 9, 'the belly, stomach; the womb, Lk. 1:31; ἐν γαστρὶ ἔχειν, to be with child, Mt. 1:18, 23; 24:19, et al.; γαστέρες, gluttons, Tit. 1:12'),
(1041, 'γε', 1145, '1065', 'γε', 'ge', 24, 'an enclitic particle imparting emphasis; indicating that a particular regard is to be had to the term to which it is attached. Its force is to be conveyed, when this is possible, by various expression; at least, indeed, even'),
(1042, 'γεδεων', 1146, '1066', 'Γεδεών', 'Gedeōn', 1, 'Gideon (Judges 6-8), pr. name, indecl., Heb. 11:32*'),
